static void reportIfAccessingUnsetNodeField(NodeField field, ConstNodePtr node) {
  if (!FF::lazify_discover_unset()) return;
  if (node->hasNodeField(field)) return;
  if (node->scene()->silenceUnsetNodeFieldAccess()) return;
  if (node->isInstanceSublayer()) return;

  const auto trace = node->scene()->nodeCreationTraceByIdOrNull(node->uniqueID());
  if (!trace) return;

  const auto& [stack, snapshot] = *trace;

  sloge("Calling accessor with unset non-trivial lazifying field",
        {
            {"field", toString(field)},
            {"guid", node->uniqueID()},
            {"nodeType", toString(node->type())},
            {"trace", join(stack, ", \n")},
            {"snapshot", snapshot.toString()},
        });

  bool isFromCreateNodeWithoutFields =
      stack.size() > 0 && (strcmp(stack.front(), "createNodeWithoutFields") == 0 ||
                           strcmp(stack.front(), "findOrCreateNodeWithoutFields") == 0);

  // This error includes a counter at the end which we increment every time we fix a bug to make
  // it easier to query Sentry for clients with the latest fixes.
  REPORT_ERROR_ONCE(format("Calling accessor with unset non-trivial lazifying field [10] field=%s, "
                           "type=%s, withoutFields=%s, rooted=%s",
                           field, node->type(), isFromCreateNodeWithoutFields,
                           node->descendsFromRoot())
                        .c_str());
}

// This helper method was originally created to allow different implementations of Node
// (ConcreteNode and VirtualNode) to share logic for Node::updateChildSiblingMasks().
// We've removed VirtualNode, but it's harmless to leave these helpers as they are for now.
void forEachChildSiblingMasks(NodePtr parent, const Function<void(NodePtr, NodePtr)>& callback) {
  NodePtr previousMask = nullptr;
  int previousMaskIndex = -1;

  const int numScrollingChildren = parent->indexOfFirstValidFixedChild();

  const int numChildren = parent->childCount();
  for (int i = 0; i < numChildren; ++i) {
    NodePtr child = parent->childAtIndex(i);
    if (!child) continue;

    if (previousMask) {
      bool nodeAndMaskAreInDifferentSections =
          previousMask && (previousMaskIndex < numScrollingChildren) != (i < numScrollingChildren);
      if (nodeAndMaskAreInDifferentSections) {
        previousMask = nullptr;
        previousMaskIndex = -1;
      }
    }

    // Both masks and frames stop mask propagation
    if (child->shouldStopMaskPropagation()) {
      if (child->isVisible() && child->isMask()) {
        previousMask = child;
        previousMaskIndex = i;
      } else {
        previousMask = nullptr;
        previousMaskIndex = -1;
      }
      callback(child, nullptr);
    } else {
      callback(child, previousMask);
    }
  }
  // Number of children shouldn't have changed.
  REPORT_IF_FALSE_DEBUG_ONLY(numChildren == static_cast<int>(parent->childCount()));
}

// Keep this in sync with shouldUseNewDescription in selection_selectors.ts
bool shouldUseNewDescription(ImmutableString newDescription, ImmutableString legacyDescription) {
  if (newDescription.empty()) {
    return false;
  }
  // Naive check to see if rich text content has been written to newDescription. If it has,
  // we can assume that the new & legacy descriptions are in sync, so returning the new description
  // is fine.
  if (newDescription.startsWith("<") && newDescription.endsWith(">")) {
    return true;
  }

  auto legacyDescriptionStr = legacyDescription.str();
  return escapeHtml(legacyDescriptionStr) == newDescription.str();
}

TerminalCount toTerminalCount(int count) {
  REPORT_IF_FALSE_DEBUG_ONLY(count >= 0);
  // Count may be any positive integer, but we define stroke panel configurations based on whether a
  // node has 0, 1, 2, or 3+ terminals. Clamp the value here so that all 3+ counts return
  // TerminalCount::THREE_OR_MORE.
  return static_cast<TerminalCount>(std::min(count, 3));
}
