const String& nodeTypeToLowerCaseString(NodeType type) {
  static Array<String> strings;
  if (strings.empty()) {
    strings.reserve(static_cast<size_t>(NodeType::_END) + 1);
    for (size_t t = 0; t <= static_cast<size_t>(NodeType::_END); t++) {
      strings.push_back(
          Figma::toString(static_cast<NodeType>(t)).toLowerCase().replaceAll("_", "-"));
    }
  }

  size_t intType = static_cast<size_t>(type);
  return intType < strings.size() ? strings[intType] : emptyString;
}

bool supportsTransition(NodeType type) {
  return (type != NodeType::NONE && type != NodeType::DOCUMENT && type != NodeType::CANVAS &&
          type != NodeType::GROUP_DEPRECATED && type != NodeType::SLICE);
}

bool SymbolData::operator==(const SymbolData& other) const {
  if (this == &other) {
    return true;
  }

  if (symbolID != other.symbolID) {
    return false;
  }

  if (isDifferentEnough(uniformScaleFactor, other.uniformScaleFactor)) {
    return false;
  }

  const auto& otherOverrides = other.symbolOverrides;

  if (symbolOverrides.size() != otherOverrides.size()) {
    return false;
  }

  // first test that all keys are the same, then test the values last
  for (const auto& it : symbolOverrides) {
    if (!otherOverrides.has(it.first)) {
      return false;
    }
  }

  // now compare the values
  for (const auto& it : symbolOverrides) {
    // The following compares entire maps.
    if (it.second != *otherOverrides.getOrNull(it.first)) {
      return false;
    }
  }

  return true;
}

String DerivedSymbolData::toString() const {
  Json::Value map = Json::objectValue;
  map.reserve(overrides.size());

  for (const auto& it : overrides) {
    Json::Value item = Json::objectValue;
    item.reserve(it.second.size());

    for (const auto& it2 : it.second) {
      item.set(Figma::toString(it2.first), it2.second.toDebugString());
    }

    map.set(it.first.toString(), std::move(item));
  }

  Json::Value value = Json::objectValue;
  value.set("layoutVersion", static_cast<int>(layoutVersion));
  value.set("symbolOverrides", std::move(map));
  return value.toStyledString();
}

String SymbolData::toString() const {
  Json::Value map = Json::objectValue;
  map.reserve(symbolOverrides.size());

  for (const auto& it : symbolOverrides) {
    Json::Value item = Json::objectValue;
    item.reserve(it.second.size());

    for (const auto& it2 : it.second) {
      item.set(Figma::toString(it2.first), it2.second.toDebugString());
    }

    map.set(it.first.toString(), std::move(item));
  }

  Json::Value levels = Json::objectValue;
  levels.reserve(symbolOverrideLevels.size());
  for (const auto& it : symbolOverrideLevels) {
    Json::Value item = Json::objectValue;
    item.reserve(it.second.size());

    for (const auto& it2 : it.second) {
      item.set(Figma::toString(it2.first), it2.second);
    }

    levels.set(it.first.toString(), std::move(item));
  }

  Json::Value value = Json::objectValue;
  value.set("symbolID", symbolID.toString());
  value.set("uniformScaleFactor", uniformScaleFactor);
  value.set("symbolOverrides", std::move(map));
  value.set("symbolOverrideLevels", std::move(levels));
  return value.toStyledString();
}

