bool NodeGenerationData::operator==(const NodeGenerationData& other) const {
  if (this == &other) {
    return true;
  }

  return areOverridesEqual(overrides, other.overrides) &&
         useFineGrainedSyncing == other.useFineGrainedSyncing &&
         areOverridesEqual(diffOnlyRemovals, other.diffOnlyRemovals);
}

String NodeGenerationData::toString() const {
  Json::Value value = Json::objectValue;
  value.set("overrides", nodeGenerationOverridesJSON(overrides));
  value.set("diffOnlyRemovals", nodeGenerationOverridesJSON(diffOnlyRemovals));
  value.set("useFineGrainedSyncing", useFineGrainedSyncing ? "true" : "false");
  return value.toStyledString();
}

