bool DerivedImmutableFrameData::operator==(const DerivedImmutableFrameData& other) const {
  if (this == &other) {
    return true;
  }

  const auto& otherOverrides = other.overrides;
  return this->version == other.version && areOverridesEqual(overrides, otherOverrides);
}

String DerivedImmutableFrameData::toString() const {
  Json::Value json = Json::objectValue;
  json.set("overrides", nodeGenerationOverridesJSON(overrides));
  json.set("version", version);
  return json.toStyledString();
}

