// updatePrimaryInstanceData ensures that, on primary instances,
// there is a valid instance override path without running SymbolUpdater.
// Although marked as const, this modifies mutable _instanceData.
// Returns false if path could not be set.
bool Node::updatePrimaryInstanceData() const {
  // Caller should have checkd isPrimaryInstance before attempting this function.
  REPORT_IF_FALSE_DEBUG_ONLY(isPrimaryInstance());
  // Do not write if null node.
  if (!isValid()) {
    return false;
  }
  if (!_instanceData) {
    _instanceData = makeOwned<InstanceData>();
  }
  if (!_instanceData->primaryID.isValid()) {
    _instanceData->primaryID = uniqueID();
  }
  bool didChange = false;
  if (!getPrimaryInstancePathForSymbol(_scene, symbolData().symbolID, _instanceData->path,
                                       &didChange)) {
    _instanceData->path.clear();
    // If everything is invalid we don't need to have InstanceData at all.
    if (!_instanceData->primaryID.isValid() && !_instanceData->backingLayerID.isValid()) {
      _instanceData = nullptr;
    }
    return false;
  }
  // Override path was successfully created.
  // Check postconditions for successful return.
  REPORT_IF_FALSE_DEBUG_ONLY(_instanceData && _instanceData->path.isValid());
  return true;
}

const String& Node::typeName() const { return nodeTypeToLowerCaseString(type()); }

String Node::toString() const { return typeName() + '(' + uniqueID().toString() + ')'; }

Optional<unsigned> Node::cachedTextSublayerCount() const {
  const auto& map = editableTextSublayerCountForNode();
  auto it = map.find(_fields.dkey());
  if (it != map.end()) {
    return it->second;
  }
  return nullopt;
}

void Node::setCachedTextSublayerCount(unsigned count) const {
  if (!isValid()) {
    REPORT_ERROR_ONCE("Node::setCachedTextSublayerCount() called on invalid node");
    return;
  }

  auto& map = editableTextSublayerCountForNode();
  if (map.size() > 10000) {
    REPORT_ERROR_ONCE("Node::editableTextSublayerCountForNode() map has over 10000 entries");
  }

  map.insert_or_assign(_fields.dkey(), count);
}

void Node::invalidateCachedTextSublayerCount() const {
  editableTextSublayerCountForNode().erase(_fields.dkey());
}

// Data privacy: do not call this function when logging to persistent storage such as node tracking
// or redis, because node names and text data are protected customer information.
String verboseDebugString(NodePtr node) {
  String result;
  result += node->hierarchyString() + "\n\n";
  if (node->isInstance()) {
    if (node->isPrimaryInstance()) {
      result += node->symbolOverridesString() + "\n\n";
      result += node->overriddenSymbolIDsString() + "\n\n";
    }
    result += node->derivedSymbolOverridesString() + "\n\n";
    result += node->overrideStashString() + "\n\n";
  }
  if (node->directlyInheritsStyle()) {
    StyleVisitor::inNode(node, SearchIn::ALL_FIELDS).forEach([&](StyleVisitable visitable) {
      if (auto isf = visitable.location.inheritStyleField()) {
        result += "inherits from: " + visitable.styleId.toString() + " (" +
                  Figma::toString(*isf, node->shouldReadS2Fields()) + ")\n\n";
      }
    });
  }

  if (node->supportsGeometryCache()) {
    result += node->geometryCache().toDebugString() + "\n\n";
  }

  if (node->hasDiagramChildren()) {
    result += "diagramChildren: " + node->diagramChildrenString() + "\n\n";
  }

  result += node->propertiesString() + "\n\n";
  return result;
}

String Node::toDebugString() const {
  Array<String> parts {
    // user readable type name is not useful without distinction between symbol and instance
#if BUILD_RELEASE
    "<obfuscated>",
#else
    format("\"%s\"", nameForWeb()),
#endif
        format("type=%s/%s", debugReadableTypeName(), typeName()), format("id=%s", uniqueID()),
  };
  if (isSymbol()) {
    SymbolRef ref = symbolRef();
    if (ref.isValid()) {
      parts.push_back("symref=" + ref.toString());
    }
    if (FF::ds_publish_file_deprecation()) {
      if (hasNodeField(NodeField::PUBLISH_ID)) {
        parts.push_back(format("publishID=%s", publishID()));
      }
    } else {
      if (hasNodeField(NodeField::PUBLISH_FILE)) {
        parts.push_back("srcfile=" + publishFile_DEPRECATED().str());
      }
    }
  }
  return format("[%s]", join(parts, " "));
}

String Node::NOT_LOCALIZED_userReadableTypeName() const {
  String text;

  switch (type()) {
    case NodeType::NONE:
      text = "None";
      break;  // not an actual type

    case NodeType::DOCUMENT:
      text = "Document";
      break;
    case NodeType::CANVAS:
      text = "Page";
      break;

    case NodeType::GROUP_DEPRECATED:
    case NodeType::FRAME:
      if (isGroup())
        text = "Group";
      else if (isStateGroup()) {
        text = "Component";
      } else {
        text = "Frame";
      }
      break;

    // these have the same name, but may render symbol in italic to set it apart
    case NodeType::SYMBOL:
    case NodeType::INSTANCE:
      text = "Component";
      break;

    case NodeType::CODE_BLOCK:
      text = "Code block";
      break;

    case NodeType::WIDGET:
      text = "Widget";
      break;

    case NodeType::STICKY:
      text = "Sticky";
      break;

    case NodeType::STAMP:
      text = "Stamp";
      break;

    case NodeType::SHAPE_WITH_TEXT:
      text = "Shape with text";
      break;

    case NodeType::VECTOR_OPERATION: {
      switch (vectorOperation()) {
        case VectorOperation::UNION:
          text = "Union";
          break;
        case VectorOperation::INTERSECT:
          text = "Intersect";
          break;
        case VectorOperation::SUBTRACT:
          text = "Subtract";
          break;
        case VectorOperation::XOR:
          text = "Exclude";
          break;
        case VectorOperation::FLATTEN:
          text = "Vector operation";
          break;
      }
      break;
    }
    case NodeType::VECTOR:
      text = "Vector";
      break;
    case NodeType::HIGHLIGHT:
      text = "Highlight";
      break;

    case NodeType::STAR:
      text = "Star";
      break;
    case NodeType::LINE:
