size_t Node::getNodeMemory() const {
  size_t totMem = 0;
  totMem += sizeof(Node);

  // Owned fields of Node
  if (_geometryCache) {
    totMem += sizeof(*_geometryCache);
  }
  if (_instanceData) {
    totMem += sizeof(*_instanceData);
  }

  // Owned fields of DenseNodeFields
  totMem += fields().getOwnedMemoryUsage();
  // Sparse fields of DenseNodeFields
  totMem += fields().getSparseFieldMemoryUsage();

  if (_hasRenderTree && _renderTree) {
    totMem += _renderTree->getSize();
  }
  if (_hasRenderTreeWithoutBackgroundBlurs) {
    const auto& map = renderTreeWithoutBackgroundBlursForNode();
    auto it = map.find(_fields.dkey());
    if (it != map.end() && it->second) {
      totMem += it->second->getSize();
    }
  }
  return totMem;
}

NodePtr Node::previousSibling() const {
  auto p = parent();
  if (!p) return nullptr;
  NodeChildren siblings = p->children();
  int index = p->indexOfChild(self());
  REPORT_IF_FALSE_DEBUG_ONLY(index >= -1);
  return index > 0 ? siblings[index - 1] : nullptr;
}

NodePtr Node::previousSiblingCyclic() const {
  auto p = parent();
  if (!p) return nullptr;
  NodeChildren siblings = p->children();
  int index = p->indexOfChild(self());
  REPORT_IF_FALSE_DEBUG_ONLY(index >= -1);
  return index > 0 ? siblings[index - 1] : siblings[siblings.size() - 1];
}

NodePtr Node::nextSibling() const {
  auto p = parent();
  if (!p) return nullptr;
  NodeChildren siblings = p->children();
  int index = p->indexOfChild(self());
  REPORT_IF_FALSE_DEBUG_ONLY(index >= -1);
  return index + 1 < (int)siblings.size() ? siblings[index + 1] : nullptr;
}

NodePtr Node::nextSiblingCyclic() const {
  auto p = parent();
  if (!p) return nullptr;
  NodeChildren siblings = p->children();
  int index = p->indexOfChild(self());
  REPORT_IF_FALSE_DEBUG_ONLY(index >= -1);
  return index + 1 < (int)siblings.size() ? siblings[index + 1] : siblings[0];
}

bool Node::isAncestorOf(ConstNodePtr node) const {
  for (node = node->parent(); node; node = node->parent()) {
    if (node == self()) return true;
  }
  return false;
}

bool Node::isSiblingOf(ConstNodePtr node) const {
  if (!node || node == self()) return false;
  return parent() && node->parent() && parent() == node->parent();
}

bool Node::isEqualToOrAncestorOf(ConstNodePtr node) const {
  while (node) {
    if (node == self()) return true;
    node = node->parent();
  }
  return false;
}

bool Node::canStack() const {
  return !isConnectorFullyAttached() && !isConnectorPartiallyAttached() && isVisible() &&
         stackPositioning() == StackPositioning::AUTO;
}

bool Node::isUntrackedSublayer() const {
  return (isOrInInstance() && !isPrimaryInstance()) || isInImmutableFrame() || isInWidget();
}

bool Node::isVisible() const {
  reportIfAccessingUnsetNodeField(NodeField::VISIBLE, self());
  return fields().visible() && !fields().isHiddenForCurrentUser();
}
bool Node::visible() const {
  reportIfAccessingUnsetNodeField(NodeField::VISIBLE, self());
  return fields().visible() && !fields().isHiddenForCurrentUser();
}

bool Node::shouldCloneForCopyOrDuplicateSubtree() const {
  if (!shouldCloneForCopyOrDuplicate()) {
    return false;
  }

  NodePtr parentNode = parent();

  // Untracked children of an instance or an immutable frame will be recreated
  // later by other mechanisms such as symbol updater or immutable frame system.
  if (parentNode && (parentNode->isImmutableFrame() || parentNode->isInstance()) &&
      self()->tracking() == NodeTracking::IGNORE) {
    return false;
  }

  return true;
}

bool Node::shouldCloneForCopyOrDuplicate() const {
  // Don't clone section overlays, as we don't want them to be tracked and they'll be recreated
  // later by the SectionLayoutObserver
  if (isSectionOverlay()) {
    return false;
  }

  if (isVotingStickable()) {
    return false;
  }

  return true;
}

