// Returns a node by the specified name if it exists as a child of the given embed root node. Else,
// returns null
NodePtr Node::findEmbedChildByName(NodePtr root, const char* name) {
  NodePtr foundNode = nullptr;
  forEachNode(root, [&foundNode, &name](NodePtr node) {
    if (node->name() == name) {
      foundNode = node;
      return Loop::BREAK;
    } else {
      return Loop::CONTINUE;
    }
  });
  return foundNode;
}

