void Node::setStickySize(float dimension, bool isWide) {
  if (type() == NodeType::STICKY) {
    // In order to set a sticky size manually, we *cannot* just use setSize(). We need to change the
    // **font size of the author text** to be the appropriate ratio for scaling up. See
    // stickyPaddingScaleRatio for more info (and thank you atesfai for the help!)

    // Stickies have a minimum size, so we must ensure that we're not making them too small.
    float minDimension = WhiteboardStyle::STICKY_MIN_SCALAR_DIMENSION;
    float stickyDimension = dimension < minDimension ? minDimension : dimension;

    auto authorText = immutableFrameAuthorText();
    auto bodyText = immutableFrameText();
    if (!authorText || !bodyText) return;

    NodePtr authorTextNode = authorText->textNodeForNode();
    NodePtr bodyTextNode = bodyText->textNodeForNode();
    if (!authorTextNode || !bodyTextNode) return;

    // Get the ratio of the new size to the default size, and scale author and body font sizes
    // accordingly.
    float stickyRatio = stickyDimension / WhiteboardStyle::STICKY_DEFAULT_SCALAR_DIMENSION;
    float authorFontSize = WhiteboardStyle::STICKY_AUTHOR_TEXT_DEFAULT_FONT_SIZE * stickyRatio;
    float bodyFontSize = bodyTextNode->fontSize() * stickyRatio;

    // Create a set of text styles with the appropriately scaled font sizes.
    WhiteboardTextStyleProperties authorTextStyle = WhiteboardTextStyleProperties(
        authorFontSize, Number(WhiteboardStyle::TEXT_LINE_HEIGHT, NumberUnit::RAW),
        WhiteboardStyle::letterSpacingForFontSize(authorFontSize));
    WhiteboardTextStyleProperties bodyTextStyle = WhiteboardTextStyleProperties(
        bodyFontSize, Number(WhiteboardStyle::TEXT_LINE_HEIGHT, NumberUnit::RAW),
        WhiteboardStyle::letterSpacingForFontSize(bodyFontSize));

    NodePtr canvas = findContainingCanvas();

    // Apply the text styles
    authorTextStyle.updateTextNodeWithTextProperties(canvas, authorTextNode);
    bodyTextStyle.updateTextNodeWithTextProperties(canvas, bodyTextNode);

    if (isWide) {
      setSize({stickyDimension * WhiteboardStyle::STICKY_RESIZE_SCALING_WIDE_WIDTH_RATIO,
               stickyDimension});
    } else {
      setSize({stickyDimension, stickyDimension});
    }
  }
}

