GUID Node::updateComponentPropDefsMap(ComponentPropDef propDef) {
  ComponentPropDefsMap propDefs = componentPropDefs();
  GUID id = propDef.propID();
  propDefs.set(id, std::move(propDef));
  setComponentPropDefs(propDefs);
  return id;
}

Map<GUID, GUID> Node::addComponentPropDefIfValid(ImmutableString name, ComponentPropType type,
                                                 Any defaultValue,
                                                 Optional<Immutable<VariableData>> varValue,
                                                 Array<Any> preferredValues,
                                                 ShouldAddToStates shouldAddToStates) {
  if (!isLooseComponent() && !isStateGroup()) {
    sloge("can only add definitions to symbols and state groups", {{"nodeID", uniqueID()}},
          {.reportAsSentryError = true});
    return {};
  }

  if (type == ComponentPropType::INSTANCE_SWAP) {
    ConstNodePtr defaultSymbolBackingInstance = _scene->nodeByIdOrNull(defaultValue.as<GUID>());
    if (!defaultSymbolBackingInstance ||
        childWouldCreateSymbolCycleUnderParent(defaultSymbolBackingInstance, self())) {
      REPORT_ERROR("Default backing symbol ID for instance prop will create cycle");
      return {};
    }
  }

  GUID defID = _scene->createUniqueComponentPropId();
  ComponentPropDefsMap propDefs = componentPropDefs();
  Optional<ComponentPropDef> newPropDef;
  if (varValue) {
    newPropDef = ComponentPropDef::makeExplicit(defID, name, type, defaultValue, *varValue,
                                                getNextPositionForPropDef(propDefs),
                                                std::move(preferredValues));
  } else {
    newPropDef = ComponentPropDef::makeExplicit(defID, name, type, defaultValue,
                                                getNextPositionForPropDef(propDefs),
                                                std::move(preferredValues));
  }

  if (!newPropDef) {
    REPORT_ERROR("could not create explicit component prop def");
    return {};
  }

  Map<GUID, GUID> nodeIDToPropDefID;

  if (isStateGroup() && shouldAddToStates == ShouldAddToStates::YES) {
    for (const auto state : children()) {
      GUID statePropDefID = state->addImplicitComponentPropDef(defID);
      if (statePropDefID.isValid()) nodeIDToPropDefID[state->uniqueID()] = statePropDefID;
    }
  }

  nodeIDToPropDefID[uniqueID()] = updateComponentPropDefsMap(std::move(*newPropDef));
  return nodeIDToPropDefID;
}

GUID Node::addImplicitComponentPropDef(GUID parentPropDefID) {
  if (!isState()) {
    return GUID::invalid();
  }

  GUID defID = _scene->createUniqueComponentPropId();
  Optional<ComponentPropDef> newPropDef = ComponentPropDef::makeImplicit(defID, parentPropDefID);

  if (!newPropDef) {
    REPORT_ERROR("could not create implicit component prop def");
    return GUID::invalid();
  }
  return updateComponentPropDefsMap(std::move(*newPropDef));
}

bool Node::validateComponentPropDefEdit(GUID defID) {
  if (!(isStateGroup() || isLooseComponent())) {
    sloge("cannot edit definitions on non-product component nodes", {{"nodeID", uniqueID()}},
          {.reportAsSentryError = true});
    return false;
  }
  if (!defID.isValid()) {
    sloge("defID is not valid", {{"nodeID", uniqueID()}}, {.reportAsSentryError = true});
    return false;
  }
  if (!containingProductComponentHasExplicitPropDef(defID)) {
    sloge("defID does not exist", {{"nodeID", uniqueID()}, {"defID", defID}},
          {.reportAsSentryError = true});
    return false;
  }
  return true;
}

bool Node::validateComponentPropDefDefaultValueEdit(const ComponentPropDef& propDef,
                                                    Any defaultValue) {
  if (!propDef.explicitDef()) {
    REPORT_ERROR("can only edit default value of explicit prop def");
    return false;
  }

  if (!ComponentPropDef::getPossibleTypesFromDefaultValue(defaultValue)
           .has(propDef.explicitDef()->type)) {
    REPORT_ERROR("setting default value to wrong type");
    return false;
  }

  if (propDef.explicitDef()->type == ComponentPropType::INSTANCE_SWAP) {
    ConstNodePtr instanceSwapDefaultValue = _scene->nodeByIdOrNull(defaultValue.as<GUID>());
    if (!instanceSwapDefaultValue) {
      REPORT_ERROR("No default backing symbol for instance prop");
      return false;
    } else if (!instanceSwapDefaultValue->isSymbol()) {
      REPORT_ERROR("Default value for instance swap must be a symbol");
      return false;
    } else if (childWouldCreateSymbolCycleUnderParent(instanceSwapDefaultValue, self())) {
      ReadonlyHooksHack().showVisualBellLocalized(
          "Symbol", "visual_bell.components_can_t_contain_instances_of_themselves", Json::nullValue,
          false);
      return false;
    } else if (StateGroupHelpers::childWouldCreateInterStateGroupCycleUnderParent(
                   instanceSwapDefaultValue, self(), {self()})) {
      ReadonlyHooksHack().showVisualBellLocalized(
          "State group", "visual_bell.components_can_t_contain_instances_of_themselves",
          Json::nullValue, false);
      return false;
    }
  }

  return true;
}

bool Node::validateComponentPropDefPreferredValuesEdit(const ComponentPropDef& propDef,
                                                       const Array<Any>& values) {
  if (!propDef.explicitDef()) {
    REPORT_ERROR("can only edit preferred values of explicit prop def");
    return false;
  }
  if (propDef.explicitDef()->type == ComponentPropType::INSTANCE_SWAP) {
    for (const Any& value : values) {
      auto val = value.asOrEmpty<InstanceSwapPreferredValue>();
      if (!val) {
        REPORT_ERROR("Invalid value for instance preferred value");
        return false;
      }
    }
  } else {
    sloge("componentPropPreferredValue",
          {{"ComponentPropType", static_cast<int>(propDef.explicitDef()->type)}});
    REPORT_ERROR("Preferred value operation on an unsupported component prop type");
    return false;
  }
  return true;
}

void Node::editComponentPropDefName(GUID defID, ImmutableString name) {
  if (!validateComponentPropDefEdit(defID)) return;

  const ComponentPropDef* explicitPropDef = getExplicitPropDefForComponentish(self(), defID);
  if (!explicitPropDef) return;

  if (!explicitPropDef->explicitDef()) {
    REPORT_ERROR("can only edit name of explicit prop def");
    return;
  }

  ComponentPropDef newDef = explicitPropDef->copy();
  newDef.setExplicitName(name);
  updateComponentPropDefsMap(std::move(newDef));
}

void Node::editComponentPropDefDefaultValue(GUID defID, Any defaultValue) {
  if (!validateComponentPropDefEdit(defID)) return;

  const ComponentPropDef* explicitPropDef = getExplicitPropDefForComponentish(self(), defID);
  if (!explicitPropDef) return;

  if (!validateComponentPropDefDefaultValueEdit(*explicitPropDef, defaultValue)) return;

  ComponentPropDef newDef = explicitPropDef->copy();
  newDef.setExplicitDefaultValue(defaultValue);
  updateComponentPropDefsMap(std::move(newDef));
}

void Node::editComponentPropDefPreferredValues(GUID defID, Array<Any> values) {
  if (!validateComponentPropDefEdit(defID)) return;
  const ComponentPropDef* explicitPropDef = getExplicitPropDefForComponentish(self(), defID);
  if (!explicitPropDef) return;

  if (!validateComponentPropDefPreferredValuesEdit(*explicitPropDef, values)) return;

  ComponentPropDef newDef = explicitPropDef->copy();
  newDef.setExplicitPreferredValues(std::move(values));
  updateComponentPropDefsMap(std::move(newDef));
}

void Node::insertPropDefBetween(const GUID& defID, const GUID& prevDefId, const GUID& nextDefId) {
  if (!validateComponentPropDefEdit(defID)) return;
  ComponentPropDefsMap defs = componentPropDefs();
  const ComponentPropDef* existingDef = defs.getOrNull(defID);
  if (!existingDef) return;

  ComponentPropDef defToModify = existingDef->copy();
  if (!defToModify.isValid() || !defToModify.explicitDef()) return;

  const ComponentPropDef& prevDef = defs.getOrDefault(prevDefId);
  const ComponentPropDef& nextDef = defs.getOrDefault(nextDefId);
  Position newPosition;

  // If we are inserting at the beginning of the list, prevDef will be invalid.
  // If we are inserting at the end of the list, nextDef will be invalid.
  if (prevDef.isValid() && prevDef.explicitDef() && nextDef.isValid() && nextDef.explicitDef()) {
    newPosition =
        Position::between(prevDef.explicitDef()->sortPosition, nextDef.explicitDef()->sortPosition);
  } else if (prevDef.isValid() && prevDef.explicitDef()) {
    newPosition = prevDef.explicitDef()->sortPosition.after();
  } else if (nextDef.isValid() && nextDef.explicitDef()) {
    newPosition = nextDef.explicitDef()->sortPosition.before();
  } else {
    return;
  }

  defToModify.setExplicitSortPosition(newPosition);
  updateComponentPropDefsMap(std::move(defToModify));
}

bool Node::validateComponentPropDefDelete(GUID defID) {
  slogi("validate", {{"nodeID", uniqueID()}, {"defID", defID}});

  if (!defID.isValid()) {
    REPORT_ERROR("defID is not valid");
    return false;
  }
  if (!componentPropDefs().has(defID)) {
    REPORT_ERROR("defID does not exist");
    return false;
  }
  return true;
}

void Node::deleteComponentPropDefs(const Array<GUID>& defIDs) {
  if (!(isStateGroup() || isSymbol())) {
    sloge("can only delete prop defs from state group or symbol nodes", {{"nodeID", uniqueID()}},
          {.reportAsSentryError = true});
    return;
  }

  ComponentPropDefsMap propDefs = componentPropDefs();
  for (const GUID& defID : defIDs) {
    if (!validateComponentPropDefDelete(defID)) continue;
    propDefs.erase(defID);
  }

  setComponentPropDefs(propDefs);
}

bool Node::validatePropRefAdd(const ComponentPropDef& explicitPropDef) {
  if (!ComponentPropRef::nodeCanHaveLinkedComponentPropRef(self(), nullopt)) {
    sloge("can only add references to a symbol or tracked symbol sublayer",
          {{"nodeID", uniqueID()}}, {.reportAsSentryError = true});
    return false;
  }

  if (!explicitPropDef.explicitDef()) {
    sloge("cannot add references if there is no existing definition", {{"nodeID", uniqueID()}},
          {.reportAsSentryError = true});
    return false;
  }

  if (explicitPropDef.explicitDef()->type == ComponentPropType::INSTANCE_SWAP) {
    Optional<GUID> defaultValueGUID = explicitPropDef.explicitDef()->value()->asGUID();
    if (!defaultValueGUID) {
      REPORT_ERROR("Instance swap prop variable value is not a GUID");
      return false;
    }
    NodePtr defaultValueBackingSymbol = scene()->nodeByIdOrNull(*defaultValueGUID);
    NodePtr containingSymbol = findContainingSymbol();
    if (defaultValueBackingSymbol && containingSymbol) {
      if (childWouldCreateSymbolCycleUnderParent(defaultValueBackingSymbol, containingSymbol)) {
        ReadonlyHooksHack().showVisualBellLocalized(
            "Symbol", "visual_bell.components_can_t_contain_instances_of_themselves",
            Json::nullValue, false);
        return false;
      } else if (StateGroupHelpers::childWouldCreateInterStateGroupCycleUnderParent(
                     defaultValueBackingSymbol, containingSymbol, {defaultValueBackingSymbol})) {
        ReadonlyHooksHack().showVisualBellLocalized(
            "State Group", "visual_bell.components_can_t_contain_instances_of_themselves",
            Json::nullValue, false);
        return false;
      }
    } else {
      REPORT_ERROR("Can't find target backing symbol or containing symbol for instance swap prop");
      return false;
    }
  }

  if (!ComponentPropRef::getRefRuleForPropType(explicitPropDef.explicitDef()->type)(self())) {
    sloge("attempting to add ref on a node that does not accept refs of that type",
          {{"nodeID", uniqueID()},
           {"defID", explicitPropDef.propID()},
           {"nodeType", Figma::toString(type())},
           {"propType", Figma::toString(explicitPropDef.explicitDef()->type)}},
          {.reportAsSentryError = true});
    return false;
  }

  return true;
}

bool Node::addComponentPropRef(NodeField field, GUID explicitDefID) {
  NodePtr containingSymbol = findContainingSymbol();
  REPORT_IF_FALSE_DEBUG_ONLY(containingSymbol);
  ConstNodePtr containingProductComponent = getContainingProductComponentForSelectedNodes({self()});
  if (!containingProductComponent) {
    REPORT_ERROR("could not find containing product component when adding prop ref");
  }
  const ComponentPropDef* explicitPropDef =
      getExplicitPropDefForComponentish(containingProductComponent, explicitDefID);

  if (!explicitPropDef) {
#if !BUILD_TEST
    REPORT_ERROR("could not find explicit prop def when adding prop ref");
#endif
    return false;
  }
  if (!validatePropRefAdd(*explicitPropDef)) {
    return false;
  }

  Optional<ComponentPropRef> newPropRef;

  if (containingSymbol->isState()) {
    auto implicitDefID = getImplicitPropDefID(containingSymbol, explicitDefID);
    if (!implicitDefID.isValid()) {
      REPORT_ERROR("could not find implicit prop def on containing state");
    }
    newPropRef = ComponentPropRef::makeLinked(field, implicitDefID);
  } else {
    newPropRef = ComponentPropRef::makeLinked(field, explicitDefID);
  }

  ComponentPropRefsMap propRefs = allComponentPropRefs();
  if (newPropRef) {
    propRefs.set(field, *newPropRef);
    setComponentPropRefs(propRefs);
    return true;
  } else {
    REPORT_ERROR("Could not make linked prop ref");
    return false;
  }
}

void Node::deleteComponentPropRef(NodeField field) {
  ComponentPropRefsMap propRefs = allComponentPropRefs();
  propRefs.erase(field);
  setComponentPropRefs(propRefs);
}

// Helper that validates setting a component prop assignment. Will return true if prop assignment
// can be edited and false otherwise. Utilize this helper to validate multiple assignment
// changes without setting the node field
Pair<bool, Optional<String>> Node::validateComponentPropAssignmentEdit(GUID assignmentID,
                                                                       Any value) {
  ComponentPropAssignmentsMap propAssignments = componentPropAssignmentsRawFieldValue();

  auto backingSymbol = findSymbolBackingInstance();
  if (!backingSymbol) {
    return {false, "Could not find backing symbol when setting assignment on instance"};
  }
  const ComponentPropDef* explicitPropDef =
      getExplicitPropDefForSymbol(backingSymbol, assignmentID);
  if (!explicitPropDef || !explicitPropDef->explicitDef()) {
    sloge("can only set assignments to instances of symbols with prop defs",
          {{"nodeID", uniqueID()}});
    return {false, "can only set assignments to instances of symbols with prop defs"};
  }

  if (explicitPropDef->explicitDef()->type == ComponentPropType::INSTANCE_SWAP) {
    if (!value.is<GUID>()) {
      return {false, "cannot set instance swap prop assignment to non-GUID value"};
    }
    GUID assignmentValue = value.as<GUID>();
    ConstNodePtr assignmentNode = _scene->nodeByIdOrNull(assignmentValue);
    if (!assignmentNode) {
      return {false, "cannot set instance swap prop assignment to point to non-existent node"};
    }
    // Instance swap prop assignments should not introduce a cyclic dependency.
    if (referencesParentSymbolOrStateGroup(assignmentNode, backingSymbol)) {
      ReadonlyHooksHack().showVisualBellLocalized(
          "Symbol", "visual_bell.components_can_t_contain_instances_of_themselves", Json::nullValue,
          false);
      return {false, nullopt};
    }
    // Instance swap prop assignments have special logic to ensure that when
    // we change the assignment value, we also transfer overrides as a result of
    // the swap caused by the assignment change.
    const auto& [isValid, err] = validateSwapNestedInstancesForContainingInstanceAssignmentChange(
        self(), assignmentID, assignmentValue, true /*shouldDisplayBell*/);
    if (!isValid) {
      return {isValid, err};
    }
  }
  return {true, nullopt};
}

void Node::setComponentPropAssignment(GUID assignmentID, Any value) {
  setMultipleComponentPropAssignments({{assignmentID, value}});
}

void Node::setMultipleComponentPropAssignments(const Map<GUID, Any>& assignmentIDToValues) {
  if (!isInstance()) {
    sloge("can only add assignments to instances", {{"nodeID", uniqueID()}},
          {.reportAsSentryError = true});
    return;
  }

  for (const auto& [assignmentID, value] : assignmentIDToValues) {
    const auto& [isValid, err] = validateComponentPropAssignmentEdit(assignmentID, value);
    if (!isValid) {
      if (err) {
        sloge(format("Assignment with id: %s cannot be set to value %s", assignmentID,
                     value.toDebugString())
                  .c_str());
        REPORT_ERROR(err->c_str());
      }
      return;
    }
  }

  ComponentPropAssignmentsMap propAssignments = componentPropAssignmentsRawFieldValue();
  ConstNodePtr backingSymbol = findSymbolBackingInstance();

  for (const auto& [assignmentID, value] : assignmentIDToValues) {
    ComponentPropAssignment newPropAssignment{assignmentID, value, self()};
    propAssignments.set(assignmentID, newPropAssignment);

    // perform extra logic for instance swap defs, do not need to perform null checks since
    // validateComponentPropAssignment edit was called above
    const ComponentPropDef* explicitPropDef =
        getExplicitPropDefForSymbol(backingSymbol, assignmentID);
    if (explicitPropDef->explicitDef()->type == ComponentPropType::INSTANCE_SWAP) {
      GUID assignmentValue = value.as<GUID>();
      GUID oldSymbolID =
          getCombinedComponentPropAssignments().getOrDefault(assignmentID).instanceSwapValue();
      ConstNodePtr oldSymbol = _scene->nodeByIdOrNull(oldSymbolID);
      ConstNodePtr newSymbol = _scene->nodeByIdOrNull(assignmentValue);
      SymbolInstanceRelinker::changeInstanceSwapPropAssignment(self(), oldSymbol, newSymbol,
                                                               assignmentID);
    }

    clearOverridesOnRefNodes(self(), assignmentID);
  }

  // Synchronously prune/garbage collect the assignments HERE so that it's
  // bundled together in the same commit. Doing it here cleans up the undo/redo
  // stack and potentially makes multiplayer easier to reason about.
  pruneComponentPropAssignments(self(), propAssignments);

  setComponentPropAssignments(propAssignments);
}

