ConstNodePtr Node::textNodeForNode() const {
  if (type() == NodeType::TEXT) {
    return self();
  } else if (isImmutableFrame()) {
    return immutableFrameText();
  } else if (isTableCell()) {
    return tableCellText();
  }
  return nullptr;
};

bool Node::isTextContainer() const { return type() != NodeType::TEXT && textNodeForNode(); }

ConstNodePtr Node::findTextContainer() const {
  ConstNodePtr search = nullptr;

  if (isOrInImmutableFrame()) {
    for (search = self(); search != nullptr; search = search->parent()) {
      if (search->isTextContainer()) {
        break;
      }
    }
  }

  return search;
}

