bool Node::isUnderHiddenSection() const {
  auto parent = findAncestorOrSelf(NodeType::SECTION);
  while (parent.isValid() && parent->type() != NodeType::CANVAS) {
    if (parent->sectionContentsHidden()) {
      return true;
    }
    parent = parent->findAncestor(NodeType::SECTION);
  }

  return false;
}

bool Node::isTopLevelNode() const {
  return parent() != nullptr &&
         (parent()->type() == NodeType::CANVAS || parent()->type() == NodeType::SECTION ||
          parent()->type() == NodeType::SLIDE_GRID || parent()->type() == NodeType::SLIDE_ROW ||
          parent()->isSlideTemplate() || parent()->isResponsiveSet());
}

