  return type() == NodeType::CANVAS || isSection() || isStateGroup() || isResponsiveSet();
}

bool Node::isOrganizer() const {
  const bool nodeIsFrameLike = isFrame() || isSymbol();
  return (nodeIsFrameLike && isTopLevelNode() &&
          // state groups are NodeType::FRAME so we need to exclude
          !isStateGroup() && !isResponsiveSet()) ||
         isState() || isResponsiveNode();
}

