int Node::indexOfFirstValidFixedChild() const {
  // Walk backward through all children. As soon as we find a scrolling node,
  // return i which points to the node above it.
  int i = childCount();
  for (const auto child : reversedChildren()) {
    if (!child->isFixed()) {
      return i;
    }

    i--;
  }

  return 0;
}

ChildNodeIterator<ConstNodePtr> Node::firstValidFixedChild() const {
  auto first = childDrawOrderIterator();
  auto cur = childDrawOrderEnd();
  if (childCount() == 0) return cur;
  while (--cur != first) {
    if (!cur->isFixed()) return (++cur);
  }
  return cur;
}

