AffineTransform Node::relativeTransformToAncestorOrSelf(ConstNodePtr node) const {
  ConstNodePtr n = self();
  if (n == node) {
    return {};
  }

  AffineTransform transform;
  transform.multiply(overlayTransform());
  auto p = parent();

  while (p) {
    AffineTransform t;
    t.multiply(n->relativeTransform());

    transform.preMultiply(t);

    if (p == node) {
      return transform;
    }

    n = p;
    p = n->parent();
  }

  sloge_debugOnly_previously_loge(
      "relativeTransformToAncestorOrSelf() called with argument that is not an ancestor node or "
      "self");
  return {};
}

void Node::changeParentIndex(ParentIndex newParentIndex, NodeOrigin origin, int sessionID) {
  if (!isValid()) return;
  SceneGraph* scn = scene();
  if (!scn) {
    REPORT_ERROR("attempting to change node's parent index without scene");
    return;
  }
  REPORT_IF_FALSE_DEBUG_ONLY(self() != scn->root());
  if (scn->forceNodeOrigin()) {
    origin = *scn->forceNodeOrigin();
  }

  auto oldParentIndex = parentIndex();

#if DEBUG_CHANGES_PARENT_INDEX
  logToConsole_deprecated("Node", "changeParentIndex for node '%s' from '%s' to '%s'",
                          uniqueID().toString().c_str(), oldParentIndex.toString().c_str(),
                          newParentIndex.toString().c_str());
#endif

  if (oldParentIndex != newParentIndex) {
    NodePtr oldParent = parent();
    NodePtr oldParentInScene = nullptr;
    int oldIndexInScene = -1;

    // 1. Update mask state in the old parent if we have one.
    if (oldParent) {
      SilenceUnsetNodeFieldAccessScope scope(scene(), !descendsFromRoot());
      // If this is a mask, we definitely need to update our old siblings
      if (isMask() && isVisible()) {
        oldParent->incVisibleMaskChildCount(-1);
      }

      // If this parent has masks, we need the parent to update our old siblings
      else if (oldParent->hasVisibleMasksAsChildren()) {
        oldParent->setChildSiblingMasksDirty();
      }

      // 2. Record the old parent
      if (descendsFromRoot()) {
        oldParentInScene = oldParent;
        oldIndexInScene = oldParent->indexOfChild(self());
        invalidateCanvasSpaceBounds(Node::Invalidate::PARENTS, origin);
        invalidateLayoutGridCachesForAncestors(Node::InvalidateLayoutGridCache::SKIP_IF_EMPTY);
      }
    } else {
      REPORT_IF_TRUE_DEBUG_ONLY(descendsFromRoot());
    }

    // 3. Remove the node from its parent
    _unlinkFromParentIndex();
    visibleSiblingMaskForNode().erase(_fields.dkey());

    // 4. Break the old parent index loop if there was one
    if (_isPartOfLoop) {
      _isPartOfLoop = false;
      for (NodePtr ancestor = scn->nodeByIdOrNull(oldParentIndex.guid);
           ancestor && ancestor != self();
           ancestor = scn->nodeByIdOrNull(ancestor->parentIndex().guid)) {
        ancestor->setIsPartOfLoop(false);
        ancestor->_linkToParentIndex();
      }
    }

    // 5. Actual transfer
    _fields.set(NodeField::PARENT_INDEX, newParentIndex);

    // 6. Detect if there is now a new parent index loop
    if (_checkIsPartOfLoop()) {
      _isPartOfLoop = true;
      for (NodePtr ancestor = scn->nodeByIdOrNull(newParentIndex.guid);
           ancestor && ancestor != self();
           ancestor = scn->nodeByIdOrNull(ancestor->parentIndex().guid)) {
        ancestor->setIsPartOfLoop(true);
        ancestor->_unlinkFromParentIndex();
      }
    }

    // 7. Add this node to its parent
    // Side effect: updates cached parent().
    if (!_isPartOfLoop) {
      _linkToParentIndex();
    }

    NodePtr newParent = parent();
    NodePtr newParentInScene;
    int newIndexInScene = -1;

    if (Node* newParentRaw = newParent.getOrNull()) {
      SilenceUnsetNodeFieldAccessScope scope(scene(), !descendsFromRoot());
      // 8. Record the new parent
      if (descendsFromRoot()) {
        newParentInScene = newParent;
        newIndexInScene = newParentRaw->indexOfChild(self());
        invalidateCanvasSpaceBounds(Node::Invalidate::PARENTS_AND_CHILDREN, origin);
        invalidateRenderTreeForChildren();
        invalidateLayoutGridCachesForAncestors(Node::InvalidateLayoutGridCache::SKIP_IF_EMPTY);
      }

      // 9. Update mask state in the new parent
      // If this is a mask, we definitely need to update our new siblings
      if (isMask() && isVisible()) {
        newParentRaw->incVisibleMaskChildCount(1);
      }

      // If this parent has masks, we need the parent to update our new siblings and us
      else if (newParentRaw->hasVisibleMasksAsChildren()) {
        newParentRaw->setChildSiblingMasksDirty();
      }

      // 10. Update symbol, instance & module caches.
      _updateInInstanceAfterParentChange(newParent->isOrInInstance());
      _updateInSymbolAfterParentChange(newParent->isOrInSymbol());
      _updateInModuleAfterParentChange(newParent->isOrInModule());
      _updateInSlideAfterParentChange(newParent->isOrInSlide());
    } else {
      REPORT_IF_TRUE_DEBUG_ONLY(descendsFromRoot());
    }

    // 11. For diagram nodes, update the diagram parent's cached _diagramChildren if adding or
    // removing nodes from the scene. We need to recurse into groups to find descendants that
    // may be diagram nodes!
    if (diagramParentIndex().isValid() || isGroup() || isSection() || isFrame()) {
      bool added = !oldParent && newParent;
      // if we were added or removed from the scene, let the old parent know we left,
      // and the new parent know we arrived. If we were only reparented, we don't want
      // to do this update.
      // (`!!` below is a c++ shorthand for "convert to bool" (literally "not not")):
      if ((!!oldParent) != (!!newParent)) {
        forEachNode(self(), [added](NodePtr descendant) {
          GUID oldDiagramParentId = GUID::invalid();
          GUID newDiagramParentId = descendant->diagramParentIndex().guid;
          if (!newDiagramParentId.isValid()) return Loop::CONTINUE;
          if (!added) std::swap(oldDiagramParentId, newDiagramParentId);
          descendant->_diagramParentChanged(oldDiagramParentId, newDiagramParentId);
          // don't need to walk into immutable frames
          return descendant->isImmutableFrame() ? Loop::SKIP : Loop::CONTINUE;
        });
      }
    }

    if (hasWeakHandle() && scn->hasViewerShim()) {
      DeprecatedJsSceneHooks::updateNodeAfterPropertyChange(
          getOrCreateWeakHandle(), *nodeFieldToViewerField(NodeField::PARENT_INDEX));
    }
    if (oldParentInScene || newParentInScene) {
      SceneGraphEvent event(self(), origin, sessionID, NodeField::PARENT_INDEX, oldParentIndex,
                            newParentIndex, oldParentInScene, newParentInScene, oldIndexInScene,
                            newIndexInScene);
      scn->triggerSceneGraphEvent(event);
    }
  }
}

