template <typename N>
void Node::copyAllReversedChildrenOrderTableCells(Array<N>& out) const {
  if (!isTable()) {
    const auto& cc = _cachedChildren;
    out.insert(out.end(), cc.rbegin(), cc.rend());
  } else {
    for (int i = tableNumRows() - 1; i >= 0; i--) {
      for (int j = tableNumColumns() - 1; j >= 0; j--) {
        if (NodePtr cell = tableCache()->cellAt(i, j)) {
          out.push_back(cell);
        }
      }
    }
  }
}

