static void hackPopulateOldValueWithDefaultForMultiplayer(NodeField field, const Any& value,
                                                          Any& outOldValue) {
  // Our SceneGraph and undo system support erasing node fields, but multiplayer
  // does not. This causes the following bug...
  //
  // 1. We add a new property to nodes called PROP of type GUID
  // 2. A user opens a document. No nodes in the document have PROP set.
  // 3. We set prop on some node to GUID(1, 1). This is synced to the server.
  // 4. The user hits undo. This erases PROP on the node, but multiplayer
  //    doesn't support erasing properties! This update is never saved to the
  //    server or sent to other clients.
  //
  // We're planning to make larger architectural changes so that this is no
  // longer a problem. Until then, the following hack works around the issue.
  //
  // In step 3, instead of firing a scene graph event saying "we changed
  // SOME_GUID from Any() to GUID(1, 1)" we fire one saying "we changed
  // SOME_GUID from GUID::invalid to GUID(1, 1)". When the user hits undo, the
  // undo system knows to undo the value back to GUID::invalid instead of to
  // Any().
  if (!outOldValue.isEmpty()) {
    return;
  }
  // We should never get here for lazy fields because the passed-in old value should
  // always be a non-empty Any.
  REPORT_IF_TRUE_DEBUG_ONLY(isLazilyPopulatedNodeField(field));

  auto defaultValue = getDefaultEmptyValueForNodeField(field);
  // IMPORTANT NOTE: some SceneGraph listeners assume that all SceneGraphEvents
  // have a different oldValue and newValue. With the approach above, we risk
  // firing an event with the same oldValue and newValue. This will happen if
  // someone sets the SOME_GUID property from Any() to GUID::invalid.
  // In that case we proceed with a scene graph event containing an empty oldValue,
  // even though it may result in issues with undo.
  if (!defaultValue.isEmpty() && value != defaultValue) {
    outOldValue = defaultValue;
  }
}

bool Node::allowSetField(NodeField field) const {
  NodeType ntype = type();

  if (ntype == NodeType::NONE) {
    // We don't want to ignore field changes if the `type` hasn't been set yet. This ideally
    // shouldn't ever happen: type should always be set before other fields.
    return true;
  }

  // This code was originally written to fix bugs that had to do with bogus overrides on instances.
  // Also, see the block comment above Node::set to see how this invariant checking might
  // change in the future. (https://app.asana.com/0/1113690004088080/1125993447933884)
  //
  // The original commit this logic comes from: fcb57dcd01b18cbbabdec37e1833c298fa3323bb
  //
  // These safeguards are run whenever `Node::set` is called, which includes when we're applying
  // changes from a remote client.

  REPORT_IF_FALSE_DEBUG_ONLY(ntype != NodeType::NONE);  // caller must have checked this already

  // Originally, this code was added in: 3cc27fa4036ae9254bc01e4c4122bb46e5596e01. The original
  // reason this was necessary was that when we applied the default StyleOverride object to a node,
  // the default text properties would also get copied over. That bug was fixed here:
  // 62091cdeaf9fb5a9c9ed355f719f93fc76c5163e. This check is kept around, however, because there
  // could still be bad data on old files and it's a useful safeguard in the future.
  if (isFieldOnlyForText(field)) {
    switch (ntype) {
      case NodeType::NONE:
      case NodeType::TEXT:
        break;
      default:
        return false;
    }
  }

  bool allow = true;
  switch (field) {
    case NodeField::FILL_PAINT_DATA: {
      // Because of the frames/instances/symbols now support fill, stroke, and corners.
      // This means that all nodes that use the geometry cache (eg vector ops, vector
      // networks, shapes, text, and now frames/instances/symbols) support `fillPaintData`.
      allow = usesGeometryCache(ntype);
      break;
    }

    case NodeField::STROKE_PAINT_DATA: {
      // See comment for FILL_PAINT_DATA
      allow = usesGeometryCache(ntype);
      break;
    }

    case NodeField::BACKGROUND_ENABLED:
    case NodeField::BACKGROUND_COLOR:
    case NodeField::BACKGROUND_OPACITY: {
      allow = isNodeTypeInFrame(ntype) || ntype == NodeType::CANVAS;
      break;
    }

    case NodeField::EFFECT_DATA: {
      switch (ntype) {
        case NodeType::DOCUMENT:
        case NodeType::CANVAS:
        case NodeType::SLICE:
          allow = false;
          break;
        default:
          allow = true;
          break;
      }
      break;
    }

    case NodeField::IS_FRAME_CLIPPING_DISABLED:
    case NodeField::OVERLAY_IS_FRAME_CLIPPING_DISABLED: {
      allow = isNodeTypeInFrame(ntype);
      break;
    }

    case NodeField::RECTANGLE_TOP_LEFT_CORNER_RADIUS:
    case NodeField::RECTANGLE_TOP_RIGHT_CORNER_RADIUS:
    case NodeField::RECTANGLE_BOTTOM_RIGHT_CORNER_RADIUS:
    case NodeField::RECTANGLE_BOTTOM_LEFT_CORNER_RADIUS:
    case NodeField::RECTANGLE_CORNER_RADII_INDEPENDENT: {
      switch (ntype) {
        case NodeType::RECTANGLE:
        case NodeType::ROUNDED_RECTANGLE:
          allow = true;
          break;
        default:
          allow = isNodeTypeInFrame(ntype);
          break;
      }
      break;
    }

    case NodeField::OVERLAY_POSITION_TYPE:
    case NodeField::OVERLAY_BACKGROUND_INTERACTION:
    case NodeField::OVERLAY_BACKGROUND_APPEARANCE:
    case NodeField::OVERFLOW_DIRECTION: {
      allow = isNodeTypeInFrame(ntype);
      break;
    }

    case NodeField::PROTOTYPE_START_NODE_ID: {
      allow = (ntype == NodeType::CANVAS);
      break;
    }

    // Autolayout fields
    case NodeField::STACK_MODE:
    case NodeField::STACK_PRIMARY_ALIGN_ITEMS:
    case NodeField::STACK_COUNTER_ALIGN_ITEMS:
    case NodeField::STACK_COUNTER_ALIGN_CONTENT:
    case NodeField::STACK_CHILD_ALIGN_SELF:
    case NodeField::STACK_CHILD_PRIMARY_GROW:
    case NodeField::STACK_SPACING:
    case NodeField::STACK_PADDING_LEFT:
    case NodeField::STACK_PADDING_TOP:
    case NodeField::STACK_PADDING_RIGHT:
    case NodeField::STACK_PADDING_BOTTOM:
    case NodeField::STACK_COUNTER_SIZING:
    case NodeField::STACK_COUNTER_SPACING:
    case NodeField::STACK_PRIMARY_SIZING:
    case NodeField::STACK_POSITIONING:
    case NodeField::STACK_WRAP:
    case NodeField::MIN_SIZE:
    case NodeField::MAX_SIZE:
    case NodeField::STACK_REVERSE_Z_INDEX: {
      allow = !isNodeTypeInImmutableFrame(ntype);
      break;
    }

    case NodeField::LAYOUT_GRID_DATA:
      allow = isNodeTypeInFrame(ntype);
      break;

    case NodeField::GUIDES_DATA:
      allow = isStackOrFixedSizeContainer() || ntype == NodeType::CANVAS;
      break;

    case NodeField::SIMPLIFY_INSTANCE_PANELS:
      allow = isProductComponent();
      break;

    // Individual borders fields should only apply to rectangles, rounded rectangles, and frames
    case NodeField::BORDER_TOP_WEIGHT:
    case NodeField::BORDER_BOTTOM_WEIGHT:
    case NodeField::BORDER_LEFT_WEIGHT:
    case NodeField::BORDER_RIGHT_WEIGHT:
    case NodeField::BORDER_STROKE_WEIGHTS_INDEPENDENT:
      allow = supportsIndividualBorders(this);
      break;

    default: {
      break;
    }
  }

  return allow;
}

static const NodeFieldSet& lazifyingFields() {
  static NodeFieldSet fieldSet({
#define LAZIFYING_FIELD_WITH_DEFAULT(enumValue, default, index) NodeField::enumValue,
#include "FGNodeFieldInclude.h"
  });
  return fieldSet;
}

static const NodeFieldSet& lazyFields() {
  // Local static variables are only initialized once, and feature flags cannot change over the
  // course of a session, so this allows us to avoid the overhead of checking the feature flag every
  // time we want to check which fields are lazy.
  static const NodeFieldSet& _lazyFields = FF::lazify_more_defaults() ? NodeFieldSet {
#define LAZIFYING_FIELD_WITH_DEFAULT(enumValue, default, index) NodeField::enumValue,
#define NODE_FIELD_WITH_DEFAULT(enumValue, default) NodeField::enumValue,
#include "FGNodeFieldInclude.h"
  } : NodeFieldSet{
#define NODE_FIELD_WITH_DEFAULT(enumValue, default) NodeField::enumValue,
#include "FGNodeFieldInclude.h"
  };

  return _lazyFields;
}

bool Node::hasNodeFieldOrDefault(NodeField field) const {
  return fields().hasNodeFieldOrDefault(field, lazyFields());
}

// CAUTION: we enforce some invariants here, and other invariants in the specific set*() functions.
// For example, Node::setIsUsingFrameClipping() used to check supportsFrameClipping(),
// but in the function below, we also set isValidFieldForNode = supportsFrameClipping() for
// NodeField::IS_FRAME_CLIPPING_DISABLED. This inconsistency in where invariants are enforced
// across node fields is generally not a good practice and is the source of many bugs, however
// a lot of this code has been in production long enough, with a lot of data relying on possibly
// quirky behavior, that it's quite risky to clean up everything at this point. Future attempts
// to consolidate the behavior can be tracked here:
// https://app.asana.com/0/1113690004088080/1125993447933884
//
// Some functions avoid calling the specific set*() functions and directly call this setter
// directly on generic NodeField, for example cloneFields() used by symbols and copy-paste, and
// recursivelyClobberValuesForQuery() used by selections. As a result, critical invariant checks
// on input data for new code should probably reside in Node::set() for safety.
//
// Of particular concern is NodeType-based checks, because the node's type can change, so the
// order in which setters are called before set(NodeField::TYPE) may be brittle. An attempt
// to make NodeType immutable is being tracked here:
// https://app.asana.com/0/1113690004088080/767337863587906
//
// The event-related logic of this function should be mirrored in
// Node::notifyVirtualFieldChange().
bool Node::set(NodeField field, Any value, NodeOrigin origin, int sessionID) {
  if (!scene()) {
    return false;
  }

  if (scene()->forceNodeOrigin()) {
    origin = *scene()->forceNodeOrigin();
  }

  if (isMultiplayerSupportedField(field) && value.isNullSharedPointer()) {
    // In general we shouldn't `format` error strings since it can cause errors to end
    // up in separate buckets in Sentry. But in this case, that's what we want! By
    // including the node field in the REPORT_ERROR we will get different events for
    // different node fields, which makes triaging to the right team easier.
    REPORT_ERROR(
        format("Invalid Node::set with null shared pointer (%s)", Figma::toString(field)).c_str());
  }

  if (FF::ds_variables_report_empty_name() && field == NodeField::NAME && isVariable()) {
    if (value.is<ImmutableString>()) {
      auto newName = value.as<ImmutableString>();
      if (newName.empty()) {
        sloge("Setting variable name to empty string", {{"variable", toDebugString()}},
              {.reportAsSentryError = true});
        // We don't block the set. Instead we'll track down the stack trace and fix it at the
        // source.
      }
    }
  }

  ////////////////////////////////////////////////////////////////////////////////////////////////
  // Error reports //
  ////////////////////////////////////////////////////////////////////////////////////////////////
  // Set when read-only observers are being fired.
  REPORT_IF_TRUE(scene()->mutationsAreDisallowed());

  // Set when `lib/scenegraph` code calls app related code that isn't
  // allowed to mutate the scene (see: ReadonlyHooksHack).
  REPORT_IF_TRUE(ReadonlyHooksHack::inAppHooksDontMutateScene());

  // These types are forbidden in the scene graph. Array<double> is also
  // forbidden, but Any<Array<double>> is disallowed and won't compile, so it
  // can't exist.
  REPORT_IF_TRUE_DEBUG_ONLY(value.is<double>() || value.is<Vector>() ||
                            value.is<AffineTransform>());

  ////////////////////////////////////////////////////////////////////////////////////////////////
  // Special cases which require early exit. //
  ////////////////////////////////////////////////////////////////////////////////////////////////

  if (settingLazyPopulatedFieldToDefault(field, value)) {
    if (isLazifyingNodeField(field) && !hasNodeFieldForBackwardsCompatibleSerialization(field)) {
      _fields.setHasFieldForLazifyingBackwardsCompatibility(field);
      Optional<SceneGraphEvent> event =
          getSceneGraphEventForLazifyingBackwardsCompatibility(field, value, origin, sessionID);
      if (event) {
        scene()->triggerSceneGraphEventForBackwardsCompatibleSerialization(*event);
      }
      // Continue returning `false` at the end of this block because node APIs
      // including `Node::set` should simulate the field being lazified.
    }
    return false;
  }

  if (bool propertyAlreadyEqualToValue = isPropertyEqualTo(field, value)) {
    return false;
  }

  // Instances & symbols should always be fixed-size frames.
  if (settingResizeToFitOnFixedSizeFrame(field, value)) {
    logResizeToFitOnFixedSizeFrame();
    return false;
  }

  if (bool neverSetFieldOnThisType = !allowSetField(field)) {
    logAllowSetFieldDenied(field);
    return false;
  }

  {
    String errmsg;
    if (!isValidValueForFieldOrEmpty(field, value, &errmsg)) {
      REPORT_ERROR(("Node::set: " + errmsg).c_str());
      return false;
    }
  }

  ////////////////////////////////////////////////////////////////////////////////////////////////
  // Special cases which require alternate behavior. //
  ////////////////////////////////////////////////////////////////////////////////////////////////

  if (field == NodeField::PARENT_INDEX) {
    // The parent index is special-cased and does not go through the setting routine that follows.
    changeParentIndex(value.as<ParentIndex>(), origin, sessionID);
    return false;
  }

  ////////////////////////////////////////////////////////////////////////////////////////////////
  // Setting the field //
  ////////////////////////////////////////////////////////////////////////////////////////////////
  REPORT_IF_TRUE_DEBUG_ONLY(field == NodeField::PARENT_INDEX);

  Optional<SceneGraphEvent> event =
      getSceneGraphEventForFieldUpdate(field, value, origin, sessionID);
  updateStyleRegistryBeforePropertyChange(field);

  Any copyOfOldValue = getCopyOf(field);
  if (FF::variables_typography_oscillation_logs() && field == NodeField::DERIVED_TEXT_DATA) {
    auto newValue = value.as<Shared<const DerivedTextData>>();
    slogw("=========== setting derived text data",
          {
              {"node", toDebugString()},
              {"oldValue", asFor<NodeField::DERIVED_TEXT_DATA>(copyOfOldValue).toString()},
              {"newValue", newValue.toString()},
          });
  }

  _fields.set(field, std::move(value));
  updateCachesAfterPropertyChange(field, origin, copyOfOldValue);

  if (event) {
    scene()->triggerSceneGraphEvent(*event);
    return true;
  }
  return false;
}

// To be used during rollout of `lazify_more_defaults` only.
// This simulates the scene graph event that would have been fired when populating the
// given field for the first time with its default value if it was NOT lazy. This is
// important for scene graph observers which serialize file data that must be backwards
// compatible with clients not in the feature flag.
Optional<SceneGraphEvent> Node::getSceneGraphEventForLazifyingBackwardsCompatibility(
    NodeField field, const Any& value, NodeOrigin origin, int sessionID) {
  REPORT_IF_FALSE_DEBUG_ONLY(isLazifyingNodeField(field));

  // Pass `value` as a `const Any&` to avoid copying unless we have to fire a
  // scene graph event (a SceneGraphEvent contains to Anys: one for the old
  // value and another for the new value). When we are first loading the
  // document, we do not fire scene graph events.
  if (!descendsFromRoot() || !scene() || !scene()->hasObservers()) {
    // Nothing to do.
    return nullopt;
  }

  // When we are populating a non-lazy field for the first time, the scene graph event
  // fired will always have `Any::empty` as the old value.
  Any oldValue = Any::empty;

  // Create the scene graph event. The constructor makes copies of the old and new values
  // by const Any& parameters.
  return SceneGraphEvent(self(), origin, sessionID, field, oldValue, value, nullptr, nullptr, -1,
                         -1);
}

Optional<SceneGraphEvent> Node::getSceneGraphEventForFieldUpdate(NodeField field, const Any& value,
                                                                 NodeOrigin origin, int sessionID) {
  // Pass `value` as a `const Any&` to avoid copying unless we have to fire a
  // scene graph event (a SceneGraphEvent contains to Anys: one for the old
  // value and another for the new value). When we are first loading the
  // document, we do not fire scene graph events.
  if (!descendsFromRoot() || !scene() || !scene()->hasObservers()) {
    // Nothing to do.
    return nullopt;
  }

  // Store the old value so that it can be broadcast in the
  // SceneGraphEvent (this is stored as an empty Any if there was
  // previously no value). Use a copy so that fields entry is valid when
  // erased.
  Any oldValue = getCopyOf(field);

  // Modify old value if necessary by populating a default value: see
  // comment in hackPopulateOldValueWithDefaultForMultiplayer for explanation.
  hackPopulateOldValueWithDefaultForMultiplayer(field, value, oldValue);

  // Create the scene graph event. The constructor makes copies of the old and new values
  // by const Any& parameters.
  return SceneGraphEvent(self(), origin, sessionID, field, oldValue, value, nullptr, nullptr, -1,
                         -1);
}

void Node::logResizeToFitOnFixedSizeFrame() const {
  slogw("RESIZE_TO_FIT may not be set to true on nodes which must be fixed-size frames",
        {{"type", Figma::toString(type())}});
}

void Node::logAllowSetFieldDenied(
    NodeField field) const {  // Prevent warning from spamming pixie datadog logs and sentry.
  static bool once = false;
  if (!once) {
    once = true;
    // At time of writing (2021-06-27), about two dozen tests fail if the following
    // is changed to sloge; we must fix all of those tests so we can change the slogw
    // to sloge to help prevent future regressions.
    slogw("allowSetField denied",
          {{"field", Figma::toString(field)}, {"type", Figma::toString(type())}});
    // Feature flag should be set at low percentage in prod to sample the issue.
    if (FF::allow_set_field_error_report()) {
      REPORT_ERROR("allowSetField denied");
    }
  }
}

bool Node::settingResizeToFitOnFixedSizeFrame(const NodeField field, const Any& value) const {
  // TODO: as with the other invariants checks, it would be nice if this invariant were checked in
  // a more consolidated way. It's not straightforward to do this because we do currently set
  // this property to false when creating frames, and we don't have a general way to disallow
  // setting properties to a particular value (in this case true).
  NodeType ntype = type();
  return field == NodeField::RESIZE_TO_FIT &&
         (ntype == NodeType::SYMBOL || ntype == NodeType::INSTANCE ||
          isNodeTypeInImmutableFrame(ntype)) &&
         value.as<bool>() == true;
}

// When we add a new property to a node, most nodes in the wild don't have that
// property.
//
// One way to handle this would be to aggressively backfill every possible
// property on every node when we load documents. We don't do this. Instead,
// when we add a new property we describe its default value in the map below.
// We only ever store data for a lazily populated property if a user has changed
// it from its default value.
//
// This map is used to handle a variety of edge cases around lazily stored
// properties. See the uses of `isLazilyPopulatedNodeField` for more info.
//
// lazyDefaults are now controlled from node-fields-codegen by setting the lazyDefault
// property of the field.
const DenseNodeFields& lazyDefaults() {
  // Local static variables are only initialized once, and feature flags cannot change over the
  // course of a session, so this allows us to avoid the overhead of checking the feature flag every
  // time we want to check which fields are lazy.
  static const DenseNodeFields& _lazyDefaults = FF::lazify_more_defaults()
                                                    ? DenseNodeFields(FieldToAny{
#define NODE_FIELD_WITH_DEFAULT(enumValue, default) {NodeField::enumValue, default},
#define LAZIFYING_FIELD_WITH_DEFAULT(enumValue, default, index) {NodeField::enumValue, default},
#include "FGNodeFieldInclude.h"
                                                      })
                                                    : DenseNodeFields(FieldToAny{
#define NODE_FIELD_WITH_DEFAULT(enumValue, default) {NodeField::enumValue, default},
#include "FGNodeFieldInclude.h"
                                                      });

  return _lazyDefaults;
};

// We want to lazify these NodeFields, but they currently have a default value that doesn't match
// the default value we eventually want them to have. This means before we can lazify these fields
// we must first ensure that no nodes exist with these fields unset.
//
// These are "Bloc B" fields targeted by #feat-remove-nodefield-defaults.
const NodeFieldSet& fieldsToEnsureAreAlwaysSet() {
  static NodeFieldSet fieldSet({
      NodeField::VISIBLE,
      NodeField::OPACITY,
      NodeField::STROKE_JOIN,
      NodeField::STROKE_ALIGN,
      NodeField::STROKE_WEIGHT,
  });

  return fieldSet;
}

// Any field not set will be empty Any.
static Array<Any> allValuesAsAny(const DenseNodeFields& fields) {
  Array<Any> out(static_cast<int>(NodeField::_END));
  for (const auto& it : fields.iter(DenseNodeFields::IterMode::NORMAL)) {
    out[static_cast<int>(it.field())] = fields.getCopyOf(it.field());
  }
  return out;
}

bool isLazilyPopulatedNodeField(NodeField field) { return lazyFields().has(field); }
bool isLazifyingNodeField(NodeField field) {
  return FF::lazify_more_defaults() && lazifyingFields().has(field);
}

const Any& defaultValueForLazilyPopulatedNodeField(NodeField field) {
  static const Array<Any>& _values = allValuesAsAny(lazyDefaults());

  auto idx = static_cast<size_t>(field);
  return idx < _values.size() ? _values[idx] : emptyValue<Any>();
}

bool Node::settingLazyPopulatedFieldToDefault(NodeField field, const Any& value) const {
  // This property has an implicit default value. Do not proceed through the rest of this
  // function, or we risk triggering a SceneGraphEvent with the same old and new value. See
  // comment in hackPopulateOldValueWithDefaultForMultiplayer for details.
  return isLazilyPopulatedNodeField(field) && !hasNodeField(field) &&
         defaultValueForLazilyPopulatedNodeField(field) == value;
}

// This function is called from cloning and symbolUpdate.
// It is performance sensitive, used from the inner loop of SymbolUpdater.
bool Node::copyPropertyFromRawPtr(NodeField field, const Node* node) {
  if (!isValid()) return false;
  updateStyleRegistryBeforePropertyChange(field);

  if (!descendsFromRoot() && field != NodeField::PARENT_INDEX) {
    // copyFrom will use fastest possible path, avoiding intermediate copy
    _fields.copyFrom(field, node->fields());
    updateCachesAfterPropertyChange(field, NodeOrigin::LOCAL);
    return false;
  }
  // Slow path if there will be scene graph events.
  return set(field, node->getCopyOf(field));
}

void Node::copyPropertyFrom(NodeField field, ConstNodePtr node) {
  if (const Node* p = node.getOrNull()) {
    copyPropertyFromRawPtr(field, p);
  }
}

void Node::copyPropertyFrom(NodeField field, NodePtr node) {
  if (const Node* p = node.getOrNull()) {
    copyPropertyFromRawPtr(field, p);
  }
}

// Side effect of setting Any{}: DenseNodeField will hereafter return false for has(field).
void Node::eraseField(NodeField field) {
  REPORT_IF_FALSE_DEBUG_ONLY(field != NodeField::PARENT_INDEX);
  updateStyleRegistryBeforePropertyChange(field);
  if (hasNodeField(field)) {
    Any erasedVal = isLazilyPopulatedNodeField(field)
                        ? DenseNodeFields::defaultsForNode().getCopyOrDefaultOf(field)
                        : Any{};

    if (descendsFromRoot()) {
      set(field, erasedVal);
    } else {
      // Fast path when not on scene, do not bother generating events.
      // We should consider folding this fast-path logic (along with the parallel implementation
      // in copyPropertyFrom() into Node::set() at some point.
      _fields.set(field, std::move(erasedVal));
      updateCachesAfterPropertyChange(field, NodeOrigin::LOCAL);
    }
  }
}

void Node::eraseFieldOrSetDefault(NodeField field) {
  if (!hasNodeField(field)) {
    return;
  }

  if (tracking() == NodeTracking::IGNORE) {
    eraseField(field);
    return;
  }

  set(field, DenseNodeFields::defaultsForNode().getCopyOrDefaultOf(field));
}

// Return a node that needs invalidation for text truncation or search highlights when we enter/exit
// text edit mode on that node. If an edit mode change shouldn't affect such a node (if it doesn't
// pertain to text edit modes, or the text node being edited isn't in a SWT or search mode is
// inactive) return a nullptr to indicate nothing needs invalidation.
static NodePtr nodeToInvalidateForTextEditModeChange(EditModeData newEditMode,
                                                     Optional<EditModeData> oldEditMode,
                                                     SceneGraph* scene) {
  // When we enter/exit text edit mode on truncated text nodes or any text node with highlights when
  // search is active, we need to regenerate the render tree to show or hide overflow text and/or
  // search highlights. We change edit modes on the *canvas*, not on a given editing node, so we'll
  // check both the type condition and then the condition of the node contained in this edit mode
  // data.

  bool isSearchActive = false;
  if (auto appState = getAppState_HackDontUse()) {
    isSearchActive = appState->showCanvasSearch();
  }

  bool isTextEditMode = newEditMode.type == EditModeType::TEXT;
  bool wasTextEditMode = oldEditMode && oldEditMode->type == EditModeType::TEXT;

  if (!isTextEditMode && !wasTextEditMode) return nullptr;

  NodePtr editNode = isTextEditMode ? newEditMode.node(scene) : oldEditMode->node(scene);
  if (!editNode) {
    return nullptr;
  }
  if (!editNode->derivedTextData()->truncationStartIndex() &&
      (!isSearchActive || editNode->canvasSearchHighlights()->empty())) {
    // nothing to do if the node wasn't truncated and doesn't have highlights
    return nullptr;
  }

  if (editNode->isInImmutableFrame()) {
    if (NodePtr containingIF = editNode->findContainingImmutableFrame()) {
      if (containingIF->type() == NodeType::SHAPE_WITH_TEXT) {
        return editNode;
      }
    }
  }

  return editNode;
}

void Node::updateStyleRegistryBeforePropertyChange(NodeField field) {
  if (!isValid()) return;

  // If these properties are about to be changed, it is possible that the node's StyleId will
  // change. To keep our StyleId : NodePtr mapping up to date, we should potentially unregister the
  // asset. If the StyleId does not change as a result of this, we will still unregister here and
  // re-register in updateCachesAfterPropertyChange.
  // TODO: Remove this method after we change style updates to be immutable.
  if (field == NodeField::SHARED_STYLE_MASTER_DATA || field == NodeField::SHARED_STYLE_REFERENCE) {
    scene()->maybeUnregisterAsset(self());
  }
}

void Node::updateCachesAfterPropertyChange(NodeField field, NodeOrigin origin, Any oldValue) {
  if (!isValid()) return;

  const Any oldValueIfLocal = origin == NodeOrigin::LOCAL ? oldValue : Any();

  switch (field) {
    case NodeField::IS_MASK:
    case NodeField::VISIBLE: {
      bool isVisibleMask;
      {
        // Normally, we want to prohibit access to these fields while they're unset because they
        // will eventually be lazified to non-trivial default values, which means such reads will
        // eventually return a different value. However, these reads occur on every call to
        // Node::set, so they're very difficult to avoid. In any case, these accesses are safe
        // because they produce no side effects and this node will not be used for rendering while
        // it is unrooted.
        SilenceUnsetNodeFieldAccessScope scope(scene(), !descendsFromRoot());
        isVisibleMask = isVisible() && isMask();
      }

      // Update the state of the parent if there was a change
      if (NodePtr pnode = parent()) {
        // Is this now a visible mask?
        if (isVisibleMask && !_cachedIsVisibleMask) {
          pnode->incVisibleMaskChildCount(1);
        }

        // Is this no longer a visible mask?
        else if (_cachedIsVisibleMask && !isVisibleMask) {
          pnode->incVisibleMaskChildCount(-1);
        }
      }

      _cachedIsVisibleMask = isVisibleMask;
      {
        SilenceUnsetNodeFieldAccessScope scope(scene(), !descendsFromRoot());
        invalidateLayoutGridCachesForAncestors(InvalidateLayoutGridCache::SKIP_IF_STAYED_EMPTY);
      }
      break;
    }

    case NodeField::IS_FRAME_CLIPPING_DISABLED:
    case NodeField::OVERLAY_IS_FRAME_CLIPPING_DISABLED:
    // Masks stop at the split between fixed and scrolling children. If a node
    // is made fixed (or not fixed), the mask propagation of its siblings might
    // be affected.
    case NodeField::SCROLL_BEHAVIOR: {
      // Update the cached mask of all of our siblings if our parent has a mask
      if (NodePtr pnode = parent()) {
        // Had invalid pointer; no-op.
        if (pnode->hasVisibleMasksAsChildren()) {
          pnode->setChildSiblingMasksDirty();
        }
      }
      break;
    }

    case NodeField::TYPE: {
      auto value = type();
      SceneGraph* scn = scene();
      if (value == NodeType::GROUP_DEPRECATED) {
        scn->_mightContainGroups = true;
      }

      // Keep track of all slices in the scene
      if (value == NodeType::SLICE) {
        scn->_allSlices.insert(self());
      } else if (value == NodeType::MODULE) {
        scn->_allModules.insert(self());
      } else {
        // We don't have to check for a slice turning from a SLICE into something else because that
        // should never happen! Either way, `forEachSlice` automatically checks to see if the nodes
        // in `_allSlices` are actually slices.
      }

      if (value == NodeType::INSTANCE || value == NodeType::SYMBOL) {
        // If a node is being turned into an instance/symbol (for example when we handle the type
        // node-change, during file load), we need to update the instance/symbol flags for its
        // children.
        //
        // Technically, we should also update the instance/symbol flags when a node changes type
        // away from being an instance/symbol. Luckily, that should never actually happen because we
        // avoid changing NodeType whenever we can. For example, when detaching an instance, we
        // actually generate a new frame to represent the instance rather than change the instance
        // into a frame.
        for (const NodePtr& child : _cachedChildren) {
          child->_updateInInstanceAfterParentChange(isOrInInstance());
          child->_updateInSymbolAfterParentChange(isOrInSymbol());
        }
      }

      // If node is being converted to module or from module, update the module flags of its
      // children. The check for when node changes type _away_ from a module should not be
      // necessary, because node type is immutable. However, we have it here just in case.
      if (value == NodeType::MODULE ||
          (oldValue == NodeType::MODULE && value != NodeType::MODULE)) {
        for (const NodePtr& child : _cachedChildren) {
          child->_updateInModuleAfterParentChange(isOrInModule());
        }
      }

      // If node is being converted to slide or from slide, update the slide flags of its
      // children. The check for when node changes type _away_ from a slide should not be
      // necessary, because node type is immutable. However, we have it here just in case.
      if (value == NodeType::SLIDE || (oldValue == NodeType::SLIDE && value != NodeType::SLIDE)) {
        for (const NodePtr& child : _cachedChildren) {
          child->_updateInSlideAfterParentChange(isOrInSlide());
        }
      }

      // Update the cached mask of all of our siblings if our parent has a mask and our type was
      // just changed (it might have just changed to/from a frame/symbol/instance, all of which stop
      // mask propagation)
      if (NodePtr pnode = parent()) {
        if (pnode->hasVisibleMasksAsChildren()) {
          pnode->setChildSiblingMasksDirty();
        }
      }

      {
        // See comment in NodeField::VISIBLE case above.
        SilenceUnsetNodeFieldAccessScope scope(scene(), !descendsFromRoot());
        invalidateLayoutGridCachesForAncestors(InvalidateLayoutGridCache::SKIP_IF_STAYED_EMPTY);
      }
      break;
    }

    case NodeField::MODULE_TYPE: {
      for (const NodePtr& child : _cachedChildren) {
        child->_updateInModuleAfterParentChange(isOrInModule());
      }
      break;
    }

    // in the case of scroll offset too
    case NodeField::OVERLAY_TRANSFORM:
    case NodeField::OVERFLOW_DIRECTION: {
      _invalidateAbsoluteTransformsOfSelfAndChildren();
      break;
    }

    case NodeField::RELATIVE_TRANSFORM: {
      _invalidateAbsoluteTransformsOfSelfAndChildren();
      if (origin == NodeOrigin::REMOTE) {
        // We only need to invalidate the diagram bounds for remote changes here, since local
        // changes will trigger a recomputation of layout via DiagramObserver.
        _invalidateDiagramCanvasSpaceBounds();
      }
      break;
    }

    case NodeField::SIZE: {
      _isAbsoluteTextBoundsDirty = true;
      if (origin == NodeOrigin::REMOTE) {
        // We only need to invalidate the diagram bounds for remote changes here, since local
        // changes will trigger a recomputation of layout via DiagramObserver.
        _invalidateDiagramCanvasSpaceBounds();
      }
      break;
    }

    case NodeField::LEADING_TRIM: {
      _isAbsoluteTextBoundsDirty = true;
      break;
    }

    case NodeField::TEXT_DATA: {
      _isAbsoluteTextBoundsDirty = true;
      break;
    }

    case NodeField::LAYOUT_GRID_DATA:
    case NodeField::INHERIT_GRID_STYLE_ID:
    case NodeField::STYLE_ID_FOR_GRID: {
      {
        // See comment in NodeField::VISIBLE case above.
        SilenceUnsetNodeFieldAccessScope scope(scene(), !descendsFromRoot());
        invalidateLayoutGridCachesForAncestors(InvalidateLayoutGridCache::SKIP_IF_STAYED_EMPTY);
      }
      break;
    }

    case NodeField::EDIT_MODE_DATA: {
      if (auto editNode = nodeToInvalidateForTextEditModeChange(
              editMode(), oldValueIfLocal.asOrEmpty<EditModeData>(), scene())) {
        editNode->invalidateRenderTreeForSelfAndParents();
      }

      break;
    }

    case NodeField::DIAGRAM_PARENT_INDEX: {
      if (!oldValue.isEmpty()) {
        DiagramParentIndex oldIndex = oldValue.as<DiagramParentIndex>();
        GUID newId = diagramParentIndex().guid;
        if (oldIndex.guid != newId) {
          _diagramParentChanged(oldIndex.guid, newId);
        } else if (oldIndex.position != diagramParentIndex().position) {
          NodePtr diagramParent = scene()->nodeByIdOrNull(newId);
          if (diagramParent && diagramParent->descendsFromRoot()) {
            diagramParent->_diagramChildOrderChanged();
          }
        }
      }
      break;
    }

    default: {
      break;
    }
  }

  if (!scene()->isLoading()) {
    _invalidateBoundsFromPropertyChange(field, origin, oldValueIfLocal);
    if (hasWeakHandle() && scene()->hasViewerShim()) {
      if (Optional<int> skewField = nodeFieldToViewerField(field)) {
        DeprecatedJsSceneHooks::updateNodeAfterPropertyChange(getOrCreateWeakHandle(), *skewField);
      }
    }
  }

  // TODO: Move this into the above switch statement so we only register assets in response to
  // fields which can affect the computed AssetId.
  scene()->maybeRegisterAsset(self());
}

void Node::invalidateLayoutGridCachesForAncestors(InvalidateLayoutGridCache mode) {
  if (!isValid()) return;

  auto& map = selfAndChildrenWithLayoutGridsForNode();

  switch (mode) {
    case InvalidateLayoutGridCache::SKIP_IF_EMPTY:
      if (selfAndChildrenWithLayoutGrids()->empty()) return;
      break;

    case InvalidateLayoutGridCache::SKIP_IF_STAYED_EMPTY: {
      auto it = map.find(_fields.dkey());
      bool wasEmpty = it != map.end() && it->second && it->second->empty();
      map.erase(_fields.dkey());
      if (wasEmpty && selfAndChildrenWithLayoutGrids()->empty()) return;
      break;
    }
  }

  for (NodePtr p = parent(); p != nullptr; p = p->parent()) {
    map.erase(p->_fields.dkey());
  }
}

Shared<Array<ConstNodePtr>> Node::_computeSelfAndChildrenWithLayoutGrids() const {
  Shared<Array<ConstNodePtr>> arrayPtr;

  if (isNodeTypeWithChildren(type()) && isVisible() && !isMask()) {
    if (layoutGridData()->anyVisible()) {
      arrayPtr = makeShared<Array<ConstNodePtr>>();
      arrayPtr->emplace_back(self());
    }

    for (const ConstNodePtr& child : children()) {
      Shared<const Array<ConstNodePtr>> childArray = child->selfAndChildrenWithLayoutGrids();
      if (childArray->empty()) continue;
      if (!arrayPtr) arrayPtr = makeShared<Array<ConstNodePtr>>();
      arrayPtr->insert(arrayPtr->end(), childArray->begin(), childArray->end());
    }
  }

  if (!arrayPtr) {
    static auto emptyPtr = makeShared<Array<ConstNodePtr>>();
    return emptyPtr;
  }

  return arrayPtr;
}

Shared<const Array<ConstNodePtr>> Node::selfAndChildrenWithLayoutGrids() const {
  auto& map = selfAndChildrenWithLayoutGridsForNode();
  auto it = map.find(_fields.dkey());
  if (it == map.end()) {
    it = map.insert_or_assign(_fields.dkey(), _computeSelfAndChildrenWithLayoutGrids()).first;
  }
  return it->second;
}

Immutable<EffectData> Node::effectData() const {
  if (ConstNodePtr styleSource =
          safelyGetDirectlyInheritedStyle(InheritStyleField::INHERIT_EFFECT_STYLE_ID)) {
    auto effectData = styleSource->effectData();
    if (auto effectDataResolved = resolveEffectData(effectData, self())) {
      if (!FF::skip_unnecessary_node_caches()) {
        resolvedEffectDataForNode().insert_or_assign(_fields.dkey(), *effectDataResolved);
      }
      return *effectDataResolved;
    }
    return effectData;
  }
  return fields().effectData();
}

Optional<Immutable<EffectData>> Node::effectDataToRender() const {
  return (supportsEffectData() && hasRenderableEffects())
             ? Optional<Immutable<EffectData>>(effectData())
             : nullopt;
}

const ExportSettingsData* Node::exportSettingsData() const { return fields().exportSettingsData(); }

const ExportSettingsData* Node::exportSettingsDataToRender() const {
  const ExportSettingsData* data = exportSettingsData();

  // fallback to PNG 1x, be careful with this routine
  if (!data) {
    data = fallbackExportSettings();
  }

  return data;
}

// Always returns non-null pointer to singleton default settings.
const ExportSettingsData* Node::fallbackExportSettings() {
  static ExportSettingsData fallbackSettings;
  if (fallbackSettings.empty()) {
    fallbackSettings.exportSettingsList.emplace_back();  // png, 1x, no suffix
  }
  return &fallbackSettings;
}

Immutable<LayoutGridData> Node::layoutGridData() const {
  if (ConstNodePtr styleSource =
          safelyGetDirectlyInheritedStyle(InheritStyleField::INHERIT_GRID_STYLE_ID)) {
    auto layoutGridData = styleSource->layoutGridData();
    if (auto layoutGridDataResolved = resolveLayoutGridData(layoutGridData, self())) {
      if (!FF::skip_unnecessary_node_caches()) {
        resolvedLayoutGridDataForNode().insert_or_assign(_fields.dkey(), *layoutGridDataResolved);
      }
      return *layoutGridDataResolved;
    }
    return layoutGridData;
  }
  return fields().layoutGridData();
}

Immutable<LayoutGridData> Node::layoutGridDataToRender() const {
  return areLayoutGridsApplicable() ? layoutGridData() : Immutable<LayoutGridData>{};
}

bool Node::areLayoutGridsApplicable() const {
  // test isResizeToFit, since that tests frame type first off cache, before doing get.
  if (!isStackOrFixedSizeContainer()) return false;

  // The frame must be custom-size, unrotated, and unflipped in canvas space
  auto transform = absoluteTransform();
  return std::fabs(transform.axisX().y) + std::fabs(transform.axisY().x) < 1e-6 &&
         transform.axisX().x > 0 && transform.axisY().y > 0;
}

bool Node::supportsGeometryCache() const {
  if (isNodeTypeInFrame(type())) {
    // We're able to render geometry for all frames/symbols/instances that have been migrated
    // during the `frame_properties_migration` project. It's worth noting that while we check
    // `!isGroup()` in the methods below (for example in `supportsRoundedCorners()`), we don't check
    // that here! That's because we still want to be able to render groups that have somehow already
    // have obtained fill/stroke/corners. However, at a product level, we don't want to allow users
    // to add fill/stroke/corners to groups.
    //
    // TODO: once `frame_properties_migration` finishes, we can just return true here!
    return containerSupportsFillStrokeAndCorners();
  }
  return usesGeometryCacheExcludingFrames(type());
}

bool Node::supportsRoundedCorners() const {
  if (isNodeTypeInFrame(type())) {
    // TODO: once `frame_properties_migration` finishes, we can just return true here!
    return (containerSupportsFillStrokeAndCorners() || isNodeTypeInImmutableFrame(type()))
           // Unlike fixed-size frames, instance, and symbols -- groups do not support corners.
           && !isGroup()
           // Stickies and modules and slides don't support rounded corners.
           && type() != NodeType::STICKY && type() != NodeType::MODULE && !isSlide() && !isTable();
  }

  return usesGeometryCacheExcludingFrames(type())
         // The only three exceptions are text, line-like objects, and stamps -- it doesn't
         // make sense for any of those to support corners.
         && type() != NodeType::TEXT && !isLineLike(type()) && !isStamp();
}

bool Node::supportsIndependentCornerRadii() const {
  if (isNodeTypeInFrame(type())) {
    // TODO: once `frame_properties_migration` finishes, we can just return true here!
    return containerSupportsFillStrokeAndCorners()
           // Unlike fixed-size frames, instance, and symbols -- groups do not support corners.
           && !isGroup() && !isSlide() && !isTable();
  }
  return type() == NodeType::RECTANGLE || type() == NodeType::ROUNDED_RECTANGLE;
}

size_t Node::countStrokeGeometryUniqueRegionIds() const {
  return geometryCache().uniqueStyleIDs(GeometryType::STROKE, UnnecessaryGeometry::IGNORE).size();
}

bool Node::supportsFillPaintData(IncludeGroups includeGroups) const {
  if (isNodeTypeInFrame(type())) {
    // TODO: once `frame_properties_migration` finishes, we can just return true here!
    return (containerSupportsFillStrokeAndCorners() || isNodeTypeInImmutableFrame(type()))
           // As long as we don't have filled connector endpoints, we don't have anything fillable
           // for top-level connectors!
           && type() != NodeType::CONNECTOR
           // We don't support stroke for modules
           && type() != NodeType::MODULE
           // We don't want users to be able to directly set fill/stroke/corners on groups.
           // However, when fill/stroke/corners are already set on a group, we still want to
           // render the group correctly -- and give the user UX to clear those fields.
           && (includeGroups == IncludeGroups::YES || !isGroup());
  }
  return usesGeometryCacheExcludingFrames(type());
}

bool Node::supportsStrokePaintData(IncludeGroups includeGroups) const {
  if (isInImmutableFrame()) {
    if (ConstNodePtr containingIF = findContainingImmutableFrame()) {
      switch (containingIF->type()) {
        case NodeType::CONNECTOR:
          // Within connectors, only the line can have stroke paints
          return type() == NodeType::VECTOR;
        case NodeType::SHAPE_WITH_TEXT:
          // The backing shapes of shapes with text can have strokes. These are any of several
          // different shape types, but we can just check that this node is not the text node.
          return type() != NodeType::TEXT;
        case NodeType::STICKY:
          // No sticky sublayers support stroke.
          return false;
        case NodeType::CODE_BLOCK:
          return false;
        case NodeType::MEDIA:
          // Only the media sublayer frame with the image fill can have stroke paints
          return immutableFrameSublayerPath() ==
                 MediaMagic::getSublayerPathForMedia(containingIF->uniqueID());
        case NodeType::SECTION_OVERLAY:
          return false;
        case NodeType::TABLE:
          return type() == NodeType::TABLE_CELL;
        default:
          REPORT_ERROR("Unknown IF type");
      }
    }
  }
  if (isNodeTypeInFrame(type())) {
    // TODO: once `frame_properties_migration` finishes, we can just return true here!
    return (containerSupportsFillStrokeAndCorners() || isNodeTypeInImmutableFrame(type()))
           // We don't support stroke for stickies
           && type() != NodeType::STICKY
           // We don't support stroke for modules
           && type() != NodeType::MODULE
           // We don't want users to be able to directly set fill/stroke/corners on groups.
           // However, when fill/stroke/corners are already set on a group, we still want to
           // render the group correctly -- and give the user UX to clear those fields.
           && (includeGroups == IncludeGroups::YES || !isGroup());
  }
  return usesGeometryCacheExcludingFrames(type());
}

bool Node::hasCornersOrStroke() const {
  return strokePaintData()->containsEnabledVisiblePaint() || cornerRadius() != 0 ||
         rectangleTopLeftCornerRadius() != 0 || rectangleTopRightCornerRadius() != 0 ||
         rectangleBottomLeftCornerRadius() != 0 || rectangleBottomRightCornerRadius() != 0;
}

TerminalCount Node::terminalCount() const {
  switch (type()) {
    case NodeType::CONNECTOR:
    case NodeType::LINE:
    case NodeType::WASHI_TAPE:
      return TerminalCount::TWO;
    case NodeType::VECTOR:
    case NodeType::HIGHLIGHT:
      return hasCompiledVectorData() ? toTerminalCount(compiledVectorData()->terminalCount())
                                     : TerminalCount::ZERO;
    default:
      if (!supportsStrokePaintData()) {
        // It's weird if we get here. We used to log errors to Sentry, but we can't figure out a
        // repro. (Sentry implies that this is being called by Group nodes through
        // PropertiesPanelHelper, which implies that some groups have non-empty stroke paints??)
        // Still, it's going to be technically here correct to return zero.
      }
      // Other node types are all closed shapes.
      return TerminalCount::ZERO;
  }
}

// In an alternate universe we might cache this computed info on the Node, but it's actually not
// that expensive to compute and is only needed in when the stroke panel is open and stroke-related
// properties change, so rather than taking up more memory on Node instances, just compute this when
// we need it.
CapDisplayInfo Node::capDisplayInfoForVectorTypes() const {
  if (type() != NodeType::VECTOR) {
    sloge("Fetching capDisplayInfo for node type that shouldn't need cap display info",
          {{"type", debugReadableTypeName()}});
    REPORT_ERROR("Fetching capDisplayInfo for node type that shouldn't need cap display info");
    return {};
  }

  if (!hasCompiledVectorData()) return {};

  CapDisplayInfo info;

  const auto terminals = terminalCount();

  const auto& network = compiledVectorData()->compiledNetwork->toNetwork();
  const auto& indices = compiledVectorData()->terminalIndices();

  const auto& getCapValue = [&](int index) -> Cap {
    const auto& vertex = network.vertices()[index];
    return compiledVectorData()
        ->styleOverrideTable.get(vertex.styleID())
        .get(NodeField::STROKE_CAP)
        .asOrDefault(strokeCap());
  };
  const auto& getVertexPosition = [&](int index) -> Vector {
    const auto& vertex = network.vertices()[index];
    return relativeTransform().transformPoint(vertex.position());
  };

  switch (terminals) {
    case TerminalCount::ZERO:
      REPORT_IF_FALSE_DEBUG_ONLY(indices.empty());
      info.zeroTerminals = true;
      return info;
    case TerminalCount::ONE: {
      REPORT_IF_FALSE_DEBUG_ONLY(indices.size() == 1);
      const int index = indices[0];
      info.single = {getCapValue(index), index};
      return info;
    }
    case TerminalCount::TWO: {
      REPORT_IF_FALSE_DEBUG_ONLY(indices.size() == 2);

      CapWithIndex cap1 = {getCapValue(indices[0]), indices[0]};
      CapWithIndex cap2 = {getCapValue(indices[1]), indices[1]};

      Vector cap1Position = getVertexPosition(indices[0]);
      Vector cap2Position = getVertexPosition(indices[1]);
      if ((isDifferentEnough(cap1Position.x, cap2Position.x) && cap1Position.x > cap2Position.x) ||
          (areApproximatelyEqual(cap1Position.x, cap2Position.x) &&
           cap1Position.y > cap2Position.y)) {
        std::swap(cap1, cap2);
      }

      info.left = std::move(cap1);
      info.right = std::move(cap2);
      return info;
    }
    case TerminalCount::THREE_OR_MORE: {
      REPORT_IF_FALSE_DEBUG_ONLY(indices.size() >= 3);
      Cap capValue = getCapValue(indices[0]);

      // Check to see if we have mixed cap values:
      for (unsigned i = 1; i < indices.size(); i++) {
        Cap nextCapValue = getCapValue(indices[i]);
        if (nextCapValue != capValue) {
          info.mixedCapsWithThreeOrMoreTerminals = true;
          return info;
        }
      }

      if (!info.mixedCapsWithThreeOrMoreTerminals) {
        // Which index we pick doesn't matter, since modifying this in the UI will clobber the cap
        // for the whole node, not just a single terminal.
        info.single = {capValue, indices[0]};
      }
      return info;
    }
  }
}

void Node::convertDefaultTerminalsToOverrides() {
  // When we want to update dash caps without changing the terminal caps, we can use this to change
  // the non-overridden terminals to depend on overrides. After we've made sure all the terminals
  // use overrides, we can update the node stroke cap to change the dash caps while maintaining the
  // appearance of the terminal caps.

  const auto nodeCap = strokeCap();
  const auto& network = compiledVectorData()->compiledNetwork->toNetwork();
  Array<unsigned> indicesToUpdate;

  for (auto idx : compiledVectorData()->terminalIndices()) {
    const auto& vertex = network.vertices()[idx];
    const auto terminalCap = compiledVectorData()
                                 ->styleOverrideTable.get(vertex.styleID())
                                 .get(NodeField::STROKE_CAP)
                                 .asOrEmpty<Cap>();
    if (!terminalCap) {
      indicesToUpdate.push_back(idx);
    }
  }
  // Actually do the update mutations in a separate loop
  if (!indicesToUpdate.empty()) {
    VectorEditor editor(self());
    for (auto idx : indicesToUpdate) {
      editor.updateVertexWithIndex(idx, NodeField::STROKE_CAP, Any(nodeCap));
    }
  }
}

