void Node::setEmbedData(Immutable<EmbedData> data) {
  // Set widget synced state here so we can access the embed data in syncedState (widgets can't
  // read from the scenegraph)
  auto oldSyncedState = fields().widgetSyncedState();
  MultiplayerMap<String, String> newSyncedState =
      oldSyncedState ? MultiplayerMap<String, String>(oldSyncedState->map().copy())
                     : MultiplayerMap<String, String>();
  if (const auto& url = data->url()) {
    newSyncedState.set("syncedState:url", getWidgetSyncedStateString(url->str()));
  }
  newSyncedState.set("syncedState:srcUrl", getWidgetSyncedStateString(data->srcUrl().str()));
  if (const auto& title = data->title()) {
    newSyncedState.set("syncedState:title", getWidgetSyncedStateString(title->str()));
  }
  if (const auto& description = data->description()) {
    newSyncedState.set("syncedState:description", getWidgetSyncedStateString(description->str()));
  }
  if (const auto& provider = data->provider()) {
    newSyncedState.set("syncedState:provider", getWidgetSyncedStateString(provider->str()));
  }
  if (const auto& thumbnailImageHash = data->thumbnailImageHash()) {
    newSyncedState.set("syncedState:thumbnailImageHash",
                       getWidgetSyncedStateString(thumbnailImageHash->str()));
  }
  if (const auto& faviconImageHash = data->faviconImageHash()) {
    newSyncedState.set("syncedState:faviconImageHash",
                       getWidgetSyncedStateString(faviconImageHash->str()));
  }
  if (const auto& width = data->width()) {
    newSyncedState.set("syncedState:width", Figma::toString(*width));
  }
  if (const auto& height = data->height()) {
    newSyncedState.set("syncedState:height", Figma::toString(*height));
  }
  newSyncedState.set("syncedState:embedType", getWidgetSyncedStateString(data->embedType().str()));
  newSyncedState.set("syncedState:resolved", data->resolved() ? "true" : "false");
  newSyncedState.set("syncedState:originalText",
                     getWidgetSyncedStateString(data->originalText().str()));
  newSyncedState.set("syncedState:embedVersionId",
                     getWidgetSyncedStateString(data->embedVersionId().str()));
  set(NodeField::WIDGET_SYNCED_STATE,
      makeShared<const MultiplayerMap<String, String>>(std::move(newSyncedState)));

  set(NodeField::EMBED_DATA, std::move(data));
}

