Immutable<PaintData> Node::fillPaintData() const {
  if (!supportsFillPaintData(IncludeGroups::YES)) {
    return Immutable<PaintData>();
  }

  if (isWidgetNodeHovered()) {
    auto hoverStyle = widgetHoverStyle();
    if (hoverStyle->areFillPaintsSet) {
      return hoverStyle->fillPaints;
    }
  }

  if (auto style = safelyGetDirectlyInheritedStyle(InheritStyleField::INHERIT_FILL_STYLE_ID)) {
    auto paintData = style->fillPaintData();
    if (auto paintDataResolved = resolvePaintData(paintData, self())) {
      if (!FF::skip_unnecessary_node_caches()) {
        resolvedFillPaintDataForNode().insert_or_assign(_fields.dkey(), *paintDataResolved);
      }
      return *paintDataResolved;
    }
    return paintData;
  }

  return fields().fillPaintData();
}

Immutable<PaintData> Node::strokePaintData() const {
  if (!supportsStrokePaintData(IncludeGroups::YES)) {
    return Immutable<PaintData>();
  }

  if (isWidgetNodeHovered()) {
    auto hoverStyle = widgetHoverStyle();
    if (hoverStyle->areStrokePaintsSet) {
      return hoverStyle->strokePaints;
    }
  }

  if (auto style =
          safelyGetDirectlyInheritedStyle(InheritStyleField::INHERIT_FILL_STYLE_ID_FOR_STROKE)) {
    auto paintData = style->fillPaintData();
    if (auto paintDataResolved = resolvePaintData(paintData, self())) {
      if (!FF::skip_unnecessary_node_caches()) {
        resolvedStrokePaintDataForNode().insert_or_assign(_fields.dkey(), *paintDataResolved);
      }
      return *paintDataResolved;
    }
    return paintData;
  }
  return fields().strokePaintData();
}

