// Whether the node supports Cmd+resize to crop an image fill
// (needs to be rect or a frame, and actually have an image fill to crop)
bool Node::canCropWhileResizing() const {
  if (type() == NodeType::ROUNDED_RECTANGLE || type() == NodeType::RECTANGLE ||
      (type() == NodeType::FRAME && !isGroup()) || isNodeTypeInSymbolOrInstance(type())) {
    return fillPaintData()->hasEnabledStaticImagePaint() ||
           fillPaintData()->hasEnabledAnimatedPaint();
  }
  return false;
}

