         parent()->children().back()->uniqueID() == uniqueID();
}

// Determine if a node is a possibly used as an Icon, based on a heuristic.
// This is used in Lego to avoid drilling too deep
// This heuristic is based on
// https://github.com/figma/figma/blob/1c4ecdb346841c715c1bfc2694133eb9cf0badfe/codegen/extensions/src/shared/index.ts#L139
bool Node::isIconLike() const {
  if (type() != NodeType::VECTOR_OPERATION && type() != NodeType::VECTOR &&
      type() != NodeType::ELLIPSE && type() != NodeType::REGULAR_POLYGON &&
      type() != NodeType::STAR) {
    return false;
  }

  return true;
}

bool Node::isIconLikeContainer() const {
  Optional<Set<GUID>> _unused(Set<GUID>({}));
  return findIconLikes(self(), _unused, false);
}

bool Node::isStickable() const {
  if (isStamp() || isHighlight() || isWashiTape() || isForcedStickable()) {
    return true;
  }

  // A group is stickable if it's non-empty and all of its children are stickable.
  if (isGroup() && children().size() > 0) {
    bool isStickable = true;
    for (const ConstNodePtr& child : children()) {
      if (!child->isStickable()) {
        isStickable = false;
        break;
      }
    }
    return isStickable;
  }

  return false;
}

bool Node::isStickableHostCandidate() const {
  return !(isInstanceSublayer() || isInWidget() || isInImmutableFrame() || isStickable() ||
           isGroup() || isSection() || isOrInConnector() || isSectionOverlay() ||
           isLockedInCanvas());
}

bool Node::isVotingStickable() const {
  return (isStamp() && !stampData()->votingSessionId().empty());
}

bool Node::isVotingStickableHostCandidate() const {
  return (type() == NodeType::STICKY || type() == NodeType::SHAPE_WITH_TEXT ||
          type() == NodeType::TEXT || type() == NodeType::FRAME ||
          type() == NodeType::ROUNDED_RECTANGLE || type() == NodeType::SYMBOL ||
          type() == NodeType::CODE_BLOCK) &&
         isStickableHostCandidate();
}

bool Node::isAllowedToContainAtMentions() const {
  if (isOrInWidget() || isOrInInstance() || isOrInSymbol()) {
    return false;
  }
  if (auto containingImmutableFrame = findContainingImmutableFrame()) {
    if (containingImmutableFrame->type() == NodeType::CODE_BLOCK) {
      return false;
    }
  }
  return true;
}

bool Node::isErasable() const {
  return !isLockedInCanvas() &&
         ((type() == NodeType::VECTOR && !isInImmutableFrame()) || isHighlight() || isWashiTape());
}

bool Node::isFirstDraft() const {
  const PluginData& pluginData = this->pluginData();
  return pluginData.hasKey("", "plugin_playground-jsx");
}

bool Node::isFirstDraftBuildingBlock() const {
  const PluginData& pluginData = this->pluginData();
  return pluginData.hasKey("", "jsx-isDone");
}

bool Node::shouldExtendRoundedCornersToEdges() const {
  // Determine whether we should extend rounded corners to bbox edges -- this betrays the underlying
  // geometry but looks nicer in FigJam.
  if (auto containingIF = findContainingImmutableFrame()) {
    if (containingIF->immutableFrameShape() == self() &&
        containingIF->type() == NodeType::SHAPE_WITH_TEXT) {
      return ShapeWithTextStyle::shapeTypeRequiresCornerExtension(
          containingIF->shapeWithTextType());
    }
  }
  return false;
}

// We use "baseline" here to mean the minimum height that a shape should reflow to. A manually sized
// shape will have a custom baseline height, but when the user clicks the shape truncation reset
// chevron button, we'll reset the baseline to the default height (208).
void Node::setShapeWithTextBaselineHeight(double height, BaselineUpdateEvent baselineUpdateEvent) {
  if (fileHasShapeTruncatesMigration(_scene)) {
    switch (baselineUpdateEvent) {
      case BaselineUpdateEvent::RESET_BASELINE: {
        // shapeUserHeight=0 is an invalid value, but we don't know whether we want this to be 208
        // (the default height) or something smaller -- when the text node can fit in a smaller
        // SWT, we want the reset button to just reset it to the min height required. But since we
        // won't know how tall the SWT needs to be until we perform reflow, set it to 0 here to
        // use as a flag that we should update shapeUserHeight in reflow.
        set(NodeField::SHAPE_USER_HEIGHT, Any(0.f));

        set(NodeField::SHAPE_TRUNCATES, Any(false));
        break;
      }
      case BaselineUpdateEvent::REFLOW_TO_HEIGHT:
      case BaselineUpdateEvent::CREATE_DEFAULT: {
        set(NodeField::SHAPE_USER_HEIGHT, Any(float(height)));
        set(NodeField::SHAPE_TRUNCATES, Any(false));
        break;
      }
      case BaselineUpdateEvent::USER_RESIZE: {
        set(NodeField::SHAPE_USER_HEIGHT, Any(float(height)));
        set(NodeField::SHAPE_TRUNCATES, Any(true));
        break;
      }
      case BaselineUpdateEvent::HEIGHT_ONLY: {
        // Set height but maintain existing truncation properties
        set(NodeField::SHAPE_USER_HEIGHT, Any(float(height)));
        break;
      }
    }
  } else {
    if (baselineUpdateEvent == BaselineUpdateEvent::USER_RESIZE) {
      set(NodeField::SHAPE_USER_HEIGHT, Any(float(height)));
    } else {
      set(NodeField::SHAPE_USER_HEIGHT, Any(float(0.)));
    }
  }
}

// Whether a shape should truncate has been determined by different fields depending on migration
// state. Here, abstract that away so that this always tells us whether a shape should truncate.
bool Node::shapeTruncates() const {
  if (fileHasShapeTruncatesMigration(_scene)) {
    return _fields.shapeTruncates();
  } else {
    return _fields.shapeUserHeight() != 0.;
  }
}

float Node::baselineHeight() const {
  const float encodedHeight = _fields.shapeUserHeight();
  if (fileHasShapeTruncatesMigration(_scene)) {
    return encodedHeight;
  } else {
    if (encodedHeight == 0.) return 208.;
    return encodedHeight;
  }
}

bool Node::isConnector() const { return type() == NodeType::CONNECTOR; }

bool Node::isOrInConnector() const {
  ConstNodePtr containingFrame = findContainingImmutableFrame();
  return containingFrame && containingFrame->type() == NodeType::CONNECTOR;
}

bool Node::isConnectorStartAttached() const {
  if (type() == NodeType::CONNECTOR && parent()) {
    return connectorStart().endpointNodeID != parent()->uniqueID() &&
           connectorStart().endpointNodeID != GUID::invalid();
  }
  return false;
}

bool Node::isConnectorEndAttached() const {
  if (type() == NodeType::CONNECTOR && parent()) {
    return connectorEnd().endpointNodeID != parent()->uniqueID() &&
           connectorEnd().endpointNodeID != GUID::invalid();
  }
  return false;
}

Vector Node::connectorStartCanvasPosition() const {
  if (type() == NodeType::CONNECTOR) {
    Vector connectorSpacePosition =
        ConnectorLayout::findConnectorEndpointPositionsInConnectorSpace(self()).start.position;
    return absoluteTransform().transformPoint(connectorSpacePosition);
  }
  return Vector::invalid();
}

Vector Node::connectorEndCanvasPosition() const {
  if (type() == NodeType::CONNECTOR) {
    Vector connectorSpacePosition =
        ConnectorLayout::findConnectorEndpointPositionsInConnectorSpace(self()).end.position;
    return absoluteTransform().transformPoint(connectorSpacePosition);
  }
  return Vector::invalid();
}

NodePtr Node::connectorOtherEndpoint(NodePtr endpoint) {
  if (type() != NodeType::CONNECTOR) {
    return nullptr;
  }

  if (connectorStart().endpointNodeID == endpoint->uniqueID()) {
    return _scene->nodeByIdOrNull(connectorEnd().endpointNodeID);
  }

  if (connectorEnd().endpointNodeID == endpoint->uniqueID()) {
    return _scene->nodeByIdOrNull(connectorStart().endpointNodeID);
  }

  return nullptr;
}

Optional<NodeType> Node::containingImmutableFrameType() const {
  if (ConstNodePtr containingFrame = findContainingImmutableFrame()) {
    return containingFrame->type();
  }
  return nullopt;
}

bool Node::isPrimaryInstance() const { return isInstance() && NodeTracking::TRACK == _tracking; }

bool Node::isNestedInstance() const { return isInstance() && NodeTracking::IGNORE == _tracking; }

GUID Node::overriddenSymbolID() const {
  if (isNestedInstance()) {
    // The OVERRIDDEN_SYMBOL_ID value we're retrieving here is a cached value copied onto a nested
    // instance during SymbolUpdater. The source-of-truth for this values should be stored on the
    // primary instance as an override for this nested-instance's instance-override-path.
    GUID overriddenID = _fields.overriddenSymbolID();
    if (overriddenID.isValid()) {
      return overriddenID;
    } else {
      return GUID::invalid();
    }
  } else {
    REPORT_ERROR("overriddenSymbolID is only meaningful on nested instances");
    return GUID::invalid();
  }
}

const GUID& Node::backingLayerID() const {
  REPORT_IF_FALSE_DEBUG_ONLY(isOrInInstance());
  return _instanceData ? _instanceData->backingLayerID : GUID::invalid();
}

void Node::setBackingLayerID(GUID guid) {
  REPORT_IF_FALSE_DEBUG_ONLY(isOrInInstance() || !guid.isValid());
  // If everything is invalid we don't need to have InstanceData at all (such as if this is called
  // multiple times before updatePrimaryInstanceData or prepareInstanceLayer is called).
  if (!guid.isValid() &&
      (!_instanceData || (!_instanceData->primaryID.isValid() && !_instanceData->path.isValid()))) {
    _instanceData = nullptr;
  } else {
    if (!_instanceData) {
      _instanceData = makeOwned<InstanceData>();
    }
    _instanceData->backingLayerID = guid;
  }
}

// Note that the "without swapping" part will only ignore the overridden symbol ID applied from
// the primary instance which owns this mode. We are reaching one step back in the inheritance
// chain to return THAT instance's symbolID -- if any swaps occur upstream we will inherit it.
// So another way to look at this is "symbol ID we inherited from inheritance chain".
GUID Node::symbolIDWithoutSwapping() const {
  if (!_scene) {
    sloge_debugOnly_previously_loge("expected scene");
    return {};
  }
  if (!isNestedInstance()) {
    sloge_debugOnly_previously_loge("expected nested instance");
    return {};
  }
  // Get the instance from which we are inheriting. Just go one step back, any swaps applied
  // upstream would be applied to the backing node, and that's fine, because we want to return
  // the symbol ID that we WOULD have if the override did not exist on the owning primary instance.
  ConstNodePtr instance = _scene->nodeByIdOrNull(backingLayerID());
  if (!instance || !instance->isInstance()) {
    sloge_debugOnly_previously_loge("expected backing instance");
    return {};
  }
  return instance->symbolID();
}

GUID Node::symbolID() const { return isInstance() ? symbolData().symbolID : GUID{}; }

bool Node::containingProductComponentHasExplicitPropDef(GUID explicitPropDefID) const {
  if (!explicitPropDefID.isValid()) return false;
  if (isStateGroup()) {
    return componentPropDefs().has(explicitPropDefID);
  }

  if (!isOrInSymbol()) return false;
  const auto containingSymbol = findContainingSymbol();
  if (containingSymbol->isState()) {
    return containingSymbol->findContainingStateGroupOrSelf()->componentPropDefs().has(
        explicitPropDefID);
  }
  return containingSymbol->componentPropDefs().has(explicitPropDefID);
}

bool Node::hasComponentPropDefs() const { return !componentPropDefs().empty(); }

const ComponentPropDef* Node::getContainingSymbolPropDef(GUID propDefID) const {
  if (!propDefID.isValid()) return nullptr;
  if (!isOrInSymbol()) return nullptr;
  auto containingSymbol = findContainingSymbol();
  if (!containingSymbol || !containingSymbol->componentPropDefs().has(propDefID)) return nullptr;

  return containingSymbol->componentPropDefs().getOrNull(propDefID);
}

const ComponentPropRef* Node::getComponentPropRef(NodeField field,
                                                  ComponentPropRefGetMode opts) const {
  const ComponentPropRef* componentPropRef = allComponentPropRefs().getOrNull(field);
  if (opts == ComponentPropRefGetMode::NONE || !componentPropRef) {
    // The ref could be a zombie, linked, or nullptr
    return componentPropRef;
  }

  if (!componentPropRef->isLinked()) {
    // At this point, we only want to return linked refs
    return nullptr;
  }

  if (opts == ComponentPropRefGetMode::MUST_BE_LINKED) {
    return componentPropRef;
  }

  ConstNodePtr symbolForRef = findContainingSymbol();

  // Use the containing symbol for primary instances since primary instances
  // can have refs.
  if (!isPrimaryInstance() && isOrInInstance()) {
    // Read the refs from the backing symbol of the closest instance (not including itself).
    // So for non-instance nodes, we look at the containing nested or primary instance and for
    // instance nodes, we look at the containing instance for this instance's parent. If
    // this node is an instance, we know it must be a nested instance (and therefore has a
    // containing instance) because the above condition ensures we are not at a primary instance.
    ConstNodePtr containingInstance = findParentInstance();
    if (!containingInstance) {
      return nullptr;
    }
    symbolForRef = scene()->nodeByIdOrNull(containingInstance->symbolID());
    if (!symbolForRef) {
      return nullptr;
    }
  }

  // Check if the propRef points to a valid propDef. If not, the propRef is not valid.
  auto explicitPropDef =
      getExplicitPropDefForSymbol(symbolForRef, componentPropRef->defIDIfLinked());
  if (explicitPropDef &&
      symbolForRef->containingProductComponentHasExplicitPropDef(explicitPropDef->propID())) {
    return componentPropRef;
  } else if (symbolForRef->isState()) {
    // It is possible for states to temporarily not have the correct fields,
    // which should get corrected when the updater runs.
    // For example, if you have loose component A and an instance,
    // and then later you drag component A into a state group,
    // SymbolUpdater will run for instance A - look at its backing symbol,
    // see that it's a state, and was previously erroring.
    // Once the updater runs, SymbolUpdater will run again, which will get
    // the instance into the correct state.
    return componentPropRef;
  } else {
#if !BUILD_TEST
    sloge("propRef does not point to a valid propDef",
          {{"defID", componentPropRef->defIDIfLinked()},
           {"nodeID", uniqueID()},
           {"field", Figma::toString(field)}});
#endif
    return nullptr;
  }
}

bool Node::hasComponentPropRefs() const { return !allComponentPropRefs().empty(); }

// (TODO taquino) remove function after default values refactor
bool Node::hasComponentPropAssignment(GUID propDefID) const {
  if (!propDefID.isValid() || !isPrimaryInstance()) return false;
  return componentPropAssignmentsRawFieldValue().has(propDefID);
}

void Node::addLibraryMoveHistoryEntry(const GUID& nodeId, const SymbolKey& componentKey) {
  Array<LibraryMoveHistoryItem> newHistory = libraryMoveHistory().toArray();
  newHistory.push_back(LibraryMoveHistoryItem{nodeId, componentKey});
  setLibraryMoveHistory(ImmutableArray<LibraryMoveHistoryItem>(std::move(newHistory)));
}

bool Node::matchesLibraryMoveHistory(const GUID& nodeId, const SymbolKey& componentKey) const {
  auto targetItem = LibraryMoveHistoryItem{nodeId, componentKey};
  for (const auto& nodeItem : libraryMoveHistory()) {
    if (nodeItem == targetItem) {
      return true;
    }
  }
  return false;
}

void Node::overrideSymbolForNestedInstance(GUID symbolID_) {
  REPORT_IF_FALSE_DEBUG_ONLY(isNestedInstance());

  set(NodeField::OVERRIDDEN_SYMBOL_ID, std::move(symbolID_));
}

// Unlike replaceSymbolID(), this doesn't try to retain text overrides.
// This also doesn't check to see if the given symbol is actually a symbol.
void Node::setSymbolForPrimaryInstance(GUID symbolID_) {
  if (!isInstance()) {
    sloge_debugOnly_previously_loge("instance expected");
    return;
  }
  // We shouldn't call symbolID() here because caller already assumes we are in a primary instance,
  // and we want to avoid buggy isPrimaryInstance() on C1 scenes when node isn't parented.
  if (symbolID_ == symbolData().symbolID) {
    return;
  }
  SymbolData copy = symbolData();
  copy.symbolID = symbolID_;
  replaceSymbolData(std::move(copy));
}

void Node::replaceSymbolData(SymbolData&& data) {
  REPORT_IF_FALSE_DEBUG_ONLY(isInstance() || isImmutableFrame());
  set<NodeField::SYMBOL_DATA>(makeShared<const SymbolData>(std::move(data)));
}

void Node::clearSymbolData() {
  static auto empty = makeShared<const SymbolData>();
  if (fields().hasNodeField(NodeField::SYMBOL_DATA)) {
    set<NodeField::SYMBOL_DATA>(empty);
  }
}

void Node::clearDerivedSymbolData() {
  static auto empty = makeShared<const DerivedSymbolData>();
  if (fields().hasNodeField(NodeField::DERIVED_SYMBOL_DATA)) {
    set<NodeField::DERIVED_SYMBOL_DATA>(empty);
  }
}

float Node::instanceUniformScalingFactor() const {
  REPORT_IF_FALSE_DEBUG_ONLY(isPrimaryInstance());
  return symbolData().uniformScaleFactor;
}

void Node::setInstanceUniformScalingFactor(float scale) {
  REPORT_IF_FALSE_DEBUG_ONLY(isPrimaryInstance());

  if (scale == instanceUniformScalingFactor()) {
    return;
  }

  SymbolData copy = symbolData();
  copy.uniformScaleFactor = scale;
  replaceSymbolData(std::move(copy));
}

const SymbolOverrides& Node::symbolOverrides() const {
  if (!isPrimaryInstance()) {
    REPORT_ERROR("symbolOverrides() called on node that is not a primary instance");
    return emptyValue<SymbolOverrides>();
  }

  if (auto data = fields().symbolData()) {
    return data->symbolOverrides;
  }

  return emptySymbol.symbolOverrides;
}

const SymbolOverrideLevels& Node::symbolOverrideLevels() const {
  if (!isPrimaryInstance()) {
    REPORT_ERROR("symbolOverrideLevels() called on node that is not a primary instance");
    return emptyValue<SymbolOverrideLevels>();
  }

  if (auto data = fields().symbolData()) {
    return data->symbolOverrideLevels;
  }

  return emptySymbol.symbolOverrideLevels;
}

// See comment in header file for usage of this function. As much as possible, we should try to
// gate all calls to this function through SymbolOverrider instead of calling ad-hoc everywhere.
void Node::setSymbolOverrides__UNSAFE(SymbolOverrides newOverrides) {
  REPORT_IF_FALSE_DEBUG_ONLY(isPrimaryInstance());

  SymbolData copy = symbolData();
  copy.symbolOverrides = std::move(newOverrides);
  replaceSymbolData(std::move(copy));
}

Optional<Any> Node::getOverrideOnInstanceSublayer(const OverridePath& sublayerPath,
                                                  NodeField field) const {
  REPORT_IF_FALSE_DEBUG_ONLY(isPrimaryInstance());
  const FieldToAny* nodeOverrides = symbolOverrides().getOrNull(sublayerPath);
  return nodeOverrides ? nodeOverrides->getCopy(field) : nullopt;
}
const FieldToAny* Node::getAllOverridesOnInstanceSublayer() const {
  auto primaryInstance = findContainingPrimaryInstance();
  if (primaryInstance) {
    return primaryInstance->symbolOverrides().getOrNull(instanceOverridePath());
  }
  return nullptr;
}

bool Node::layerHasSymbolOverrideApplied(NodeField field) const {
  bool hasOverride = false;
  if (ConstNodePtr primaryInstance = findContainingPrimaryInstance()) {
    const FieldToAny* overrides =
        primaryInstance->symbolOverrides().getOrNull(instanceOverridePath());
    hasOverride = overrides && overrides->has(field);
  }
  return hasOverride;
}

bool Node::containedLayersHaveSymbolOverrides() const {
  bool foundOverrides = false;

  forEachNode(self(), [&foundOverrides](ConstNodePtr node) {
    const auto& overridePath = node->instanceOverridePath();
    if (overridePath.isValid()) {
      if (ConstNodePtr primaryInstance = node->findContainingPrimaryInstance()) {
        if (primaryInstance->symbolOverrides().has(overridePath)) {
          foundOverrides = true;
          return Loop::BREAK;
        }
      }
    }
    // Otherwise, continue searching sublayers of the instance.
    return Loop::CONTINUE;
  });

  return foundOverrides;
}

bool Node::layerHasModifiedPropAssignments() const {
  if (!isOrInInstance()) {
    return false;
  }
  return getModifiedPropAssignments(self()).size();
}

bool Node::primaryInstanceHasScalingApplied() const {
  REPORT_IF_FALSE_DEBUG_ONLY(isPrimaryInstance());
  return isDifferentEnough(instanceUniformScalingFactor(), 1.0);
}

bool Node::containedPrimaryInstancesHaveInstanceScalingApplied() const {
  bool hasInstanceScaling = false;

  forEachNode(self(), [&hasInstanceScaling](ConstNodePtr node) {
    if (node->isPrimaryInstance() && node->primaryInstanceHasScalingApplied()) {
      hasInstanceScaling = true;
      return Loop::BREAK;
    }

    if (node->isOrInInstance()) {
      // No need to traverse into instance sub-layers, we're only looking for primary instances.
      return Loop::SKIP;
    }

    return Loop::CONTINUE;
  });

  return hasInstanceScaling;
}

// FIXME: why isn't this called from every site where we remove any override???
void Node::createResetEventIfLazilyAddedField(const NodePtr primaryInstance, NodeField field) {
  // When we reset an override (e.g. reset visibility), we expect that a SceneGraphEvent gets
  // generated for that field (e.g. NodeField::VISIBLE). This happens for most fields because
  // when symbol updater gets run after the reset, the fields are copied over from the symbol
  // to the instance.
  //
  // However, for fields like TRANSITION_NODE_ID which don't exist by default on every node,
  // if the symbol doesn't have a TRANSITION_NODE_ID, it won't get copied over to the instance
  // when we reset the symbol overrides and thus, won't generate a SceneGraphEvent for it. Our
  // hack to get around this is to first create an override that sets the field to the default
  // value (equivalent to not having the field) which will create a SceneGraphEvent, and then
  // immediately remove the override.

  NodePtr backingNodeOrSymbol;
  if (self() == primaryInstance) {
    backingNodeOrSymbol = findSymbolBackingInstance();
  } else {
    backingNodeOrSymbol = findLocalNodeOrNull(backingLayerID());
  }

  if (isLazilyPopulatedNodeField(field) && backingNodeOrSymbol &&
      !backingNodeOrSymbol->hasNodeField(field)) {
    set(field, defaultValueForLazilyPopulatedNodeField(field));
  }
}

// Eventually, we want this to return false when we fix:
// https://app.asana.com/0/1136613730629709/1141322336026913
static bool resetOverrideShouldCreateResetEventsForLazilyAddedField() { return true; }

// Visit every primary instance contained under this node's subtree and erase any overrides found.
void Node::resetSymbolOverridesForAllContainedLayers() {
  // go through the node and its sublayers and erase any overrides associated with those layers
  //   this will also erase overrides on instances that are within a symbol.
  bool mayCreateResetEvent = resetOverrideShouldCreateResetEventsForLazilyAddedField();

  NodePtr primaryInstance = findContainingPrimaryInstance();
  if (!primaryInstance) return;

  SymbolOverrider overrider(primaryInstance);
  forEachNode(self(), [&](NodePtr node) {
    const auto& overridePath = node->instanceOverridePath();
    for (const auto& field : overrider.getOverriddenFields(overridePath)) {
      if (mayCreateResetEvent) {
        node->createResetEventIfLazilyAddedField(primaryInstance, field);
      }
    }
    overrider.erasePath(overridePath);
    return Loop::CONTINUE;
  });

  overrider.save();
}

void Node::resetSymbolOverridesForLayer(const Array<NodeField>& resetFields) {
  bool mayCreateResetEvent = resetOverrideShouldCreateResetEventsForLazilyAddedField();

  NodePtr primaryInstance = findContainingPrimaryInstance();
  if (!primaryInstance) return;

  const auto& overridePath = instanceOverridePath();
  SymbolOverrider overrider(primaryInstance);
  for (const auto& field : resetFields) {
    if (mayCreateResetEvent) {
      createResetEventIfLazilyAddedField(primaryInstance, field);
    }
    overrider.erase(overridePath, field);
  }
  overrider.save();
}

void Node::resetOverrideStash() {
  NodePtr containingInstance = findContainingNestedOrPrimaryInstance();
  if (!containingInstance) return;

  // If we are resetting all overrides for a primary instance, we
  // cam completely clear the stash for this node.
  if (containingInstance->isPrimaryInstance()) {
    containingInstance->setOverrideStashInMem(makeShared<const OverrideStash>());
    return;
  }

  // The override stash lives on the primary instance.
  NodePtr containingPrimaryInstance = findContainingPrimaryInstance();
  OverrideStash stash;
  if (auto overrideStash = containingPrimaryInstance->overrideStashInMem()) {
    stash = *overrideStash;
  }
  const OverridePath& path = containingInstance->instanceOverridePath();

  // If we are resetting all overrides on a nested instance, we want to clear the stash
  // only for swaps at that node, which can be identified by the override path to the node.
  stash.resetStashAtPath(path);
  containingPrimaryInstance->setOverrideStashInMem(
      makeShared<const OverrideStash>(std::move(stash)));
}

void Node::resetInstanceScalingForPrimaryInstance() {
  if (isPrimaryInstance() && primaryInstanceHasScalingApplied()) {
    setInstanceUniformScalingFactor(1.0);
  }
}

void Node::resetInstanceScalingForAllContainedPrimaryInstances() {
  forEachNode(self(), [](NodePtr node) {
    if (node->isPrimaryInstance() && node->primaryInstanceHasScalingApplied()) {
      node->setInstanceUniformScalingFactor(1.0);
    }

    if (node->isOrInInstance()) {
      // No need to traverse into instance sub-layers, we're only looking for primary instances.
      return Loop::SKIP;
    }

    return Loop::CONTINUE;
  });
}

void Node::pushAssignmentsToMain() {
  if (!isPrimaryInstance()) {
    sloge("primary instance required", {{"node", uniqueID()}}, {.reportAsSentryError = true});
    return;
  }

  NodePtr backingNode = findSymbolBackingInstance();
  NodePtr backingProductComponent = backingNode->findContainingProductComponent();
  ComponentPropDefsMap propDefs = backingProductComponent->componentPropDefs();
  bool defsChanged = false;

  for (const auto& [assignmentID, propAssignment] : componentPropAssignmentsRawFieldValue()) {
    const ComponentPropDef* existingExplicitDef =
        getExplicitPropDefForSymbol(backingNode, assignmentID);

    if (!existingExplicitDef) {
      sloge("Could not find explicit def corresponding to assignment",
            {{"backingNode", backingNode->uniqueID()},
             {"node", uniqueID()},
             {"assignmentID", assignmentID}});
      continue;
    }

    ComponentPropDef explicitDef = existingExplicitDef->copy();

    if (propDefs.has(explicitDef.propID()) &&
        explicitDef.explicitDef()->value()->asAny__FOR_VARIABLE_PROPS_WORKSTREAM_ONLY() !=
            propAssignment.value()) {
      defsChanged = true;
      if (!validateComponentPropDefDefaultValueEdit(
              explicitDef, propAssignment.value()->asAny__FOR_VARIABLE_PROPS_WORKSTREAM_ONLY())) {
        continue;
      }
      explicitDef.setExplicitDefaultValue(
          propAssignment.value()->asAny__FOR_VARIABLE_PROPS_WORKSTREAM_ONLY());
      propDefs.set(explicitDef.propID(), std::move(explicitDef));
    }
  }
  if (defsChanged) {
    backingProductComponent->setComponentPropDefs(propDefs);
  }
  setComponentPropAssignments({});
}

void Node::pushOverridesToMaster() {
  if (!isPrimaryInstance()) {
    REPORT_ERROR("primary instance required");
    return;
  }

  SymbolOverrider overrider(self());
  for (const auto& [path, fields] : symbolOverrides()) {
    if (!path.isValid()) {
      continue;
    }
    const NodePtr backingNode =
        getBackingNodeForPrimaryInstanceAtOverrideKey(self(), path.peekFront())
            .irresponsiblyCastAwayConst();
    if (!backingNode) {
      slogw("Can't find backing node of primary instance sublayer",
            {.logToConsole = LogToConsole::ALWAYS});
      continue;
    }

    auto shouldPushOverriddenField = [backingNode](NodeField field) {
      if (!fieldShouldRemainAsOverride(field)) {
        // All non-layout overrides besides NAME should be pushed
        return true;
      } else {
        // We don't want to push layout overrides of the instance up to the symbol.
        // If we did that, everyone's symbols would confusingly change. Instead, we
        // remember the layout overrides of this primary instance so we can retain
        // the layout at the very end. The exception to this is stacks and size
        // overrides, which we do want to push.
        if (backingNode->isStack() && field == NodeField::SIZE) {
          return true;
        } else {
          return false;
        }
      }
    };

    if (path == instanceOverridePath()) {
      ///////////////////////////////////////////////////////////////////////////////////
      // These overrides pertain directly to this primary instance. The backing node must
      // thus be the symbol backing this primary instance.
      ///////////////////////////////////////////////////////////////////////////////////
      REPORT_IF_FALSE_DEBUG_ONLY(backingNode->uniqueID() == symbolID());

      for (const auto& [field, value] : fields) {
        if (shouldPushOverriddenField(field)) {
          backingNode->set(field, value);
          overrider.erase(path, field);
        }
      }
    } else if (path.size() == 1 && !backingNode->isPrimaryInstance()) {
      ///////////////////////////////////////////////////////////////////////////////////
      // The backing node is just a normal symbol sublayer.
      ///////////////////////////////////////////////////////////////////////////////////

      for (const auto& [field, value] : fields) {
        if (shouldPushOverriddenField(field)) {
          backingNode->set(field, value);
          overrider.erase(path, field);
        }
      }
    } else if (path.size() == 1 && backingNode->isPrimaryInstance()) {
      ///////////////////////////////////////////////////////////////////////////////////
      // If the path length is 1 and the backing node is a primary instance,
      // we can set the current node's overrides as overrides in the backing node
      // using the backing node's symbol ID as the path to set.
      ///////////////////////////////////////////////////////////////////////////////////

      // Swap the primary instance in the master node before applying any overrides
      bool swapped = false;
      if (const Any* value = fields.getOrNull(NodeField::OVERRIDDEN_SYMBOL_ID)) {
        auto overriddenSymbolID = value->raw<GUID>();
        if (overriddenSymbolID && overriddenSymbolID->isValid()) {
          // Instead of using: replaceSymbolID(backingNode, *overriddenSymbolID);
          // which would also try to retain text overrides, we set the symbol ID via
          // Node::setSymbolForPrimaryInstance() which doesn't do that.
          backingNode->setSymbolForPrimaryInstance(*overriddenSymbolID);
          overrider.erase(path, NodeField::OVERRIDDEN_SYMBOL_ID);
          swapped = true;
        }
      }

      GUID backingNodeSymbolID = backingNode->symbolID();
      if (backingNodeSymbolID.isValid()) {
        SymbolOverrider backingNodeOverrider(backingNode);
        const OverridePath backingNodeSymbolIDAsPath = OverridePath(backingNodeSymbolID);

        if (swapped) {
          // Maintain the original sublayer size in the backing node when swapping
          backingNodeOverrider.set<NodeField::SIZE>(backingNodeSymbolIDAsPath, backingNode->size());
        }

        // Apply overrides to backing node
        for (const auto& [field, value] : fields) {
          if (field == NodeField::COMPONENT_PROP_ASSIGNMENTS) {
            // prop assignments are stored directly on primary instances so we do not want to push
            // as an override
            if (const Optional<ComponentPropAssignmentsMap> assignmentOverrides =
                    value.asOrEmpty<ComponentPropAssignmentsMap>()) {
              backingNode->setComponentPropAssignments(*assignmentOverrides);
              overrider.erase(path, field);
            }

            // erase assignments from instanceSublayer because the field is not populated by default
            NodePtr instanceSublayer =
                getInstanceSublayerByRelativePath(self(), path).irresponsiblyCastAwayConst();
            if (!instanceSublayer) {
              continue;
            }
            instanceSublayer->eraseFieldOrSetDefault(NodeField::COMPONENT_PROP_ASSIGNMENTS);
            continue;
          }
          if (field != NodeField::OVERRIDDEN_SYMBOL_ID && shouldPushOverriddenField(field)) {
            backingNodeOverrider.setAny(backingNodeSymbolIDAsPath, field, Any{value});
            overrider.erase(path, field);
          }
        }

        backingNodeOverrider.save();
      }
    } else {
      ///////////////////////////////////////////////////////////////////////////////////
      // If the path length is > 1, the backing node should be a primary instance (because
      // we got the backing node from just the first GUID in the path!). The path itself
      // refers to an instance sublayer nested deeply within the primary instance.
      ///////////////////////////////////////////////////////////////////////////////////

      REPORT_IF_FALSE(
          backingNode->isPrimaryInstance() &&
          "the first GUID in an instance-override-path w/ size > 1 should be the primary instance");

      SymbolOverrider backingNodeOverrider(backingNode);

      // In order to get the path these overrides apply to inside of the backing instance,
      // we drop the first GUID from the path.
      OverridePath subpath(path.drop(1));

      // Apply overrides to backing node
      for (const auto& [field, value] : fields) {
        // still push assignment overrides but also clear the node field explicitly from instance
        // sublayer because it is not populated by default
        if (field == NodeField::COMPONENT_PROP_ASSIGNMENTS) {
          NodePtr instanceSublayer =
              getInstanceSublayerByRelativePath(self(), path).irresponsiblyCastAwayConst();
          if (instanceSublayer) {
            instanceSublayer->eraseFieldOrSetDefault(NodeField::COMPONENT_PROP_ASSIGNMENTS);
          }
          overrider.erase(path, field);
        }
        if (shouldPushOverriddenField(field)) {
          backingNodeOverrider.setAny(subpath, field, Any{value});
          overrider.erase(path, field);
        }
      }

      backingNodeOverrider.save();
    }
  }
  overrider.save();
}

FieldToAny Node::overridesAtPath(const OverridePath& path) const {
  REPORT_IF_FALSE_DEBUG_ONLY(isPrimaryInstance());
  return symbolOverrides().getOrDefault(path);
}

// This accessor look a little odd because the underlying storage is a pointer which might be null
// but the caller contract always expects a valid const reference. This could be simplified if we
// changed SymbolData to Immutable<> like safe pointer which is never null, but this is expected to
// be set on most objects that require it.
const NodeGenerationData& Node::nodeGenerationData() const {
  Shared<const NodeGenerationData> ptr{fields().nodeGenerationData()};
  // This fields is only useful on immutable frame roots, which are never instance sublayers.

  // Note that this apparently dangling pointer is safe because ptr aliases a shared pointer that's
  // owned by fields() when ptr is non-null. However this is a very fragile pattern to use as it
  // could silently result in memory corruption if we make other seemingly safe code changes (e.g.,
  // to the generated DenseNodeFields code). It would be better to make DenseNodeFields
  // return a reference to a Shared, or have this function return a Shared<NodeGenerationData>.
#pragma clang diagnostic push
#pragma clang diagnostic ignored "-Wdangling"
  return ptr ? *ptr : emptyValue<NodeGenerationData>();
#pragma clang diagnostic pop
}

const NodeGenerationOverrides& Node::nodeGenerationOverrides() const {
  if (!isImmutableFrame()) {
    REPORT_ERROR("nodeGenerationOverrides() called on node that is not an immutable frame");
    return emptyValue<NodeGenerationOverrides>();
  }

  if (auto data = fields().nodeGenerationData()) {
    return data->overrides;
  }

  return emptyValue<NodeGenerationOverrides>();
}

void Node::setNodeGenerationOverrides(NodeGenerationOverrides newOverrides) {
  REPORT_IF_FALSE_DEBUG_ONLY(isImmutableFrame());

  NodeGenerationData copy = nodeGenerationData();
  copy.overrides = std::move(newOverrides);
  replaceNodeGenerationData(std::move(copy));
}

void Node::replaceNodeGenerationData(NodeGenerationData&& data) {
#if BUILD_DEBUG
  if (!areOverridesValid(data, type())) {
    sloge(
        "Setting invalid NodeGenerationData on immutable frame! This may be from an old FigJam "
        "file, such as a hub file on community, whose immutable frames do not yet have derived "
        "data. We may need to perform an update for these nodes.");
  }
#endif
  REPORT_IF_FALSE_DEBUG_ONLY(isImmutableFrame());
  set<NodeField::NODE_GENERATION_DATA>(makeShared<const NodeGenerationData>(std::move(data)));
}

void Node::_updateInInstanceAfterParentChange(bool parentIsOrInInstance) {
  if (parentIsOrInInstance == _cachedIsInstanceSublayer) {
    return;
  }
  _cachedIsInstanceSublayer = parentIsOrInInstance;

  // reuse the parameter to recompute for children
  parentIsOrInInstance = isOrInInstance();
  // use of the _cachedChildren array directly is safe because the recursion is guaranteed
  // not to modify _cachedChildren and invalidate iterators.
  for (const NodePtr& child : _cachedChildren) {
    child->_updateInInstanceAfterParentChange(parentIsOrInInstance);
  }
}

void Node::_updateInModuleAfterParentChange(bool parentIsOrInModule) {
  if (parentIsOrInModule == _cachedIsModuleSublayer) {
    return;
  }
  _cachedIsModuleSublayer = parentIsOrInModule;

  // reuse the parameter to recompute for children
  parentIsOrInModule = isOrInModule();
  for (const NodePtr& child : _cachedChildren) {
    child->_updateInModuleAfterParentChange(parentIsOrInModule);
  }
}

void Node::_updateInSlideAfterParentChange(bool parentIsOrInSlide) {
  if (parentIsOrInSlide == _cachedIsSlideSublayer) {
    return;
  }
  _cachedIsSlideSublayer = parentIsOrInSlide;

  // reuse the parameter to recompute for children
  parentIsOrInSlide = isOrInSlide();
  for (const NodePtr& child : _cachedChildren) {
    child->_updateInSlideAfterParentChange(parentIsOrInSlide);
  }
}

void Node::_updateInSymbolAfterParentChange(bool parentIsOrInSymbol) {
  if (parentIsOrInSymbol == _cachedIsSymbolSublayer) {
    return;
  }
  _cachedIsSymbolSublayer = parentIsOrInSymbol;

  parentIsOrInSymbol = isOrInSymbol();
  for (const NodePtr& child : _cachedChildren) {
    child->_updateInSymbolAfterParentChange(parentIsOrInSymbol);
  }
}

