void Node::prepareInstanceLayer(GUID primaryID, const OverridePath& overridePath,
                                NodeType sourceType, bool isPrimary) {
  if (!isValid()) return;
  REPORT_IF_FALSE_DEBUG_ONLY(overridePath.isValid());

  NodeType targetType = (NodeType::SYMBOL == sourceType) ? NodeType::INSTANCE : sourceType;
  if (type() != targetType) {
    setType(targetType);
  }

  if (!_instanceData) {
    _instanceData = makeOwned<InstanceData>();
  }
  _instanceData->primaryID = primaryID;
  _instanceData->path = overridePath;
  _cachedIsInstanceSublayer = !isPrimary;
}

// We used to identify IF sublayers by a single overrideKey of type GUID that described the
// sublayer's purpose in the IF. For example, all sticky backgrounds would have the same
// overrideKey.
//
// When building tables, we needed to be able to identify cells by their column and row. So we
// expanded this GUID to a GUID Path where the first entry is GUID that used to be the override key,
// and subsequent entries store the row and column of the table.
//
// We also started prefixing the path by the node id of the containing immutable frame. That way,
// sublayers in different IFs have distinct paths. This was necessary to build parenting into IFs;
// so that nodes could identify their (potentially untracked) parent with this guid path.
void Node::prepareImmutableFrameSublayer(StablePath sublayerPath) {
  if (!isValid()) return;
  REPORT_IF_FALSE(sublayerPath.size() >= 2);
  _cachedIsImmutableFrameSublayer = true;
  setImmutableFrameSublayerPath(std::move(sublayerPath));
}

OverridePath Node::immutableFrameOverridePath() const {
  StablePath sublayerPath = immutableFrameSublayerPath();
  if (sublayerPath.size() < 2) {
    // TODO(tables): Perhaps report error here.
    return OverridePath::invalid();
  }
  return immutableFrameSublayerPath().immutableFrameOverridePath();
}

// It is possible to get here before symbol updater has run, for example, something as simple as
// dragging a symbol to make a duplicate instance tries to set name on instance in the following
// call stack:
// - duplicateSelection
//   - uniquifyNodeNamesFromCopy
//     - Node::setName ... observer side effect tries to set symbol override and fails
// This will create a new instance and try to set name on the primary instance before running
// symbol updater. This is not very clean on the part of the model, but this is reality for now.
// In the long term, it would be good to move towards fully dynamically computed paths (such as
// the SceneCursor model) and not have this function.
//
// Another case, when an instance is created by applying data in branching and merging:
// - PHASE change comes in, node is created with type NONE
// - CHANGE for fields come in enum order: name, type, size ...
//   - if SymbolObserver is running, it will query instanceOverridePath()
//     in order to sync overrides, this happens before SymbolUpdater is run.
//
// Returns empty (invalid) path if there is an error getting the path.
const OverridePath& Node::instanceOverridePath() const {
  if (isPrimaryInstance()) {
    updatePrimaryInstanceData();
  }
  return _instanceData ? _instanceData->path : emptyValue<OverridePath>();
}

OverridePath Node::overridePathForInstanceOfSymbol() const {
  if (!isOrInSymbol()) {
    return {};
  }
  if (auto cursor = SceneCursor::fromNode(self(), SceneCursor::TrackAncestorVariableMode::YES,
                                          SceneCursor::SuppressLogging::YES)) {
    return cursor->overridePathForInstanceOfSymbol();
  }
  // Failed to create a cursor.
  return {};
}

StablePathArray Node::stablePath() const {
  if (isInstanceSublayer()) {
    StablePathArray guidsInPath;
    guidsInPath.reserve(1 + instanceOverridePath().size());

    GUID primaryGUID;
    if (!_instanceData) {
      auto primaryInstance = findContainingPrimaryInstance();
      // Guard against Sentry Error "cannot dereference nullptr" in Node::stablePath()
      // https://figma.sentry.io/issues/4115292539/
      // Not clear why if you have a sublayer, how its containing instance can be null.
      if (!primaryInstance) {
        slogw("isInstanceSublayer _instanceData and findContainingPrimaryInstance() are both null",
              {{"nodeId", uniqueID()}});
        return {uniqueID()};
      }
      primaryGUID = primaryInstance->uniqueID();
    } else {
      primaryGUID = _instanceData->primaryID;
    }

    guidsInPath.push_back(primaryGUID);
    for (auto& guid : instanceOverridePath().guids()) {
      guidsInPath.push_back(guid);
    }
    return guidsInPath;
  }
  if (isInImmutableFrame()) {
    return immutableFrameSublayerPath().copyGuids();
  }

  return {uniqueID()};
}

ConstNodePtr Node::findContainingPrimaryInstance() const {
  ConstNodePtr search = nullptr;

  if (isOrInInstance()) {
    for (search = self(); search != nullptr; search = search->parent()) {
      if (search->isPrimaryInstance()) {
        break;  // Break once we reach the PRIMARY INSTANCE
      }
    }
  }

  return search;
}

ConstNodePtr Node::findContainingNestedInstance() const {
  ConstNodePtr search = nullptr;

  if (isOrInInstance()) {
    for (search = self(); search != nullptr; search = search->parent()) {
      if (search->isInstance()) {
        break;  // Break on the closest INSTANCE
      }
    }
  }

  if (!search || search->isPrimaryInstance()) {
    return nullptr;  // Ignore if this is a PRIMARY INSTANCE
  }

  return search;
}

ConstNodePtr Node::findContainingNestedOrPrimaryInstance() const {
  ConstNodePtr search = nullptr;

  if (isOrInInstance()) {
    for (search = self(); search != nullptr; search = search->parent()) {
      if (search->isInstance()) {
        break;  // Break on the closest INSTANCE
      }
    }
  }

  return search;
}

ConstNodePtr Node::findParentInstance() const {
  if (ConstNodePtr parent = self()->parent()) {
    return parent->findContainingNestedOrPrimaryInstance();
  }
  return nullptr;
}

ConstNodePtr Node::findContainingSymbol() const {
  ConstNodePtr search = nullptr;

  if (isOrInSymbol()) {
    // find containing symbol
    for (search = self(); search != nullptr; search = search->parent()) {
      if (search->isSymbol()) {
        break;
      }
    }
  }

  return search;
}

