NodePtr Node::findSymbolBackingInstance() const {
  if (!isInstance()) {
    return nullptr;
  }
  GUID guid = symbolID();
  NodePtr symbol = findLocalNodeOrNull(guid);
  if (!symbol) {
    // We should not pollute the console with warnings here as there is code which expects this
    // function to return false with a dangling instance in the normal course of operation;
    // for example, when Paster calls Node::isSubscribedInstance().
    return nullptr;
  }
  if (!symbol->isSymbol()) {
    slogw("instance backed by non-symbol",
          {{"instance", toString()}, {"symbol", symbol->toString()}});
    return nullptr;
  }
  return symbol;
}

NodePtr Node::findVariableSetForVariable() const {
  if (!isVariable()) {
    return nullptr;
  }
  NodePtr variableSet = _scene->variableSetByIdOrNull(variableSetIdForVariable());
  if (!variableSet || !variableSet->isVariableSet()) {
    sloge("Variable has invalid backing variable set",
          {{"variable", self()->variableId().toString()},
           {"variableSet", variableSetIdForVariable().toString()}},
          {.modeEventName = "variables_broken_invariants", .forwardToDatadog = true});
    return nullptr;
  }
  return variableSet;
}

NodePtr Node::findVariableSetBackingExtension() const {
  if (!isVariableSetExtension()) {
    return nullptr;
  }

  NodePtr backingVariableSet = _scene->variableSetByIdOrNull(backingVariableSetId());
  if (!backingVariableSet) {
    slogw("Variable set extension has no backing variable set",
          {{"variableSetExtension", uniqueID().toString()}});
    return nullptr;
  }

  if (!backingVariableSet->isVariableSet()) {
    slogw("Backing variable set is not a variable set",
          {{"variableSetExtension", self()->uniqueID().toString()}});
    return nullptr;
  }

  return backingVariableSet;
}

ConstNodePtr Node::findRootVariableSetBackingSet() const {
  if (!isVariableSet()) {
    return nullptr;
  }
  // Trivial case: if this is a root variable set, it's its own backing root variable set.
  if (isRootVariableSet()) {
    return self();
  }

  Set<ConstNodePtr> visited = {self()};

  NodePtr backingVariableSet = findVariableSetBackingExtension();
  while (backingVariableSet) {
    if (backingVariableSet->isRootVariableSet()) {
      return backingVariableSet;
    }

    if (!visited.emplace(backingVariableSet).second) {
      // We've seen this extension before, so there's a cycle.
      sloge("Cycle in variable set extensions",
            {{"variableSetExtension", self()->uniqueID().toString()}},
            {.reportAsSentryError = true});
      return nullptr;
    }

    backingVariableSet = backingVariableSet->findVariableSetBackingExtension();
  }

  sloge("Invalid variable set chain", {{"variableSet", uniqueID().toString()}},
        {.reportAsSentryError = true});
  return nullptr;
}

NodePtr Node::findProductComponentBackingInstance() const {
  if (NodePtr backingSymbol = findSymbolBackingInstance()) {
    return backingSymbol->findContainingProductComponent();
  }
  return nullptr;
}

NodePtr Node::findContainingProductComponent() {
  return const_cast<const Node*>(this)
      ->findContainingProductComponent()
      .irresponsiblyCastAwayConst();
}

ConstNodePtr Node::findContainingProductComponent() const {
  if (isStateGroup() || isLooseComponent()) {
    return self();
  }
  auto containingSymbol = findContainingSymbol();
  if (containingSymbol && containingSymbol->isState()) {
    return containingSymbol->findContainingStateGroupOrSelf();
  } else {
    return containingSymbol;
  }
}

ConstNodePtr Node::findContainingModule() const {
  ConstNodePtr search = nullptr;

  if (isOrInModule()) {
    for (search = self(); search != nullptr; search = search->parent()) {
      if (search->isModule()) {
        break;
      }
    }
  }

  return search;
