ConstNodePtr Node::findContainingImmutableFrame() const {
  if (isImmutableFrame()) {
    return self();
  } else if (isInImmutableFrame()) {
    if (immutableFrameSublayerPath().size() == 0) {
      sloge("empty immutable frame sublayer path", {{"sublayer", uniqueID()}},
            {.reportAsSentryError = true});
      return nullptr;
    }

    GUID immutableFrameId = immutableFrameSublayerPath()[0];
    return _scene->nodeByIdOrNull(immutableFrameId);
  } else {
    return nullptr;
  }
}

