ConstNodePtr Node::findContainingConnector() const {
  ConstNodePtr search = nullptr;

  if (isOrInImmutableFrame()) {
    for (search = self(); search != nullptr; search = search->parent()) {
      if (search->type() == NodeType::CONNECTOR) {
        return search;
      } else if (search->isImmutableFrame()) {
        // Immutable frames cannot be nested in other immutable frames, so if a non-connector
        // immutable frame is reached we know that there is no containing connector.
        return nullptr;
      }
    }
  }

  return nullptr;
}

NodePtr Node::findContainingConnector() {
  return const_cast<const Node*>(this)->findContainingConnector().irresponsiblyCastAwayConst();
}

Array<NodePtr> Node::getSimplifiedChildren(TraversalOrder traversalOrder) {
  Array<NodePtr> descendants;

  forEachNode(
      self(),
      [&](NodePtr node) {
        if (node == self()) {
          return Loop::CONTINUE;
        } else if (node->isVisibleWhenSimplified()) {
          descendants.push_back(node);
          return Loop::SKIP;
        } else if (node->isInstance() && !node->propsAreBubbled()) {
          // Skip the entire subtree of non-bubbled instances
          return Loop::SKIP;
        } else {
          return Loop::CONTINUE;
        }
      },
      traversalOrder);

  return descendants;
}

bool Node::hasChildrenWithPropRefs() const {
  if (!isProductComponent() && !isSymbol() && !isInstance()) {
    return false;
  }

  ConstNodePtr nodeWithDefs =
      isSymbol() ? findContainingProductComponent() : findProductComponentBackingInstance();
  if (!nodeWithDefs) {
    return false;
  }

  auto model = ComponentPropModel(nodeWithDefs.irresponsiblyCastAwayConst());
  ComponentPropDefsMap defs = nodeWithDefs->componentPropDefs();

  for (const auto& [defID, def] : defs) {
    auto error = model.getPropDefError(defID);
    if (error != ComponentPropDefError::UNUSED_DEF_ERROR) {
      // Has at least one used def
      return true;
    }
  }

  return false;
}

// Should be kept in sync with SceneNodeCpp::setPropsAreBubbled so we can return useful error
// messages to the plugin API
bool Node::canTogglePropsAreBubbled() const {
  if (!isPrimaryInstance() ||
      (!isOrInSymbol() && !(isOrInModule() && FF::dse_editor_templates_v1()))) {
    return false;
  }

  NodePtr backingSymbol = findSymbolBackingInstance();
  if (!backingSymbol) {
    return false;
  }

  if (const ComponentPropRef* instanceRef = getComponentPropRef(
          NodeField::OVERRIDDEN_SYMBOL_ID, ComponentPropRefGetMode::MUST_BE_LINKED_TO_VALID_DEF)) {
    return true;
  }

  return hasBubbledInstanceDescendant() || propsAreBubbled() || hasChildrenWithPropRefs() ||
         backingSymbol->isState();
}

void Node::lockDiagramLayout() {
  if (isDiagramNode()) {
