}

NodePtr Node::findBestAncestorInRestorePath() const {
  // Find the bottom-most still-visible ancestor at time of soft deletion. The
  // ancestors are stored top-to-bottom, so we should traverse in reverse.
  auto ancestorPath = ancestorPathBeforeDeletion();
  for (int i = ancestorPath.size() - 1; i >= 0; i--) {
    const GUID& ancestorID = ancestorPath[i];
    NodePtr ancestor = _scene->nodeByIdOrNull(ancestorID);
    if (!ancestor) continue;
    // This should never happen because we stop ancestorPathBeforeDeletion at
    // the canvas level, but this guards against unsanitary / arbitrary paths.
    if (ancestor->type() == NodeType::DOCUMENT) {
      return nullptr;
    }
    if (!ancestor->isInternalOnlyNode()) {
      return ancestor;
    }
  }

  // If no visible ancestor is found, we return nullptr and it is up to the
  // caller to find a suitable default.
  return nullptr;
}

