// Returns true for any node that has a hidden canvas
// as an ancestor.
bool Node::isSoftDeleted() const {
  // Check for the isSoftDeletedStyle property first. While styles should in theory always
  // be in the internal canvas, there's some files where it's strangely not the case, so
  // we can't return false just because a node isn't in the internal canvas.
  // https://app.asana.com/0/1195912481763937/1196708415730057
  //
  // There's no meaningful concept of a "soft deleted shared style". However, we haven't
  // always correctly guaranteed that we clear `isSoftDeletedStyle` when turning a local
  // style into a shared style.
  if (isSoftDeletedStyle() && !isSubscribedStyle()) {
    return true;
  }

  if (isSoftDeletedVariableAsset() && (isLocalVariable() || isLocalVariableSet())) {
    return true;
  }

  if (isSoftDeletedVariableOverride() && isLocalVariableOverride()) {
    return true;
  }

  ConstNodePtr page = findContainingCanvas();
  if (!page) {
    return false;
  }

  if (!page->isInternalOnlyCanvas()) {
    return false;
  }

  auto internalOnlyType = SceneGraph::getInternalOnlyType(self());
  return internalOnlyType == SceneGraph::InternalOnlyType::SOFT_DELETED_SYMBOL ||
         internalOnlyType == SceneGraph::InternalOnlyType::SOFT_DELETED_STYLE ||
         internalOnlyType == SceneGraph::InternalOnlyType::SOFT_DELETED_STATE_GROUP ||
         internalOnlyType == SceneGraph::InternalOnlyType::SOFT_DELETED_VARIABLE ||
         internalOnlyType == SceneGraph::InternalOnlyType::SOFT_DELETED_VARIABLE_SET;
}

void Node::updateSharedSymbolVersion(UpdateUserFacingVersion updateUserFacingVersion) {
  if (!isSymbol()) {
    sloge("Not a symbol", {{"nodeID", uniqueID()}}, {.reportAsSentryError = true});
    return;
  }
  // We shouldn't ever be updating the shared symbol version of a subscribed
  // symbol since the version is managed by the publishing document.
  if (isSubscribedSymbol()) {
    sloge("Cannot update shared symbol version of subscribed symbol", {{"nodeID", uniqueID()}},
          {.reportAsSentryError = true});
    return;
  }
  const auto& newVersion = ImmutableString(scene()->createUniqueSymbolVersion().toString());
  set(NodeField::SHARED_SYMBOL_VERSION, newVersion);
  if (updateUserFacingVersion == UpdateUserFacingVersion::YES) {
    set(NodeField::USER_FACING_VERSION, newVersion);
  }
}

void Node::updateSharedStateGroupVersion(UpdateUserFacingVersion updateUserFacingVersion) {
  if (!isStateGroup()) {
    sloge("Not a state group", {{"nodeID", uniqueID()}}, {.reportAsSentryError = true});
    return;
  }
  // We shouldn't ever be updating the version of a subscribed state group since
  // the version is managed by the publishing document.
  if (isSubscribedStateGroup()) {
    sloge("Cannot update shared state group version of subscribed state group",
          {{"nodeID", uniqueID()}}, {.reportAsSentryError = true});
    return;
  }
  const auto& newVersion = ImmutableString(scene()->createUniqueSymbolVersion().toString());
  set(NodeField::VERSION, newVersion);
  if (updateUserFacingVersion == UpdateUserFacingVersion::YES) {
    set(NodeField::USER_FACING_VERSION, newVersion);
  }
}

void Node::updateVariableVersion(UpdateUserFacingVersion updateUserFacingVersion) {
  if (!assertAssetType(AssetType::VARIABLE)) {
    return;
  }
  // We shouldn't ever be updating the version of a subscribed variable since
  // the version is managed by the publishing document.
  if (isSubscribedVariable()) {
    sloge("Cannot update shared variable version of subscribed variable", {{"nodeID", uniqueID()}},
          {.reportAsSentryError = true});
    return;
  }
  updateVersion(updateUserFacingVersion);
}

