ImmutableString generateAssetKeyString(const SceneGraph* scene, GUID guid, AssetType type) {
  if (!scene) {
    REPORT_ERROR("scene required for asset key");
    return {};
  }
  FileKey fileKey = scene->fileKeyForLibraryPublish();
  if (!fileKey.isValid()) {
    // If you run into this assertion during tests, you probably need to use
    // SceneTestContext API which automatically generates a file key for the scene
    // instead of creating AppModel/AppState/SceneGraph manually.
    Json::Value value;
    value.set("node_id", guid.toString());
    value.set("asset_type", Json::Int(type));
    Analytics::track("Invalid File Key at Publish", value);
    return {};
  }
  return generateAssetKey(fileKey, guid);
}

