bool Node::isSubscribedInstance() const {
  REPORT_IF_FALSE_DEBUG_ONLY(isPrimaryInstance());
  ConstNodePtr symbol = findSymbolBackingInstance();
  if (!symbol) return false;

  if (symbol->isState()) {
    auto stateGroup = symbol->findContainingStateGroupOrSelf();
    if (!stateGroup) return false;

    return stateGroup->isSubscribedStateGroup();
  } else {
    return symbol->isSubscribedSymbol();
  }
}

bool Node::isInternalSubscribedInstance() const {
  if (!isPrimaryInstance() || !isSubscribedInstance()) {
    return false;
  }
  if (auto symbol = findContainingSymbol()) {
    // If instance is a subscribed primary instance (checked in the previous
    // statement) and its containing symbol is also a subscribed symbol,
    // then node is an internal subscribed instance.
    return symbol->isSubscribedSymbol();
  }
  return false;
}

bool Node::isSubscribedStateGroup() const {
  if (!isStateGroup()) {
    return false;
  }
  if (!isInternalOnlyNode()) {
    return false;
  }

  // A subscribed state group has a valid state group key and unpopulated published
  // version field. We do this to match the behavior for symbols in isSubscribedSymbol.
  //
  // We add a publishedFile check since moved, local state groups that have been soft-deleted
  // and unpublished will also have a valid state group key and an empty publish version. To
  // distinguish between a soft-deleted moved state group and a subscribed state group, we must do
  // an additional check on publishFile, which is only populated on subscribed state groups.
  if (FF::ds_publish_file_deprecation()) {
    return hasValidStateGroupKey() && !hasBeenPublished() && publishID().isValid();
  } else {
    return hasValidStateGroupKey() && !hasBeenPublished() && publishFile_DEPRECATED().isValid();
  }
}

bool Node::isSubscribedSymbol() const {
  if (!isSymbol()) {
    sloge_debugOnly_previously_loge("should not have queried isSubscribedSymbol on non-symbol");
    return false;
  }
  // Subscribed symbol has valid component key, have unpopulated published version.
  // This is to rule out certain published symbols copied onto clipboard scene during
  // copy paste, which contains both valid component key and published version, which
  // shouldn't be considered as a subscribed symbol (but rather a local symbol that
  // be easily converted to a subscribed symbol).
  //
  // We add a publishedFile check since moved, local symbols that have been soft-deleted
  // and unpublished will also have a valid component key and an empty publish version. To
  // distinguish between a soft-deleted moved symbol and a subscribed symbol, we must do
  // an additional check on publishFile, which is only populated on subscribed symbols.
  if (FF::ds_publish_file_deprecation()) {
    return hasValidComponentKey() && !hasBeenPublished() && publishID().isValid();
  } else {
    return hasValidComponentKey() && !hasBeenPublished() && publishFile_DEPRECATED().isValid();
  }
}

bool Node::isSubscribedSymbolOrStateGroup() const {
  return (isSymbol() && isSubscribedSymbol()) || isSubscribedStateGroup();
}

bool Node::isSubscribedAsset() const {
  return isSubscribedSymbolOrStateGroup() || isSubscribedStyle() || isSubscribedVariable() ||
         isSubscribedVariableSet() || isSubscribedVariableOverride() || isSubscribedModule();
}

bool Node::isLocalAsset() const { return assetType() != AssetType::NONE && !isSubscribedAsset(); }

bool Node::hasInstances() const {
  REPORT_IF_FALSE_DEBUG_ONLY(isSymbol());

  bool found = false;
  forEachNode(self(), [&found](ConstNodePtr node) {
    if (node->isInstance()) {
      found = true;
      return Loop::BREAK;
    }
    return Loop::CONTINUE;
  });
  return found;
}

static void logInvalidAssetError(const Node& node, AssetType assetType) {
  sloge(
      format("Invalid %s method call on a %s", Figma::toString(assetType), node.typeName()).c_str(),
      {.reportAsSentryError = true});
}

AssetType Node::assetType() const {
  if (isSymbol()) return AssetType::SYMBOL;
  if (isStateGroup()) return AssetType::STATE_GROUP;
  if (isStyle()) return AssetType::STYLE;
  if (isVariable()) return AssetType::VARIABLE;
  if (isVariableSet()) return AssetType::VARIABLE_SET;
  if (isVariableOverride()) return AssetType::VARIABLE_OVERRIDE;
  if (isModule()) return AssetType::MODULE;

  return AssetType::NONE;
}

bool Node::assertAssetType(AssetType expectedAssetType) const {
  if (assetType() == expectedAssetType) return true;
  logInvalidAssetError(*this, expectedAssetType);
  return false;
}

