Shared<const MultiplayerMapVariableFieldVariableData> Node::variableConsumptionMap() const {
  return _fields.variableConsumptionMap();
}

bool Node::setVariableConsumptionMap(
    Shared<const MultiplayerMapVariableFieldVariableData> consumptionMap) {
  bool shouldClearNulloptValues = false;
  if (consumptionMap) {
    // Verify that we never try to set any values to `nullopt`. This value is only
    // used internally by `MultiplayerMap` to sync deletions of keys.
    for (const auto& [_, value] : consumptionMap->map()) {
      if (!value.has_value()) {
        REPORT_ERROR("Attempt to set a `nullopt` value on VariableConsumptionMap");
        shouldClearNulloptValues = true;
        // We've found one nullopt entry already, don't call REPORT_ERROR multiple times and
        // continue to the repair below
        break;
      }
    }
    if (shouldClearNulloptValues) {
      MultiplayerMapVariableFieldVariableData mapWithNulloptsRemoved =
          consumptionMap.get()->map().copy();
      for (const auto& [key, value] : consumptionMap->map()) {
        if (!value.has_value()) {
          mapWithNulloptsRemoved.remove(key);
        }
      }
      consumptionMap = makeShared(std::move(mapWithNulloptsRemoved));
    }

    // Verify that we are not setting variant bindings to instances inside other instances or
    // symbols
    if (!FF::variables_nested_variant_props() &&
        consumptionMap->has(VariableField::VARIANT_PROPERTIES) &&
        (isInstance() && (isNestedInstance() || isOrInSymbol()))) {
      MultiplayerMapVariableFieldVariableData cleanedMap = consumptionMap.get()->map().copy();
      cleanedMap.remove(VariableField::VARIANT_PROPERTIES);
      consumptionMap = makeShared(std::move(cleanedMap));
    }
  }
  return set(NodeField::VARIABLE_CONSUMPTION_MAP, std::move(consumptionMap));
}

