bool Node::setPageLevelDefaultMode(VariableSetKey key, ModeId modeId,
                                   bool requireVariableSetIsLoaded) {
  if (type() != NodeType::CANVAS) {
    sloge("Cannot set page level default mode on a non-canvas node", {{"nodeId", uniqueID()}},
          {.reportAsSentryError = true});
    return false;
  }
  if (isInternalOnlyCanvas()) {
    sloge("Cannot set page level default mode on an internal canvas node", {{"nodeId", uniqueID()}},
          {.reportAsSentryError = true});
    return false;
  }
  MutableModeConsumptionData mcd = mutableModeConsumptionData();
  return mcd.setPageLevelExplicitModeForVariableSetKey(key, std::move(modeId),
                                                       requireVariableSetIsLoaded);
}

// TODO (taquino): deprecate uses of this function in favor of functions on `ModeConsumptionData`
bool Node::setRawVariableModeBySetMap(
    Shared<const MultiplayerMapVariableSetIDGUID> variableModeBySetMap) {
  // Verify that we never try to set any values to `nullopt`. This value is only
  // used internally by `MultiplayerMap` to sync deletions of keys.
  if (variableModeBySetMap) {
    for (const auto& [_, value] : variableModeBySetMap->map()) {
      if (!value.has_value()) {
        REPORT_ERROR("Attempt to set a `nullopt` value on variableModeBySetMap");
        return false;
      }
    }
  }
  return set(NodeField::VARIABLE_MODE_BY_SET_MAP, std::move(variableModeBySetMap));
}

// TODO (taquino): deprecate uses of this function in favor of functions on `ModeConsumptionData`
void Node::setVariableConsumerModeForSet(VariableSetID variableSetID, GUID modeID) {
  auto variableSet = _scene->variableSetByIdOrNull(variableSetID);
  if (!variableSet) {
    sloge("Could not find variable set", {{"variableSetID", variableSetID.toString()}},
          {.reportAsSentryError = true});
    return;
  }
  auto modeData = mutableModeConsumptionData();
  auto varSetKey = variableSet->variableSetKeyForPublish();
  bool success =
      modeData.setExplicitModeForRootVariableSetKey(varSetKey, ModeId(varSetKey, modeID));
  // TODO (taquino): success is false if a VariableSetId can't be determined for the map (i.e. has
  // no existing value and isn't consumed in subtree). We still set the value for now to maintain
  // test functionality.
  if (!success) {
    auto oldMap = variableModeBySetMap__UNSAFE();
    MultiplayerMapVariableSetIDGUID newMap =
        oldMap ? MultiplayerMapVariableSetIDGUID(oldMap->map().copy())
               : MultiplayerMapVariableSetIDGUID();
    newMap.set(variableSetID, modeID);

    setRawVariableModeBySetMap(
        makeShared<const MultiplayerMapVariableSetIDGUID>(std::move(newMap)));
  }
}

// TODO (taquino): deprecate uses of this function in favor of functions on `ModeConsumptionData`
void Node::clearVariableConsumerModeForSet(VariableSetID variableSetID) {
  auto oldMap = variableModeBySetMap__UNSAFE();
  if (!oldMap || !oldMap->has(variableSetID)) {
    return;
  }

  MultiplayerMapVariableSetIDGUID newMap(oldMap->map().copy());
  newMap.remove(variableSetID);

  // Remove the entry with the variableSetKey + invalid version
  auto variableSet = _scene->variableSetByIdOrNull(variableSetID);
  if (!variableSet) {
    sloge("Could not find variable set", {{"variableSetID", variableSetID.toString()}},
          {.reportAsSentryError = true});
  } else {
    VariableSetKey variableSetKey = variableSet->variableSetKeyForPublish();
    VariableSetID variableSetKeyEntry = VariableSetID::fromKeyWithDummyVersion(variableSetKey);
    newMap.remove(variableSetKeyEntry);
  }

  setRawVariableModeBySetMap(makeShared<const MultiplayerMapVariableSetIDGUID>(std::move(newMap)));
}

bool Node::isSoftDeletedVariableAsset() const {
  if (!isVariable() && !isVariableSet()) {
    return false;
  }

  if (fields().isSoftDeleted()) {
    return true;
  }

  // Variables inside a soft-deleted variable set should also be considered soft-deleted
  if (isVariable()) {
    if (ConstNodePtr variableSet = findVariableSetForVariable()) {
      if (variableSet->isSoftDeletedVariableAsset()) {
        return true;
      }
    } else {
      sloge("Can't find backing variable set during soft-deleted check",
            {{"nodeID", uniqueID().toString()}, {"variableID", self()->variableId().toString()}},
            {.reportAsSentryError = true});
    }
  }

  return false;
}

bool Node::isSoftDeletedVariableOverride() const {
  if (!isVariableOverride()) {
    return false;
  }

  // An override can only be soft-deleted by its containing EC being soft-deleted.
  // Otherwise, we expect variable overrides to be hard-deleted.
  if (ConstNodePtr backingVariableSet =
          scene()->variableSetByIdOrNull(variableSetIdForVariableOverride())) {
    return backingVariableSet->isSoftDeleted();
  } else {
    sloge("Unable to find backing variable set for variable override",
          {{"node", self()->toDebugString()}}, {.reportAsSentryError = true});
    return false;
  }
}

Array<VariableSetMode> Node::variableSetModesSorted() const {
  if (!assertAssetType(AssetType::VARIABLE_SET)) {
    return {};
  }
  Array<VariableSetMode> modes;
  for (const auto& [_, mode] : variableSetModes()->map()) {
    if (mode) {
      modes.emplace_back(mode->copy());
    }
  }
  VariableSetHelpers::sortVariableSetModes(modes);
  return modes;
}

void Node::setVariableConsumption(VariableField field, Immutable<VariableData> variableData) {
  if (!variableData->isAlias() && !variableData->isExpression() && !variableData->isFontStyle() &&
      !variableData->isVariableMap()) {
    sloge(
        "Variable data values in the consumption map must be an alias, expression, font style, or "
        "map",
        {{"nodeID", uniqueID()},
         {"field", Figma::toString(field)},
         {"variableData", variableData->toString()}},
        {.reportAsSentryError = true});
    return;
  }

  if (!validateVariableDataForField(field, variableData)) {
    sloge("Cannot set variable consumption on field",
          {{"field", Figma::toString(field)}, {"variableData", variableData->toString()}},
          {.reportAsSentryError = true});
    return;
  }

  if (!validateVariableFieldForStyleType(field, styleType())) {
    sloge("Cannot set variable consumption on style node",
          {
              {"field", Figma::toString(field)},
              {"styleType", Figma::toString(styleType())},
          },
          {.reportAsSentryError = true});
  }

  if (isInstanceSublayer()) {
    if (auto nodeField = nodeFieldForVariableField(field)) {
      if (!fieldIsStoredInOverrides(*nodeField, false)) {
        sloge("Cannot set variable consumption on instance sublayer field",
              {{"field", Figma::toString(field)}, {"variableData", variableData->toString()}},
              {.reportAsSentryError = true});
        return;
      }
    }
  }

  static Set<NodeField> cornerRadiiFields = {NodeField::RECTANGLE_TOP_LEFT_CORNER_RADIUS,
                                             NodeField::RECTANGLE_BOTTOM_LEFT_CORNER_RADIUS,
                                             NodeField::RECTANGLE_BOTTOM_RIGHT_CORNER_RADIUS,
                                             NodeField::RECTANGLE_TOP_RIGHT_CORNER_RADIUS};
  static Set<NodeField> borderWeightFields = {
      NodeField::BORDER_TOP_WEIGHT, NodeField::BORDER_BOTTOM_WEIGHT, NodeField::BORDER_LEFT_WEIGHT,
      NodeField::BORDER_RIGHT_WEIGHT};

  if (Optional<NodeField> nodeField = nodeFieldForVariableField(field)) {
    if (nodeField == NodeField::SIZE && isPrimaryInstance()) {
      // do not automatically detach width and height variables, as we're
      // about to overwrite them below anyway. This prevents autodetatching
      // the variables we apply further down, since the autodetatching logic
      // runs asynchronously.
      VariableObserver::DoNotAutoDetachBindings doNotDetachScope{scene()->variableObserver()};
      setInstanceUniformScalingFactor(1.0);
    }
    if (cornerRadiiFields.has(*nodeField)) {
      setCornerRadius(0);  // if an individual corner radius is bound to a variable, the general
                           // field should be unset
      setRectangleCornerRadiiIndependent(true);
    } else if (borderWeightFields.has(*nodeField)) {
      setBorderStrokeWeightsIndependent(true);
    }
  }

  auto oldConsumptionMap = variableConsumptionMap();
  MultiplayerMapVariableFieldVariableData newConsumptionMap =
      oldConsumptionMap ? MultiplayerMapVariableFieldVariableData(oldConsumptionMap->map().copy())
                        : MultiplayerMapVariableFieldVariableData();

  newConsumptionMap.set(field, variableData);
  setVariableConsumptionMap(
      makeShared<const MultiplayerMapVariableFieldVariableData>(std::move(newConsumptionMap)));
}

bool Node::clearVariableConsumption(VariableField field) {
  auto oldConsumptionMap = variableConsumptionMap();
  if (!oldConsumptionMap || !oldConsumptionMap->has(field)) {
    return false;
  }

  MultiplayerMapVariableFieldVariableData newConsumptionMap(oldConsumptionMap->map().copy());

  newConsumptionMap.remove(field);
  return setVariableConsumptionMap(
      makeShared<const MultiplayerMapVariableFieldVariableData>(std::move(newConsumptionMap)));
}

bool Node::clearNestedVariableConsumption(VariableField field, ImmutableString key) {
  auto oldConsumptionMap = variableConsumptionMap();
  if (!oldConsumptionMap || !oldConsumptionMap->has(field)) {
    return false;
  }

  Immutable<VariableData> consumptionMapEntry = *oldConsumptionMap->map().getOrDefault(field);
  if (!consumptionMapEntry->isVariableMap()) {
    return false;
  }

  VariableMap oldVariableMap = *consumptionMapEntry->asVariableMap();
  if (!oldVariableMap.values->has(key.str())) {
    return false;
  }

  VariableMapData newValues = oldVariableMap.values->copy();
  newValues.erase(key.str());
  VariableMap newVariableMap(std::move(newValues));

  MultiplayerMapVariableFieldVariableData newConsumptionMap(oldConsumptionMap->map().copy());
  newConsumptionMap.set(field, Immutable(VariableData(newVariableMap)));
  return setVariableConsumptionMap(
      makeShared<const MultiplayerMapVariableFieldVariableData>(std::move(newConsumptionMap)));
}

Shared<const MultiplayerMapGUIDVariableSetMode> Node::variableSetModes() const {
  if (!assertAssetType(AssetType::VARIABLE_SET)) {
    return nullptr;
  }
  if (isVariableSetExtension()) {
    if (ConstNodePtr backingSet = scene()->variableSetByIdOrNull(backingVariableSetId())) {
      return backingSet->variableSetModes();  // these should always match, all the way down to the
                                              // root variable set
    } else {
      sloge("Could not find backing variable set for set extension",
            {{"variableSetId", variableSetId().toString()},
             {"backingVariableSetId", backingVariableSetId().toString()}});
      return nullptr;
    }
  }
  Shared<const MultiplayerMapGUIDVariableSetMode> result = _fields.variableSetModes();
  if (!result || result->size() == 0) {
    sloge("Variable set has no modes", {{"variableSet", self()->variableSetId().toString()}},
          {.modeEventName = "variables_broken_invariants", .forwardToDatadog = true});
  }
  return _fields.variableSetModes();
};

Optional<VariableSetMode> Node::variableSetDefaultMode() const {
  if (!assertAssetType(AssetType::VARIABLE_SET)) {
    return nullopt;
  }
  if (!variableSetModes()) {
    REPORT_ERROR("Variable set does not have any modes defined");
    return nullopt;
  }
  return VariableSetHelpers::getVariableSetDefaultMode(*variableSetModes());
}

VariableDataSetResult Node::setVariableDataForMode(const GUID& modeID,
                                                   Immutable<VariableData> variableData) {
  if (!isVariable() && !isVariableOverride()) {
    REPORT_ERROR("Can only set variableData on a variable node");
    return VariableDataSetResult::InvalidArgument;
  }
  if (!variableDataValues()) {
    REPORT_ERROR(
        "Cannot set variableData on variable or variable override with no mode values defined");
    return VariableDataSetResult::InvalidArgument;
  }
  if (variableData->isFontStyle()) {
    REPORT_ERROR("Cannot set fontStyle data directly on a variable");
    return VariableDataSetResult::InvalidArgument;
  }

  NodePtr backingVariable =
      isVariableOverride() ? scene()->variableByIdOrNull(
                                 VariableSetExtensionHelpers::getRootVariableIdForOverride(self()))
                           : self();
  if (!backingVariable) {
    sloge("Invalid backing variable or self for setVariableDataMode", {},
          {.reportAsSentryError = true});
    return VariableDataSetResult::InvalidArgument;
  }

  auto variableResolvedType = backingVariable->variableResolvedType();
  if (variableData->resolvedType() != variableResolvedType) {
    REPORT_ERROR("Variable data has mismatching type from variable");
    return VariableDataSetResult::InvalidArgument;
  }

  auto variableDepTracker = _scene->variableDependencyTracker();
  if (!variableDepTracker) {
    REPORT_ERROR("No variable dependency tracker for scene");
    return VariableDataSetResult::InvalidArgument;
  }

  if (variableData->isAlias() || variableData->isExpression()) {
    VariableID variableID = backingVariable->variableId();
    if (variableDepTracker->wouldCreateSelfReference(variableID, variableData)) {
      return VariableDataSetResult::SelfReference;
    }
    if (variableDepTracker->wouldCreateCycle(variableID, variableData)) {
      return VariableDataSetResult::Cycle;
    }
  }

  auto newModeToVarData = variableDataValues()->map().copy();
  newModeToVarData.set(modeID, std::move(variableData));
  setVariableDataValues(
      makeShared<const MultiplayerMapGUIDVariableData>(std::move(newModeToVarData)));

  return VariableDataSetResult::Success;
}

void Node::duplicateVariableDataForMode(const GUID& sourceModeID, const GUID& destinationModeID) {
  if (!assertAssetType(AssetType::VARIABLE)) {
    return;
  }
  if (!variableDataValues()) {
    REPORT_ERROR("Cannot set variableData on variable with no mode values defined");
    return;
  }

  const auto oldData = variableDataValues()->map().getOrDefault(sourceModeID);
  if (oldData) {
    setVariableDataForMode(destinationModeID, *oldData);
  }
}

void Node::removeVariableDataForMode(const GUID& modeID) {
  if (!assertAssetType(AssetType::VARIABLE)) {
    return;
  }
  if (!variableDataValues()) {
    REPORT_ERROR("Cannot remove mode from variable with no mode values defined");
    return;
  }

  auto newModeToVarData = variableDataValues()->map().copy();
  if (newModeToVarData.erase(modeID)) {
    setVariableDataValues(
        makeShared<const MultiplayerMapGUIDVariableData>(std::move(newModeToVarData)));
  }
}

bool Node::setFileAssetIdForKey(AssetKey assetKey, AssetIdentifier assetId,
                                bool updateIfAlreadySet) {
  if (type() != NodeType::DOCUMENT) {
    sloge("Attempting to set the file asset id on a non-document node", {{"nodeId", uniqueID()}},
          {.reportAsSentryError = true});
    return false;
  }

  if (!fileAssetIds()) {
    Shared<const MultiplayerMapAssetKeyAssetId> fileAssetIds =
        makeShared<const MultiplayerMapAssetKeyAssetId>();
    setFileAssetIds(std::move(fileAssetIds));
  } else if (!updateIfAlreadySet && fileAssetIds()->map().has(assetKey)) {
    // This is acceptable; we want the first write to win.
    return false;
  }

  auto newFileAssetRefMap = fileAssetIds()->map().copy();
  newFileAssetRefMap.set(assetKey, assetId.copy());
  return setFileAssetIds(
      makeShared<const MultiplayerMapAssetKeyAssetId>(std::move(newFileAssetRefMap)));
}

void Node::clearFileAssetIdForKey(AssetKey assetKey) {
  if (type() != NodeType::DOCUMENT) {
    sloge("Attempting to clear the file asset id on a non-document node", {{"nodeId", uniqueID()}},
          {.reportAsSentryError = true});
    return;
  }

  if (!fileAssetIds() || !fileAssetIds()->map().has(assetKey)) {
    return;
  }

  auto newFileAssetRefMap = fileAssetIds()->map().copy();
  newFileAssetRefMap.erase(assetKey);
  setFileAssetIds(makeShared<const MultiplayerMapAssetKeyAssetId>(std::move(newFileAssetRefMap)));
}

bool Node::setVariableSetModes(Shared<const MultiplayerMapGUIDVariableSetMode> variableSetModes) {
  // Verify that we never try to set any values to `nullopt`. This value is only
  // used internally by `MultiplayerMap` to sync deletions of keys.
  if (isVariableSetExtension()) {
    sloge("Attempted to set variable set modes on a variable set extension",
          {{"varSetId", variableSetId().toString()}});
    return false;
  }
  if (variableSetModes) {
    for (const auto& [_, value] : variableSetModes->map()) {
      if (!value.has_value()) {
        REPORT_ERROR("Attempt to set a `nullopt` value on VariableSetModes");
        return false;
      }
    }
  }
  return set(NodeField::VARIABLE_SET_MODES, std::move(variableSetModes));
}

GUID Node::addVariableSetMode(String name) {
  if (!assertAssetType(AssetType::VARIABLE_SET)) {
    return GUID::invalid();
  }

  const auto& modes = variableSetModesSorted();
  if (modes.size() < 1) {
    REPORT_ERROR("Could not find mode to duplicate");
    return GUID::invalid();
  }
  const auto& lastMode = modes[modes.size() - 1];
  return duplicateVariableSetMode(lastMode.modeID(), name);
}

GUID Node::duplicateVariableSetMode(GUID sourceModeID, Optional<String> name) {
  if (!assertAssetType(AssetType::VARIABLE_SET)) {
    return GUID::invalid();
  }

  MultiplayerMapGUIDVariableSetMode newModes(variableSetModes()->map().copy());
  if (!newModes.has(sourceModeID)) {
    REPORT_ERROR("Cannot find mode to duplicate");
    return GUID::invalid();
  }
  // Add the new mode to the variable set.
  GUID newModeID = _scene->createUniqueVariableModeId();
  ImmutableString newModeName =
      name ? ImmutableString(*name) : newModes.map().getOrDefault(sourceModeID)->name();
  Position newPosition = VariableSetHelpers::getNextPositionForMode(newModes);
  newModes.set(newModeID, VariableSetMode(newModeID, newModeName, newPosition));

  VariableSetHelpers::filterNulloptModes(newModes);
  setVariableSetModes(makeShared<const MultiplayerMapGUIDVariableSetMode>(std::move(newModes)));

  // On all variables in the variable set, populate their variableData under the new mode with the
  // default variableData.
  VariableSetID variableSetID = self()->variableSetId();
  for (const VariableID& variableID :
       _scene->variableDependencyTracker()->getVariablesInSet(variableSetID)) {
    NodePtr variable = _scene->variableByIdOrNull(variableID);
    if (!variable) {
      REPORT_ERROR("Variable belonging to variable set not found");
      continue;
    }
    variable->duplicateVariableDataForMode(sourceModeID, newModeID);
  }

  return newModeID;
}

bool Node::setVariableSetModeAsDefault(GUID modeID) { return reorderVariableSetMode(modeID, 0); }

bool Node::reorderVariableSetMode(GUID modeID, size_t newIndex) {
  if (!assertAssetType(AssetType::VARIABLE_SET)) {
    return false;
  }
  auto modes = variableSetModes();
  if (!modes) return false;

  const Optional<VariableSetMode>& modeToReorder = modes->map().getOrDefault(modeID);
  if (!modeID.isValid() || !modeToReorder) {
    sloge("Could not find valid mode to reorder",
          {{"variableSet", toDebugString()}, {"modeID", modeID}}, {.reportAsSentryError = true});
    return false;
  }

  auto sortedModes = variableSetModesSorted();
  if (newIndex >= sortedModes.size()) {
    sloge("Invalid index to reorder mode",
          {{"variableSet", toDebugString()},
           {"newIndex", newIndex},
           {"sortedModesSize", sortedModes.size()}},
          {.reportAsSentryError = true});
    return false;
  }

  // Nothing to do
  if (sortedModes[newIndex].modeID() == modeToReorder->modeID()) return false;

  VariableSetMode reorderedMode;
  if (newIndex == 0) {
    // add to beginning
    const auto& oldFirstMode = sortedModes[newIndex];
    reorderedMode = {modeToReorder->modeID(), modeToReorder->name(),
                     oldFirstMode.sortPosition().before()};
  } else if (newIndex == sortedModes.size() - 1) {
    // add to end
    const auto& oldLastMode = sortedModes[newIndex];
    reorderedMode = {modeToReorder->modeID(), modeToReorder->name(),
                     oldLastMode.sortPosition().after()};
  } else {
    // insert in middle
    auto beforePosition = sortedModes[newIndex - 1].sortPosition();
    auto afterPosition = sortedModes[newIndex].sortPosition();
    // Edge case: support transforming [a,b,c,d] to [b,a,c,d] by doing reorder(a, 1)
    if (sortedModes[newIndex - 1].modeID() == modeToReorder->modeID()) {
      beforePosition = afterPosition;
      afterPosition = sortedModes[newIndex + 1].sortPosition();
    }
    reorderedMode = {modeToReorder->modeID(), modeToReorder->name(),
                     Position::between(beforePosition, afterPosition)};
  }
  if (reorderedMode.modeID() != modeID) {
    sloge("Reordering modes should not change mode IDs",
          {{"reorderedMode", reorderedMode.toString()}, {"modeID", modeID.toString()}},
          {.reportAsSentryError = true});
    return false;
  }
  auto newModes = modes->map().copy();
  newModes.set(modeID, Optional<VariableSetMode>{std::move(reorderedMode)});
  return setVariableSetModes(
      makeShared<const MultiplayerMapGUIDVariableSetMode>(std::move(newModes)));
}

void Node::editVariableSetModeName(GUID modeID, String name) {
  MultiplayerMapGUIDVariableSetMode newModes(variableSetModes()->map().copy());
  if (!modeID.isValid() || !newModes.has(modeID)) {
    REPORT_ERROR("Could not find valid mode to rename");
    return;
  }

  Optional<VariableSetMode> newMode = newModes.map().getOrDefault(modeID).copy();
  if (newMode) {
    newMode->setName(ImmutableString(name));
    newModes.set(modeID, std::move(newMode));
    VariableSetHelpers::filterNulloptModes(newModes);
    setVariableSetModes(makeShared<const MultiplayerMapGUIDVariableSetMode>(std::move(newModes)));
  }
}

bool Node::deleteVariableSetMode(GUID modeID) {
  if (!assertAssetType(AssetType::VARIABLE_SET)) {
    return false;
  }

  auto varDepTracker = _scene->variableDependencyTracker();
  if (!varDepTracker) {
    REPORT_ERROR("No variable dependency tracker found on scene");
    return false;
  }

  auto modes = variableSetModes();
  if (!modeID.isValid() || !modes->has(modeID)) {
    REPORT_ERROR("Could not find valid mode to delete");
    return false;
  }
  size_t validModesCount = 0;
  for (const auto& [guid, mode] : modes->map()) {
    if (guid.isValid() && mode) validModesCount++;
  }
  // We may be trying to delete a corrupt mode. This is OK, since it isn't included in the valid
  // count above.
  const auto& modeToDelete = modes->map().getOrDefault(modeID);
  if (modeToDelete.has_value() && validModesCount <= 1) {
    REPORT_ERROR("Cannot delete the only mode in a Variable Set");
    return false;
  }

  MultiplayerMapGUIDVariableSetMode newModes =
      MultiplayerMapGUIDVariableSetMode(modes->map().copy());
  newModes.remove(modeID);
  VariableSetHelpers::filterNulloptModes(newModes);
  setVariableSetModes(makeShared<const MultiplayerMapGUIDVariableSetMode>(std::move(newModes)));

  // On all variables in the variable set, remove the value for the mode
  VariableSetID variableSetID = self()->variableSetId();
  for (const VariableID& variableID : varDepTracker->getVariablesInSet(variableSetID)) {
    NodePtr variable = _scene->variableByIdOrNull(variableID);
    if (!variable) {
      REPORT_ERROR("Variable belonging to variable set not found");
      continue;
    }
    variable->removeVariableDataForMode(modeID);
  }

  // Do this immediately to avoid scenegraph observers reading the wrong state
  // before VariableDependencyTracker's observer runs.
  varDepTracker->clearNodeToVariableModeBySetCache();

  return true;
}

Optional<Immutable<VariableData>> Node::getVariableData(GUID modeID) const {
  ConstNodePtr variableSet = findVariableSetForVariable();
  if (!variableSet) {
    return nullopt;
  }

  GUID validModeID = modeID;
  if (!modeID.isValid()) {
    // If an invalid modeID is passed in, fallback to the default mode
    Optional<VariableSetMode> defaultMode = variableSet->variableSetDefaultMode();
    if (!defaultMode || !defaultMode->modeID().isValid()) {
      sloge("Variable set does not have a valid default mode",
            {{"variableSetID", variableSet->uniqueID()}}, {.reportAsSentryError = true});
      return nullopt;
    }
    validModeID = defaultMode->modeID();
  }

  auto modeToVarData = variableDataValues();
  if (!modeToVarData->has(validModeID)) {
    sloge("Requested mode does not exist on variable",
          {{"variableID", uniqueID()}, {"modeID", validModeID}}, {.reportAsSentryError = true});
    return nullopt;
  }

  auto variableData = modeToVarData->map().getOrDefault(validModeID);
  if (!variableData) {
    sloge("Variable data not found for mode", {{"variableID", uniqueID()}, {"modeID", validModeID}},
          {.reportAsSentryError = true});
  }
  return variableData;
}

Shared<const MultiplayerMapVariableSetIDGUID> Node::variableModeBySetMap__UNSAFE() const {
  return _fields.variableModeBySetMap();
};

Shared<const Set<VariableScope>> Node::variableScopes() const {
  auto variableScopes = _fields.variableScopes();
  // we want scopes to default to ALL_SCOPES if they haven't been set yet
  if (!variableScopes) {
    return makeShared<const Set<VariableScope>>(Set<VariableScope>({VariableScope::ALL_SCOPES}));
  }
  return variableScopes;
}

void Node::setVariableScopes(Shared<const Set<VariableScope>> scopes) {
  set(NodeField::VARIABLE_SCOPES, scopes);
}

Shared<const MultiplayerMapCodeSyntax> Node::codeSyntax() const {
  if (!assertAssetType(AssetType::VARIABLE)) {
    return nullptr;
  }
  return _fields.codeSyntax();
}

void Node::setCodeSyntax(Shared<const MultiplayerMapCodeSyntax> codeSyntax) {
  set(NodeField::CODE_SYNTAX, std::move(codeSyntax));
}
///////////////

// styles

bool Node::shouldReadS2Fields() const {
  return scene()->fileVersion() >= FileVersion::S2_FOR_READS;
}

bool Node::isStyle(StyleType type) const { return styleType() == type; }

// Returns the appropriate style key held on the node for S1/S2 styles. This method is private
// because it does no validations on the node.
StyleKey Node::_subscribedStyleKey() const {
  if (!assertAssetType(AssetType::STYLE)) {
    return {};
  }

  // We're in an S1 file. If we should try to read from the S2 fields AND that field is populated,
  // then use it. Otherwise, fallback to reading S1 fields to support old data.
  if (shouldReadS2Fields()) {
    return fields().key();
  }

  if (hasS1SharedStyleReference()) {
    return sharedS1StyleReference()->styleKey;
  }

  return {};
}

StyleKey Node::styleKey() const {
  if (!assertAssetType(AssetType::STYLE)) {
    return {};
  }

  auto subscribedKey = _subscribedStyleKey();
  if (subscribedKey.isValid()) return subscribedKey;

  sloge_debugOnly_previously_loge("Style does not have a valid style key");
  return {};
}

bool Node::hasStyleKey() const {
  if (!assertAssetType(AssetType::STYLE)) {
    return {};
  }
  return _subscribedStyleKey().isValid();
}

StyleKey Node::styleKeyForPublish() const {
  if (!assertAssetType(AssetType::STYLE)) {
    return {};
  }
  const StyleKey& subscribedKey = _subscribedStyleKey();
  if (subscribedKey.isValid()) {
    return subscribedKey;
  }

  // If the scene has a valid hubFileIdForLibraryPublish, then we want to use generateAssetKeyString
  // to generate the styleKey regardless of whether or not we're in an S1 or S2 file. This will
  // ensure that the assetKey is hashed using the hubFileId rather than the fileKey.
  if (scene()->hubFileIdForLibraryPublish().isValid()) {
    return generateAssetKeyString(scene(), uniqueID(), AssetType::STYLE);
  }

  if (shouldReadS2Fields()) {
    // The generateAssetKeyString method validates that the scene's file key is valid, which
    // unfortunately we cannot always expect to be the case for styles. Our StyleObserver keeps
    // caches keyed on StyleKeys which are populated in response to SceneGraph events, which can be
    // fired before the scene's file key is set.
    return generateAssetKey(scene()->fileKeyForLibraryPublish(), uniqueID());
  }

  if (hasS1SharedStyleMasterData()) {
    const StyleKey& localKey = sharedS1StyleMasterData()->styleKey;
    if (localKey.isValid()) {
      return localKey;
    }
  }

  return {};
}

void Node::setInheritStyleID(InheritStyleField inheritField, StyleId value) {
  // TODO: This should just be one line that doesn't do any lookups once we complete the S2
  // migration: set(toNodeField(inheritField, true), Any(value));
  if (auto vals = value.valuesForSet(scene())) {
    set(toNodeField(inheritField, false), Any(vals->first));
    set(toNodeField(inheritField, true), Any(vals->second));
  }
}

StyleId Node::inheritedStyleID(InheritStyleField inheritField) const {
  // Note: Don't use getCopyOf() here. This is a very hot function, and getCopyOf() allocates
  // memory. We can avoid allocating any memory by getting the GUIDs directly instead.
  NodeField field = toNodeField(inheritField, shouldReadS2Fields());
  if (shouldReadS2Fields() && isInheritStyleField(field, false /* isS2File */)) {
    sloge("Accessed S1 field");
  }
  switch (field) {
    case NodeField::STYLE_ID_FOR_FILL:
      return fields().styleIdForFill();
    case NodeField::STYLE_ID_FOR_STROKE_FILL:
      return fields().styleIdForStrokeFill();
    case NodeField::STYLE_ID_FOR_TEXT:
      return fields().styleIdForText();
    case NodeField::STYLE_ID_FOR_EFFECT:
      return fields().styleIdForEffect();
    case NodeField::STYLE_ID_FOR_GRID:
      return fields().styleIdForGrid();

    case NodeField::INHERIT_FILL_STYLE_ID:
      return StyleId::createFromGuid__S1_ONLY(fields().inheritFillStyleID());

    case NodeField::INHERIT_FILL_STYLE_ID_FOR_STROKE:
      return StyleId::createFromGuid__S1_ONLY(fields().inheritFillStyleIDForStroke());

    case NodeField::INHERIT_TEXT_STYLE_ID:
      return StyleId::createFromGuid__S1_ONLY(fields().inheritTextStyleID());

    case NodeField::INHERIT_EFFECT_STYLE_ID:
      return StyleId::createFromGuid__S1_ONLY(fields().inheritEffectStyleID());

    case NodeField::INHERIT_GRID_STYLE_ID:
      return StyleId::createFromGuid__S1_ONLY(fields().inheritGridStyleID());

    default:
      REPORT_IF_FALSE_DEBUG_ONLY(false);
      return StyleId();
  }
}

namespace {

// This is a "compatibility mode" that reproduces the slightly odd behavior of StyleObserver for
// `isIndirectlySubscribedStyle`. In particular, this will return `true` if the only direct usages
// are in unexpanded instances or scaled instances, even though conceptually these are directly
// subscribed styles.
bool isIndirectlySubscribedStyleStyleConsumptionObserverCompatibility(
    const Node& currentNode, const SceneGraph* scene, Optional<GUID>* directConsumerForLogging) {
  if (!scene->styleConsumptionObserver() &&
      !scene->styleConsumptionObserver()->isTrackingDependencies()) {
    return false;
  }

  bool hasAnyConsumers = false;
  bool hasDirectConsumers = false;
  const StyleId styleId = currentNode.styleId();
  for (const StyleConsumptionDependency& dependency :
       scene->styleConsumptionObserver()->dependencyCache().getDependents(styleId)) {
    // We ignore all dependencies through SYMBOL_DATA or COMPONENT_PROPS_ASSIGNMENT to be compatible
    // with StyleObserver.
    if (!StyleDependencyCacheHelpers::isDirectLocalStyleConsumption(
            dependency.location.topLevelField, *scene)) {
      continue;
    }

    if (ConstNodePtr consumer = scene->nodeByIdOrNull(dependency.fromId)) {
      hasAnyConsumers = true;
      if (consumer->isInternalOnlyNode()) {
        continue;
      }
      if (StyleDependencyCacheHelpers::isDirectStyleConsumer(
              styleId, dependency.location.inheritStyleField, consumer)) {
        hasDirectConsumers = true;
        if (directConsumerForLogging) {
          directConsumerForLogging->emplace(dependency.fromId);
        }
        break;
      }
    }
  }
  return hasAnyConsumers && !hasDirectConsumers;
}

bool isIndirectlySubscribedStyleStyleConsumptionObserver(const Node& currentNode,
                                                         const SceneGraph* scene,
                                                         Optional<GUID>* directConsumerForLogging) {
  if (!FF::style_obs_unexpanded_sublayers()) {
    return isIndirectlySubscribedStyleStyleConsumptionObserverCompatibility(
        currentNode, scene, directConsumerForLogging);
  }
  bool hasAnyConsumers = false;
  bool hasDirectConsumers = false;
  if (scene->styleConsumptionObserver() &&
      scene->styleConsumptionObserver()->isTrackingDependencies()) {
    for (const StyleConsumptionDependency& dependency :
         scene->styleConsumptionObserver()->dependencyCache().getDependents(
             currentNode.styleId())) {
      if (ConstNodePtr node = scene->nodeByIdOrNull(dependency.fromId)) {
        hasAnyConsumers = true;
      }

      bool isDirectConsumer = StyleDependencyCacheHelpers::isDependencyDirect(scene, dependency);
      if (isDirectConsumer) {
        if (directConsumerForLogging) {
          *directConsumerForLogging = dependency.fromId;
        }
        hasDirectConsumers = true;
        break;
      }
    }
  } else {
    REPORT_ERROR_ONCE("Expected styleConsumptionObserver to be enabled and tracking dependencies.");
  }

  return hasAnyConsumers && !hasDirectConsumers;
}
}  // namespace

bool Node::isIndirectlySubscribedStyle() const {
  if (!assertAssetType(AssetType::STYLE)) {
    return false;
  }

  if (FF::disable_style_consumer_observer()) {
    return isIndirectlySubscribedStyleStyleConsumptionObserver(*this, _scene, nullptr);
  }

  StyleObserver* styleObserver = _scene->styleObserver();
  const bool indirectlySubscribed = styleObserver->styleHasConsumers(styleType(), styleId()) &&
                                    !styleObserver->styleHasDirectConsumers(styleType(), styleId());

  if (_scene->styleConsumptionObserver() && FF::style_obs_migration_indirectly() &&
      _scene->styleConsumptionObserver()->isTrackingDependencies()) {
    // TODO: After successful rollout, remove this validation and StyleObserver-based calculation
    // above.
    Optional<GUID> directConsumerForLogging;

    bool indirectlySubscribedFromObserver = isIndirectlySubscribedStyleStyleConsumptionObserver(
        *this, _scene, &directConsumerForLogging);

    // Note that if both hasAnyConsumers and hasAnyUnexpandedConsumers are true, we expect
    // `styleObserver` to be wrong here, so we skip the validation. This is because
    // `styleObserver` will incorrectly count dependencies involving unexpanded instances as
    // "indirect" dependencies.
    if (StyleDependencyCacheHelpers::shouldValidateAgainstStyleObserver(_scene, styleId()) &&
        (indirectlySubscribed != indirectlySubscribedFromObserver)) {
      slogi(
          "Node::isIndirectlySubscribedStyle from styleConsumptionObserver and styleObserver do "
          "not match for style ID: ",
          {{"style ID", styleId().toString()},
           {"indirectlySubscribed - StyleObserver", indirectlySubscribed},
           {"indirectlySubscribed - StyleConsumptionObserver", indirectlySubscribedFromObserver},
           {"indirectlySubscribedFromObserver", indirectlySubscribedFromObserver},
           {"direct consumer", directConsumerForLogging ? directConsumerForLogging->toString()
                                                        : String("No direct consumer found")}});
      REPORT_ERROR_ONCE(
          "Node::isIndirectlySubscribedStyle from styleConsumptionObserver and styleObserver do "
          "not match");
    }
  }

  return indirectlySubscribed;
}

