// shared styles
bool Node::isSubscribedStyle() const {
  // We intentionally call this on non-styles, so this just returns false
  // without logging an error.
  if (!isStyle()) {
    return false;
  }

  if (shouldReadS2Fields()) {
    // In S2, subscribed styles have their key populated.
    return hasStyleKey();
  }

  return sharedS1StyleReference()->isValid() && !hasS1SharedStyleMasterData();
}

bool Node::reportIfSubscribedStyle() const {
  if (isSubscribedStyle()) {
    sloge("Not a subscribed style", {{"guid", uniqueID()}}, {.reportAsSentryError = true});
    return true;
  }
  return false;
}

bool Node::isLocalStyle() const {
  // We intentionally call this on non-styles, so this just returns false
  // without logging an error.

  if (!isStyle()) {
    return false;
  }
  if (shouldReadS2Fields()) {
    return styleSortPosition()->isValid();
  }

  return hasS1SharedStyleMasterData();
}

bool Node::isS2LocalStyle() const { return isStyle() && fields().sortPosition()->isValid(); }

void Node::reportIfS2AInvalidStyle() const {
  if (shouldReadS2Fields() && isStyle() && !isSubscribedStyle() && !isLocalStyle()) {
    sloge("Style missing local and subscribed data",
          {{"guid", uniqueID()}, {"shouldReadS2Fields", shouldReadS2Fields() ? "true" : "false"}},
          {.reportAsSentryError = true});
  }
}

void Node::setStyleVersion(StyleVersion versionHash,
                           UpdateUserFacingVersion updateUserFacingVersion) {
  if (!assertAssetType(AssetType::STYLE)) {
    return;
  }
  set(NodeField::VERSION, versionHash.value());
  if (updateUserFacingVersion == UpdateUserFacingVersion::YES) {
    set(NodeField::USER_FACING_VERSION, versionHash.value());
  }
  if (isSubscribedStyle()) {
    SharedStyleReference ref(*sharedS1StyleReference());
    ref.versionHash = versionHash;
    setSharedStyleReference__S1_ONLY(ref);
  }
}

StyleVersion styleVersion__S1_ONLY(const Node* style) {
  if (style->hasS1SharedStyleMasterData()) {
    return style->version().value();
  }
  return style->sharedS1StyleReference()->versionHash;
}

StyleVersion Node::styleVersion() const {
  if (!assertAssetType(AssetType::STYLE)) {
    return {};
  }
  if (shouldReadS2Fields()) {
    return version().value();
  }
  return styleVersion__S1_ONLY(this);
}

StyleVersion Node::userFacingStyleVersion() const {
  if (!assertAssetType(AssetType::STYLE)) {
    return {};
  }
  StyleVersion userFacingVersion{fields().userFacingVersion()};
  if (userFacingVersion.isValid()) {
    return userFacingVersion;
  }
  return styleVersion();
}

void Node::populateSubscribedStyleFields(StyleKey key, ConstNodePtr sourceStyle) {
  if (!assertAssetType(AssetType::STYLE)) {
    return;
  }
  if (isLocalStyle() || hasS1SharedStyleMasterData()) {
    sloge("Immutable styles violation - cannot convert local style to subscribed",
          {.reportAsSentryError = true});
  }
  // While these local fields don't dictate whether a style is considered local or subscribed,
  // they should not exist on subscribed nodes.
  REPORT_IF_TRUE_DEBUG_ONLY(isPublishable());
  REPORT_IF_TRUE_DEBUG_ONLY(isSoftDeletedStyle());

  StyleVersion version = styleVersion__S1_ONLY(sourceStyle.get());
  auto ref = SharedStyleReference(key, version);
  setSharedStyleReference__S1_ONLY(ref);

  set(NodeField::KEY, key.value());
  set(NodeField::VERSION, sourceStyle->version().value());
  set(NodeField::USER_FACING_VERSION, sourceStyle->userFacingStyleVersion().value());
}

void Node::populateLocalStyleFields(Immutable<Position> position, FileKey fileKey, StyleKey key,
                                    StyleVersion version) {
  if (!assertAssetType(AssetType::STYLE)) {
    return;
  }
  REPORT_IF_TRUE(isSubscribedStyle(),
                 "Immutable styles violation - cannot convert subscribed style to local");
  setStyleVersion(version, UpdateUserFacingVersion::YES);
  setStyleSortPosition(position);

  // Make style into a local master
  setSharedStyleMasterData_S1_ONLY(SharedStyleMasterData(key, position.value(), fileKey));

  REPORT_IF_FALSE(hasS1SharedStyleMasterData(), "Main style is missing style master data");
  REPORT_IF_FALSE((!hasS1SharedStyleReference() || !sharedS1StyleReference()->isValid()) &&
                  "Main style has shared style reference data");
}

bool Node::setSharedStyleMasterData_S1_ONLY(const SharedStyleMasterData& data) {
  if (!assertAssetType(AssetType::STYLE)) {
    return false;
  }

// We purposefully call setSharedStyleMasterData when hasS1SharedStyleReference() is true
// in a migration test to check that we fix up corrupted data. In order for that test to pass,
// we can't throw an error here. But we should be throwing an error in other environments
// such as prod so we can log them.
#if !BUILD_TEST
  if (data.styleKey.isValid() && hasS1SharedStyleReference()) {
    sloge("setSharedStyleMasterData called on node with valid style reference",
          {{"nodeId", uniqueID()},
           {"styleReferenceKey", sharedS1StyleReference()->styleKey.str()},
           {"styleMasterKey", data.styleKey.str()}});
    REPORT_ERROR("setSharedStyleMasterData called on node with valid style reference");
  }
#endif
  return set(NodeField::SHARED_STYLE_MASTER_DATA, Immutable<SharedStyleMasterData>(data));
}

void Node::setSharedStyleReference__S1_ONLY(const SharedStyleReference& data) {
  if (!assertAssetType(AssetType::STYLE)) {
    return;
  }

// We purposefully call setSharedStyleReference when hasS1SharedStyleMasterData() is true
// in a migration test to check that we fix up corrupted data. In order for that test to pass,
// we can't throw an error here. But we should be throwing an error in other environments
// such as prod so we can log them.
#if !BUILD_TEST
  if (data.styleKey.isValid() && hasS1SharedStyleMasterData()) {
    sloge("setSharedStyleReference called on node with valid master data",
          {{"nodeId", uniqueID()},
           {"styleMasterKey", sharedS1StyleMasterData()->styleKey.str()},
           {"styleReferenceKey", data.styleKey.str()}});
    REPORT_ERROR("setSharedStyleReference called on node with valid master data");
  }
#endif

  set(NodeField::SHARED_STYLE_REFERENCE,
      Immutable(SharedStyleReference(
          data.styleKey,
          // In S2 files we always populate S1 fields, but sometimes we treat the S2 value as the
          // source of truth (i.e. when the S2 read flag is enabled). Even in those cases, we don't
          // want the S2 dedupe suffix that we write to S2 version fields during the migration to
          // make it onto S1 fields, so make sure to strip it here.
          ImmutableString{
              data.versionHash.str().substr(0, data.versionHash.str().find("@S2_DEDUPE_"))})));
}

bool Node::hasS1SharedStyleMasterData() const {
  return sharedS1StyleMasterData()->styleKey.isValid();
}

bool Node::hasS1SharedStyleReference() const {
  return sharedS1StyleReference()->styleKey.isValid();
}

Immutable<SharedStyleReference> Node::sharedS1StyleReference() const {
  return fields().sharedStyleReference();
}

Immutable<SharedStyleMasterData> Node::sharedS1StyleMasterData() const {
  return fields().sharedStyleMasterData();
}

StyleRef Node::styleRef(GetStyleRefFor refFor) const {
  if (!assertAssetType(AssetType::STYLE)) {
    return {};
  }
  if (!shouldReadS2Fields() && isSubscribedStyle()) {
    // Post-migration, the S2 versions on subscribed styles may differ from their S1 versions.
    // When we're writing to an S2 consumption field, the ref should use the corresponding S2
    // version; if we're using it to identify a style, we should use the S1 version.
    auto legacyRef = sharedS1StyleReference();
    StyleVersion styleVersion =
        refFor == GetStyleRefFor::Consumer ? version().value() : legacyRef->versionHash;
    return {legacyRef->styleKey, styleVersion};
  }
  return {styleKeyForPublish(), styleVersion()};
}

void Node::setStyleRef(const StyleRef& ref) {
  if (!assertAssetType(AssetType::STYLE)) {
    return;
  }
  set(NodeField::KEY, ref.key().value());
  setStyleVersion(ref.version(), UpdateUserFacingVersion::YES);

  setSharedStyleReference__S1_ONLY({ref.key(), ref.version()});
}

void Node::setLocalStyleAndFileKey__S1_ONLY(StyleKey key, FileKey fileKey) {
  SharedStyleMasterData newMasterData = sharedS1StyleMasterData().value();
  newMasterData.styleKey = key;
  newMasterData.fileKey = fileKey;
  setSharedStyleMasterData_S1_ONLY(std::move(newMasterData));
}

Immutable<Position> Node::styleSortPosition() const {
  if (!assertAssetType(AssetType::STYLE)) {
    return {};
  }
  if (shouldReadS2Fields()) {
    return fields().sortPosition();
  }

  if (hasS1SharedStyleMasterData()) {
    return Immutable(sharedS1StyleMasterData()->sortPosition);
  }
  return {};
}

void Node::setStyleSortPosition(Immutable<Position> position) {
  if (!assertAssetType(AssetType::STYLE)) {
    return;
  }
  set(NodeField::SORT_POSITION, position);

  SharedStyleMasterData newMasterData = sharedS1StyleMasterData().value();
  newMasterData.sortPosition = *position;
  setSharedStyleMasterData_S1_ONLY(std::move(newMasterData));
}

bool SharedStyleReference::operator==(const SharedStyleReference& other) const {
  if (this == &other) {
    return true;
  }

  if (styleKey != other.styleKey) {
    return false;
  }

  if (versionHash != other.versionHash) {
    return false;
  }

  return true;
}

SharedStyleReference& SharedStyleReference::operator=(SharedStyleReference&& other) STL_FAST {
  styleKey = std::move(other.styleKey);
  versionHash = std::move(other.versionHash);
  return *this;
}

