VariableDataSetResult Node::clearVariableOverrideForMode(const GUID& modeId) {
  if (!isLocalVariableOverride()) {
    sloge("Can only clear overrides on local variable overrides", {{"node", this->toString()}},
          {.reportAsSentryError = true});
    return VariableDataSetResult::InvalidArgument;
  }
  auto newModeToVarData = variableDataValues()->map().copy();
  newModeToVarData.erase(modeId);
  bool success = setVariableDataValues(
      makeShared<const MultiplayerMapGUIDVariableData>(std::move(newModeToVarData)));
  if (success) {
    return VariableDataSetResult::Success;
  } else {
    return VariableDataSetResult::InvalidArgument;
  }
}

VariableDataSetResult Node::setVariableOverrideForMode(const GUID& modeId,
                                                       Immutable<VariableData> variableData) {
  if (!isLocalVariableOverride()) {
    sloge("Can only set overrides on local variable overrides", {{"node", this->toString()}},
          {.reportAsSentryError = true});
    return VariableDataSetResult::InvalidArgument;
  }
  ConstNodePtr backingVariableSetExtension =
      scene()->variableSetByIdOrNull(variableSetIdForVariableOverride());
  if (!backingVariableSetExtension) {
    sloge("Could not find backing variable set extension for variable override",
          {{"variableOverride", this->toString()},
           {"backingVariableSetId", backingVariableSetId().toString()}},
          {.reportAsSentryError = true});
    return VariableDataSetResult::InvalidArgument;
  }
  if (!backingVariableSetExtension->isLocalVariableSetExtension()) {
    sloge("variable override belongs to non-local variable set extension",
          {{"node", this->toString()},
           {"backingVariableSet", backingVariableSetExtension->toString()}},
          {.reportAsSentryError = true});
    return VariableDataSetResult::InvalidArgument;
  }
  if (!backingVariableSetExtension->variableSetModes()->map().getOrDefault(modeId)) {
    sloge("Backing variable set extension does not contain mode", {{"modeId", modeId.toString()}},
          {.reportAsSentryError = true});
    return VariableDataSetResult::InvalidArgument;
  }
  return setVariableDataForMode(modeId, variableData);
}

Optional<Immutable<VariableData>> Node::getVariableOverrideForMode(const GUID& modeId) const {
  if (!isVariableOverride()) {
    sloge("Can only get overrides on variable override nodes", {{"node", this->toString()}},
          {.reportAsSentryError = true});
    return nullopt;
  }
  ConstNodePtr backingVariableSetExtension =
      scene()->variableSetByIdOrNull(variableSetIdForVariableOverride());
  if (!backingVariableSetExtension) {
    sloge("Could not find backing variable set extension for variable override",
          {{"variableOverride", this->toString()},
           {"backingVariableSetId", backingVariableSetId().toString()}},
          {.reportAsSentryError = true});
    return nullopt;
  }
  if (!backingVariableSetExtension->isVariableSetExtension()) {
    sloge("variable override belongs to non-variable set extension",
          {{"node", this->toString()},
           {"backingVariableSet", backingVariableSetExtension->toString()}},
          {.reportAsSentryError = true});
    return nullopt;
  }
  if (!backingVariableSetExtension->variableSetModes()->map().getOrDefault(modeId)) {
    sloge("Backing variable set extension does not contain mode", {{"modeId", modeId.toString()}},
          {.reportAsSentryError = true});
    return nullopt;
  }
  return variableDataValues()->map().getOrDefault(modeId);
}

