// Getters are removed from the header to remove some code bloat
ImmutableArray<float> Node::strokeDashPattern() const {
  // It's possible to specify a dash pattern [0,0], which renders as solid but can be tricky if we
  // check for `dashPattern.size()` to determine dashed-ness. In these cases, return an empty array.
  ImmutableArray<float> result = fields().strokeDashPattern();
  if (!result.empty() &&
      std::all_of(result.begin(), result.end(), [](const float& f) { return f == 0; })) {
    return {};
  }
  return result;
}

Join Node::strokeJoin() const {
  reportIfAccessingUnsetNodeField(NodeField::STROKE_JOIN, self());
  return fields().strokeJoin();
}
float Node::strokeMiterLimit() const {
  if (!hasNodeField(NodeField::STROKE_MITER_LIMIT) || fields().strokeMiterLimit() < 1) {
    // This value must be greater than or equal to 1, but there are some codepaths which attempt to
    // set this value to 0/assert that unset values are equal to 0. We think it's safe to interpret
    // these invalid values as the default value of 4, so we'll roll out this feature flag slowly to
    // validate this assumption.
    //
    // See: https://developer.mozilla.org/en-US/docs/Web/SVG/Attribute/stroke-miterlimit
    return defaultMiterLimit;
  }

  reportIfAccessingUnsetNodeField(NodeField::STROKE_MITER_LIMIT, self());
  return fields().strokeMiterLimit();
}

