Shared<const Text::DerivedTextData> Node::derivedTextData() const {
  // During the simultaneous rollout of the double-write migration, read flag, and single-write
  // migration, there will be a mix of users with the read flag on or off, and file content with
  // OLD_ONLY, BOTH, or NEW_ONLY fields. Rather than requiring each stage to fully rollout before
  // starting the next, gracefully handle mixed data and flags. Scenarios to consider:
  // 1. Read flag is on & OLD_ONLY:   Fallback to the old field
  // 2. Read flag is on & BOTH:       Prefer the new field
  // 3. Read flag is on & NEW_ONLY:   Prefer the new field
  // 4. Read flag is off & OLD_ONLY:  Prefer the old field
  // 5. Read flag is off & BOTH:      Prefer the old field
  // 6. Read flag is off & NEW_ONLY:  Fallback to the new field

  Shared<const DerivedTextData> newField =
      _fields.derivedTextData() ? _fields.derivedTextData() : DerivedTextData::UNSET;

  if (scene()->fileVersion() >= FileVersion::TEXT_SEPARATE_DERIVED_TEXT_CLEANUP) {
    return newField;
  }

  Shared<const DerivedTextData> oldField =
      _fields.textData() && _fields.textData()->dangerouslyGetDerivedTextData()
          ? _fields.textData()->dangerouslyGetDerivedTextData()
          : DerivedTextData::UNSET;

  if (FF::text_data_derived_sep_read() && newField != DerivedTextData::UNSET) {
    // Scenarios 2 and 3
    return newField;
  } else if (oldField != DerivedTextData::UNSET) {
    // Scenarios 1, 4, and 5
    slogw_oncePerDoc(
        "Reading deprecated derived text data field",
        {{"nodeID", uniqueID()}, {"fileVersion", Figma::toString(scene()->fileVersion())}},
        {.reportAsSentryError = true});
    return oldField;
  } else {
    // Scenario 6
    return newField;
  }
}

Shared<const Text::DerivedTextData> Node::placeholderDerivedTextData() const {
  // Even though placeholderDerivedTextData is not persisted to the file, it may be in paste
  // buffers, so we need to handle the scenarios just the same as with derivedTextData above.

  Shared<const DerivedTextData> newField = _fields.placeholderDerivedTextData();

  if (scene()->fileVersion() >= FileVersion::TEXT_SEPARATE_DERIVED_TEXT_CLEANUP) {
    return newField;
  }

  Shared<const DerivedTextData> oldField =
      _fields.placeholderTextData() ? _fields.placeholderTextData()->dangerouslyGetDerivedTextData()
                                    : nullptr;

  if (FF::text_data_derived_sep_read() && newField) {
    return newField;
  } else if (oldField && oldField != DerivedTextData::UNSET) {
    slogw_oncePerDoc(
        "Reading deprecated placeholder derived text data field",
        {{"nodeID", uniqueID()}, {"fileVersion", Figma::toString(scene()->fileVersion())}},
        {.reportAsSentryError = true});
    return oldField;
  } else {
    return newField;
  }
}

