bool Node::hasPrototypeInteraction() const { return this->prototypeInteractions()->size() > 0; }

Optional<PrototypeInteraction> Node::findPrototypeInteraction(
    const Optional<GUID>& interactionID) const {
  if (!interactionID) {
    return nullopt;
  }
  Optional<PrototypeInteraction> targetInteraction;
  const auto nodePrototypeInteractions = prototypeInteractions();
  for (auto& interaction : *nodePrototypeInteractions) {
    if (interaction.getID() == interactionID) {
      targetInteraction = interaction;
      break;
    }
  }

  return targetInteraction;
}

Immutable<PrototypeInteractions> Node::nonInternalPrototypeInteractions() const {
  Immutable<PrototypeInteractions> currentInteractions = prototypeInteractions();
  Array<PrototypeInteraction> newInteractions(currentInteractions->size());
  ConstNodePtr node = self();
  ConstNodePtr canvas = findContainingCanvas();

  auto it = std::copy_if(currentInteractions->interactions().begin(), currentInteractions->end(),
                         newInteractions.begin(), [canvas, node](PrototypeInteraction interaction) {
                           return !isInternalInteraction(canvas, node, interaction);
                         });
  newInteractions.resize(std::distance(newInteractions.begin(), it));

  return Immutable<PrototypeInteractions>(std::move(newInteractions));
}

Optional<PrototypeInteraction> Node::findEmptyPrototypeInteraction() const {
  const auto nodePrototypeInteractions = prototypeInteractions();
  for (const auto& interaction : *nodePrototypeInteractions) {
    if (interaction.getEvent()->getInteractionType() == InteractionType::NONE) {
      return interaction;
    }
    if (interaction.getActions().empty()) {
      return interaction;
    }
    bool interactionContainsConnection = false;
    for (auto action : interaction.getActions()) {
      if (action.getConnectionType() != ConnectionType::NONE) {
        interactionContainsConnection = true;
      }
    }
    if (!interactionContainsConnection) {
      return interaction;
    }
  }
  return nullopt;
}

int Node::filterPrototypeInteractions(const Function<bool(PrototypeInteraction)>& filterFunction) {
  const auto currentInteractions = prototypeInteractions();
  Array<PrototypeInteraction> newInteractions(currentInteractions->size());
  auto it = std::copy_if(currentInteractions->begin(), currentInteractions->end(),
                         newInteractions.begin(),
                         [&](const auto& interaction) { return filterFunction(interaction); });
  newInteractions.resize(std::distance(newInteractions.begin(), it));
  setPrototypeInteractions(Immutable<PrototypeInteractions>(std::move(newInteractions)));
  return currentInteractions->size() - prototypeInteractions()->size();
}

int Node::deletePrototypeInteraction(const GUID& interactionID) {
  auto cb = [&](PrototypeInteraction interaction) { return interaction.getID() != interactionID; };
  return filterPrototypeInteractions(cb);
}

Optional<PrototypeAction> Node::findFirstActionForPrototypeInteraction(
    const Optional<GUID>& interactionID) const {
  if (!interactionID) {
    return nullopt;
  }
  auto interaction = findPrototypeInteraction(interactionID);
  if (!interaction) {
    return nullopt;
  }

  return interaction->getFirstAction();
}

ImmutableArray<PrototypeAction> Node::findAllActionsForPrototypeInteraction(
    const Optional<GUID>& interactionID) const {
  if (!interactionID) {
    return ImmutableArray<PrototypeAction>();
  }
  auto interaction = findPrototypeInteraction(interactionID);
  if (!interaction) {
    return ImmutableArray<PrototypeAction>();
  }

  return interaction->getAllActions(true);
}

void Node::updateOrAddPrototypeInteraction(const PrototypeInteraction& interaction) {
  Array<PrototypeInteraction> interactions = prototypeInteractions()->interactions().copy();
  auto didSetInteraction = false;
  for (size_t i = 0; i < interactions.size(); i++) {
    if (interaction.getID() == interactions[i].getID()) {
      interactions[i] = interaction;
      didSetInteraction = true;
      break;
    }
  }

  if (!didSetInteraction) {
    interactions.push_back(interaction);
  }

  setPrototypeInteractions(Immutable<PrototypeInteractions>(std::move(interactions)));
}

