ScrollBehavior Node::effectiveScrollBehavior() const {
  // Find the constraint anchor for this node. If it is a scrolling frame, use
  // the position of our ancestor directly below the frame to figure out if we
  // should be fixed. We do this to handle layers inside groups inside scrolling
  // frames. For example...
  //
  // Canvas
  //  |_Scrollable Frame (Vertical)
  //    |_ Group
  //       |_ Rectangle
  //
  // Here Rectangle's scroll behavior is ::FIXED iff Group is fixed.
  auto directChild = findAncestorOrSelfMatching([&](ConstNodePtr node) {
    auto parent = node->parent();
    if (parent && parent->isStackOrFixedSizeContainer()) {
      return true;
    }
    return false;
  });

  if (!directChild) {
    return ScrollBehavior::SCROLLS;
  }

  return directChild->scrollBehavior();
}

void Node::setIsFixed(bool isFixed) {
  set(NodeField::SCROLL_BEHAVIOR, isFixed ? ScrollBehavior::FIXED : ScrollBehavior::SCROLLS);
}

