bool Node::isScrollingFrame() const {
  return isNodeTypeInFrame(type()) && !isGroup() && hasNodeField(NodeField::OVERFLOW_DIRECTION) &&
         overflowDirection() != OverflowDirection::NONE;
}

bool Node::isScrollableInPrototypeView() const {
  if (isGroup()) {
    return false;
  }

  if (!isNodeTypeInFrame(type())) {
    return false;
  }

  if (isOrInWidget()) {
    return false;
  }

  return isTopLevelNode() || overflowDirection() != OverflowDirection::NONE;
}

bool Node::isGroup() const {
  bool result = isNodeTypeInFrame(type()) && fields().resizeToFit();
  if (result && isStack()) {
    sloge_debugOnly_previously_loge("resize-to-fit should be set to false on all stacking frames");
    return false;
  }
  return result;
}

bool Node::isStackOrFixedSizeContainer() const { return isNodeTypeInFrame(type()) && !isGroup(); }

bool Node::isConstrainingFrame() const {
  return isNodeTypeInConstrainingFrame(type()) && !isGroup();
}

