void Node::setIsUsingFrameClipping(bool enable) {
  // Validation is done inside set().
  // It's important to call the generated accessor (double-negative or no) to get the
  // virtual field lookup logic. Calling set() directly would bypass that.
  setIsFrameClippingDisabled(!enable);
}

bool Node::isMaskInCanvas() const {
  // this means that a shape is a part of a mask group, it or the parent must have isMask set
  //   this is different than isMasked which are the siblings that are affected by the mask

  for (ConstNodePtr node = self(); node; node = node->parent()) {
    if (node->isMask()) {
      return true;
    }
  }
  return false;
}

// Recompute children's masks.
// Upon return, areChildSiblingMasksDirty is guaranteed false.
void Node::updateChildSiblingMasks() {
  REPORT_IF_FALSE_DEBUG_ONLY(
      isValid());  // Should only be called from one place where it is already checked.
  if (!_areChildSiblingMasksDirty) return;

  forEachChildSiblingMasks(self(), [&](NodePtr child, NodePtr previousMask) {
    child->setVisibleSiblingMask(previousMask);
  });

  _areChildSiblingMasksDirty = false;
}

void Node::setVisibleSiblingMask(NodePtr p) {
  if (p) {
    visibleSiblingMaskForNode().insert_or_assign(_fields.dkey(), p);
  } else {
    visibleSiblingMaskForNode().erase(_fields.dkey());
  }
}

bool Node::isMaskOutline() const {
  return isMaskOutline_DEPRECATED() || fields().maskType() == MaskType::OUTLINE;
}

NodePtr Node::visibleSiblingMask() const {
  // these stop masks, so will get back null if called on one
  if (shouldStopMaskPropagation()) return nullptr;

  // Querying a child evaluates all children in the parent if the parent is dirty. This ensures
  // that iterating over all children of this parent and calling "visibleSiblingMask()" on each
  // child is O(n) time. It would be O(n^2) instead if each child had its own dirty state and only
  // updated its cached sibling mask, which would be horrendously slow for larger files.
  if (NodePtr p = parent()) {
    p->updateChildSiblingMasks();
  }

  const auto& map = visibleSiblingMaskForNode();
  auto it = map.find(_fields.dkey());
  return it != map.end() ? it->second : nullptr;
}

bool Node::hasVisibleMasksAsChildren() const {
  const auto& map = visibleMaskChildCountForNode();
  auto it = map.find(_fields.dkey());
  return it != map.end() && it->second;
}

bool Node::isThisOrParentOutlineMask() const {
  for (ConstNodePtr node = self(); node; node = node->parent()) {
    if (node->isMask() && node->isMaskOutline()) return true;
  }

  return false;
}

AnnotationMeasurement Node::addMeasurement(NodePtr toNode, AnnotationMeasurementNodeSide fromSide,
                                           AnnotationMeasurementNodeSide toSide,
                                           float innerOffsetFixed, float outerOffsetRelative) {
  Array<AnnotationMeasurementRaw> measurements;
  measurements.reserve(self()->measurements().size() + 1);

  for (auto& m : self()->measurements()) {
    measurements.push_back(m.copy());
  }

  AnnotationMeasurementRaw newMeasurementRaw{scene()->createUniqueMeasurementId(),
                                             GUID::invalid(),  // deprecated
                                             fromSide,
                                             fromSide == toSide,
                                             innerOffsetFixed,
                                             outerOffsetRelative,
                                             toNode->stablePath()};

  AnnotationMeasurement newMeasurement =
      AnnotationMeasurement::fromRaw(newMeasurementRaw, _uniqueID);

  measurements.push_back(std::move(newMeasurementRaw));
  self()->setMeasurements(ImmutableArray<AnnotationMeasurementRaw>(std::move(measurements)));

  return newMeasurement;
}

void Node::removeMeasurement(GUID measurementId) {
  Array<AnnotationMeasurementRaw> measurements;
  measurements.reserve(self()->measurements().size() - 1);

  for (auto& m : self()->measurements()) {
    if (m.id != measurementId) {
      measurements.push_back(m.copy());
    }
  }

  self()->setMeasurements(ImmutableArray<AnnotationMeasurementRaw>(std::move(measurements)));
}

AnnotationMeasurement Node::updateMeasurementOffset(GUID measurementId, float innerOffsetRelative,
                                                    float outerOffsetFixed) {
  Array<AnnotationMeasurementRaw> measurementsRaw;
  measurementsRaw.reserve(self()->measurements().size());

  AnnotationMeasurement newMeasurement{};

  for (auto& m : self()->measurements()) {
    if (m.id == measurementId) {
      auto newMeasurementRaw = m.copy();
      newMeasurementRaw.innerOffsetRelative = innerOffsetRelative;
      newMeasurementRaw.outerOffsetFixed = outerOffsetFixed;

      newMeasurement = AnnotationMeasurement::fromRaw(newMeasurementRaw, _uniqueID);

      measurementsRaw.push_back(std::move(newMeasurementRaw));
    } else {
      measurementsRaw.push_back(m.copy());
    }
  }

  self()->setMeasurements(ImmutableArray<AnnotationMeasurementRaw>(std::move(measurementsRaw)));

  return newMeasurement;
}

AnnotationMeasurement Node::updateMeasurementToNode(GUID measurementId,
                                                    const StablePathArray& toNodePath) {
  Array<AnnotationMeasurementRaw> measurementsRaw;
  measurementsRaw.reserve(self()->measurements().size());

  AnnotationMeasurement newMeasurement{};

  for (auto& m : self()->measurements()) {
    if (m.id == measurementId) {
      auto newMeasurementRaw = m.copy();
      // When converting a frame to a component, the component is added and then the frame is
      // removed. If we don't update the measurement ID, the measurement will be added to the
      // measurement observer's data structures when the component is added (it's already there, so
      // will just 'update' it), and _then_ remove it when the frame is removed and measurements are
      // cleaned up. So setting a new ID gets around this issue and keeps the observer data
      // structures in sync.
      newMeasurementRaw.id = scene()->createUniqueMeasurementId();
      newMeasurementRaw.toNodeStablePath = toNodePath.copy();

      newMeasurement = AnnotationMeasurement::fromRaw(newMeasurementRaw, _uniqueID);

      measurementsRaw.push_back(std::move(newMeasurementRaw));
    } else {
      measurementsRaw.push_back(m.copy());
    }
  }

  self()->setMeasurements(ImmutableArray<AnnotationMeasurementRaw>(std::move(measurementsRaw)));

  return newMeasurement;
}

void Node::copyMeasurements(const ImmutableArray<AnnotationMeasurementRaw>& oldMeasurements) {
  Array<AnnotationMeasurementRaw> measurements;
  measurements.reserve(self()->measurements().size());

  for (auto& m : oldMeasurements) {
    auto newMeasurement = m.copy();
    // Keep measurement IDs unique between different nodes. This ensures adding / removing nodes
    // (such as converting to a component or duplicating) is handled correctly in the annotation
    // observer.
    newMeasurement.id = scene()->createUniqueMeasurementId();
    measurements.push_back(std::move(newMeasurement));
  }

  self()->setMeasurements(ImmutableArray<AnnotationMeasurementRaw>(std::move(measurements)));
}

bool Node::canHaveStatus() const {
  if ((type() != NodeType::SECTION && type() != NodeType::FRAME && type() != NodeType::SYMBOL &&
       type() != NodeType::INSTANCE) ||
      isResponsiveSet()) {
    return false;
  }

  // Only top-level frames / components / instances are allowed status.
  // Only nodes that aren't inside something with status are allowed status.
  NodePtr node = parent();
  while (node) {
    if (node->type() == NodeType::CANVAS) {
      return true;
    }

    if (node->type() != NodeType::SECTION) {
      return false;
    }

    if (node->hasReadyStatus()) {
      return false;
    }

    if (node->hasCompletedStatus()) {
      return false;
    }

    node = node->parent();
  }

  return true;
}

bool Node::hasBeenEdited() const {
  if (!_fields.hasNodeField(NodeField::EDIT_INFO)) {
    return false;
  }

  return editInfo().lastEditedAt > editInfo().createdAt;
}
bool Node::hasReadyStatus() const { return statusInfo().status == NodeStatus::BUILD; }
bool Node::hasCompletedStatus() const {
  return FF::dt_dev_mode_workflows() && statusInfo().status == NodeStatus::COMPLETED;
}
bool Node::hasStatus() const { return hasReadyStatus() || hasCompletedStatus(); }
bool Node::hasBeenEditedSinceLastStatusChange() const {
  return FF::dt_dev_mode_workflows() &&
         statusInfo().lastUpdateUnixTimestamp != NodeStatusInfo::HAS_NO_TIMESTAMP &&
         editInfo().lastEditedAt > statusInfo().lastUpdateUnixTimestamp;
}

NodeStatusInfo Node::statusInfo() const {
  if (!canHaveStatus()) {
    return NodeStatusInfo();
  }

  if (fields().hasNodeField(NodeField::SECTION_STATUS_INFO)) {
    return fields().sectionStatusInfo();
  }

  return NodeStatusInfo();
}

void Node::setStatusInfo(const NodeStatusInfo& statusInfo) {
  if (!canHaveStatus()) {
    return;
  }

  // Only allow writing the COMPLETED status if the feature flag is enabled.
  if (statusInfo.status == NodeStatus::COMPLETED && !FF::dt_dev_mode_workflows()) {
    return;
  }

  NodePtr containingPage = findContainingCanvas();
  if (!containingPage) {
    return;
  }

  auto statusMapPtr = containingPage->handoffStatusMap();
  MultiplayerMapHandoffStatus statusMap;
  if (statusMapPtr) {
    statusMap = statusMapPtr->map().copy();
  }
  statusMap.set(uniqueID(), statusInfo.status);
  containingPage->setHandoffStatusMap(
      makeShared<MultiplayerMapHandoffStatus>(std::move(statusMap)));
  set(NodeField::SECTION_STATUS_INFO, statusInfo);
}

Set<GUID> Node::getNodesContainedWithStatuses() const {
  if (!isCanvas() && !canHaveStatus()) {
    return {};
  }

  Set<GUID> guids{};

  if (isCanvas() && handoffStatusMap() && handoffStatusMap()->map().size() > 0) {
    for (auto& [guid, nodeStatusInfo] : handoffStatusMap()->map()) {
      if (nodeStatusInfo && nodeStatusInfo->status != NodeStatus::NONE) {
        guids.insert(guid);
      }
    }
  }

  // Account for old files where the handoff status map didn't exist
  if (!isCanvas() && statusInfo().status != NodeStatus::NONE) {
    guids.insert(_uniqueID);
  }
  for (const ConstNodePtr& child : children()) {
    guids.merge(child->getNodesContainedWithStatuses());
  }

  return guids;
}

bool Node::hasSectionPreset() const {
  if (!fields().hasNodeField(NodeField::SECTION_PRESET_INFO)) {
    return false;
  }

  // Multiplayer doesn't support erasing node fields, so we need to check
  // if the shelfId is a non-zero value. This can happen in the case where a user
  // undos the change or the user removes the preset.
  return fields().sectionPresetInfo()->shelfId() != 0;
}

// The order of layers is updated to make sure that fixed nodes are always above
// scrolling nodes.
void Node::setScrollBehaviorAndUpdateLayerOrdering(ScrollBehavior behavior) {
  auto p = parent();
  if (!p) {
    logWarningToConsole_debugOnly_deprecated("Node::setScrollBehaviorAndUpdateLayerOrdering",
                                             "Called on a node without a parent");
    return;
  }

  if (behavior == effectiveScrollBehavior()) {
    return;
  }

  if (behavior != ScrollBehavior::FIXED && effectiveScrollBehavior() != ScrollBehavior::FIXED) {
    return setScrollBehavior(behavior);
  }

  // Move this node *just* to the other side of the boundary. If this node is
  // currently fixed, it'll be moved just above all the scrolling elements. If
  // it scrolls, it'll be moved just below all the fixed elements.
  int dividerIndex = p->indexOfFirstValidFixedChild();

  // If we're just re-ordering the node so that it's higher in the same parent,
  // reduce the insertion index for legacy reasons. See details in
  // SceneGraph::transferChild.
  if (p->indexOfChild(self()) < dividerIndex) {
    dividerIndex--;
  }

  p->transferChild(dividerIndex, self(), behavior);
}

