// Keep this logic in sync with selectComponentDescriptionFromSelection in selection_selectors.ts
ImmutableString Node::description() const {
  if (!isStyle() && !isStateGroup() && !isSymbol() && !isVariable() && !isModule()) {
    sloge("Attempted to get description on an invalid node type",
          {{"type", typeName()}, {"guid", uniqueID()}},
          {.reportAsSentryError = true, .includePayloadAsTags = true});
    return ImmutableString();
  }
  auto legacyDescription = isStyle() ? fields().styleDescription() : fields().symbolDescription();
  auto newDescription = fields().description();

  if (shouldUseNewDescription(newDescription, legacyDescription)) {
    return newDescription;
  }

  auto legacyDescriptionStr = legacyDescription.str();
  return ImmutableString(escapeHtml(legacyDescriptionStr));
}

ImmutableString Node::descriptionPlain() const {
  if (!isStyle() && !isStateGroup() && !isSymbol() && !isVariable() && !isModule()) {
    sloge("Attempted to get description on an invalid node type",
          {{"type", typeName()}, {"guid", uniqueID()}},
          {.reportAsSentryError = true, .includePayloadAsTags = true});
    return ImmutableString();
  }
  return isStyle() ? fields().styleDescription() : fields().symbolDescription();
}

void Node::setDescription(String description) {
  if (!isStyle() && !isStateGroup() && !isSymbol() && !isVariable() && !isModule()) {
    REPORT_ERROR("Attempted to set description on an invalid node type");
    return;
  }
  String richtextDescription = escapeHtml(description);

  auto legacyField = isStyle() ? NodeField::STYLE_DESCRIPTION : NodeField::SYMBOL_DESCRIPTION;
  set(legacyField, ImmutableString(std::move(description)));
  set(NodeField::DESCRIPTION, ImmutableString(std::move(richtextDescription)));
}

