  }
  auto legacyField = isStyle() ? NodeField::STYLE_DESCRIPTION : NodeField::SYMBOL_DESCRIPTION;
  set(NodeField::DESCRIPTION, ImmutableString(std::move(descriptionRich)));
  set(legacyField, ImmutableString(std::move(descriptionPlain)));
}

