void Node::setHeight(float value) { setSize(VectorF{width(), value}); }

static bool sizeIsValid(Vector size) {
  return size.isFinite() && !size.hasNaN() && Vector::abs(size).min() < MAX_CANVAS_SIZE;
}

bool Node::setMinSize(Optional<VectorF> value) {
  if (value == minSize()) return false;

  if (!value) {
    // We're clearing out this node's min.
    set(NodeField::MIN_SIZE, Optional<VectorF>());
    return true;
  }

  // Error check the min for validity
  const bool newSizeIsValid = sizeIsValid(*value);
  const bool oldSizeIsValid = sizeIsValid(Vector(minWidth(), minHeight()));
  if (!newSizeIsValid && oldSizeIsValid) {
    REPORT_ERROR_ONCE(
        "Node::setMinSize() cannot set min size of node to non-numerical/too-large vector");
    slogw("Node::setMinSize() cannot set min size of node to non-numerical/too-large vector",
          {
              {"node_guid", uniqueID().toString()},
              {"width", format("%.2f", value->x)},
              {"height", format("%.2f", value->y)},
              {"will_abort_set", FF::ce_no_nan_sizes()},
          });

    if (FF::ce_no_nan_sizes()) {
      return false;
    }
  }

  // First, set the min to the provided value, clamped by the allowed values.
  static const VectorF maxVectorCanvasSize(MAX_CANVAS_SIZE, MAX_CANVAS_SIZE);
  VectorF clampedMin;
  if (FF::ce_no_nan_sizes() && oldSizeIsValid) {
    clampedMin = VectorF::clamp(VectorF(0, 0), *value, maxVectorCanvasSize);
  } else {
    clampedMin = VectorF::max(VectorF(0, 0), *value);
  }

  if (clampedMin.isZero()) {
    set(NodeField::MIN_SIZE, Optional<VectorF>());
    return true;
  }

  set(NodeField::MIN_SIZE, Optional<VectorF>(clampedMin));

  // We now need to check max in both axes to determine whether we should
  // update it to the new min value.
  // It's not valid to have a max smaller than the new min.
  Optional<VectorF> storedMax = maxSize();
  VectorF prospectiveMax;
  if (storedMax) {
    prospectiveMax = *storedMax;
  }

  if (clampedMin.x > maxWidth()) {
    prospectiveMax.x = clampedMin.x;
  }
  if (clampedMin.y > maxHeight()) {
    prospectiveMax.y = clampedMin.y;
  }

  if (storedMax && prospectiveMax != *storedMax) {
    set(NodeField::MAX_SIZE, Optional<VectorF>(prospectiveMax));
  }

  return true;
}

bool Node::setMaxSize(Optional<VectorF> value) {
  if (value == maxSize()) return false;

  if (!value) {
    // We're clearing out this node's max.
    set(NodeField::MAX_SIZE, Optional<VectorF>());
    return true;
  }

  // First, set the max to the provided value, clamped by the allowed values.
  // Max cannot be < 1 in either dimension. Theoretically we could allow sizes 0 < max < 1, but due
  // to the way SelectionTransformer does pixel snapping, resizing a node with a very small max size
  // causes the size to snap to 0, which is bad. So only allow values >= 1.
  VectorF clampedMax = VectorF::max(VectorF{1, 1}, *value);
  if (clampedMax.isInfinity()) {
    set(NodeField::MAX_SIZE, Optional<VectorF>());
    return true;
  }

  set(NodeField::MAX_SIZE, Optional<VectorF>(clampedMax));

  // We now need to check min in both axes to determine whether we should update it to the new max
  // value. It's not valid to have a min larger than the new max.
  Optional<VectorF> storedMin = minSize();
  VectorF prospectiveMin;
  if (storedMin) {
    prospectiveMin = *storedMin;
  }

  if (clampedMax.x < minWidth()) {
    prospectiveMin.x = clampedMax.x;
  }
  if (clampedMax.y < minHeight()) {
    prospectiveMin.y = clampedMax.y;
  }

  if (storedMin && prospectiveMin != *storedMin) {
    set(NodeField::MIN_SIZE, Optional<VectorF>(prospectiveMin));
  }

  return true;
}

// At some point we should consolidate stampSizeIsValid with sizeIsValid. It turns out
// that immutable frame text sublayers can end up being negative-sized in one dimension,
// so we can't assert and abort whenever we attempt to set a negative size.
static bool stampSizeIsValid(Vector size) {
  return size.isFinite() && !size.isNegative() && !size.hasNaN() &&
         Vector::abs(size).min() < MAX_CANVAS_SIZE;
}

void Node::setSize(VectorF value) {
  if (type() == NodeType::STAMP) {
    const bool currentSizeIsValid = stampSizeIsValid(size());
    const bool newSizeIsValid = stampSizeIsValid(value);
    if (currentSizeIsValid && !newSizeIsValid) {
      REPORT_ERROR("Node::setSize() cannot set size of stamp to non-finite/non-positive vector");
      slogw("Node::setSize() cannot set size of stamp to non-finite/non-positive vector",
            {
                {"stamp_guid", uniqueID().toString()},
                {"width", format("%.2f", value.x)},
                {"height", format("%.2f", value.y)},
            });
      return;
    }
    // Group and vectors can be NaN sized, and IF sublayers can be infinite due to "online" setting
    // of size from DIFD that was previously set to infinite. We don't want to log a warning for
    // those cases.
  } else if (!isGroupOrVectorOperation() && !isInImmutableFrame()) {
    const bool currentSizeIsValid = sizeIsValid(size());
    const bool newSizeIsValid = sizeIsValid(value);
    if (currentSizeIsValid && !newSizeIsValid) {
      slogw("Node::setSize() cannot set size of node to non-numerical/too-large vector",
            {
                {"guid", uniqueID().toString()},
                {"width", format("%.2f", value.x)},
                {"height", format("%.2f", value.y)},
                {"will_abort_set", FF::ce_no_nan_sizes()},
            });
      REPORT_ERROR_ONCE(
          "Node::setSize() cannot set size of node to non-numerical/too-large vector");

      if (FF::ce_no_nan_sizes()) {
        return;
      }
    }
  }

  set(NodeField::SIZE, value);
}

