namespace {
void hoverDescendants(NodePtr root, NodePtr node) {
  if (root == node) {
    node->setIsWidgetNodeHovered(true);
  } else if (!node->widgetEvents().empty()) {
    return;
  }

  node->setIsWidgetNodeHovered(true);
  for (auto& child : node->children().asArray()) {
    hoverDescendants(root, child);
  }
}
}  // namespace

void Node::triggerWidgetHover() {
  NodePtr node = self();

  while (node && node->type() != NodeType::WIDGET) {
    if (!node->widgetEvents().empty()) {
      hoverDescendants(node, node);
    }
    node = node->parent();
  }
}

