    setFillPaintData(std::move(paintsToClear));

    // special-case handling to mimic frame behavior when dragging to create
    if (isSection()) {
      if (value) {
        setStrokeWeight(shouldStyleSectionForDesign
                            ? WhiteboardStyle::SECTION_STROKE_WEIGHT_FOR_DESIGN
