void Node::slogErrorForNaNTransform(AffineTransform value) const {
  static bool reportedNaN = false;
  if (reportedNaN) return;

  const bool isGroupOrVectorOperationWithAllNaN = isGroupOrVectorOperation() && value.allNaN();
  const bool hasNaN = value.hasNaN();
  const bool hasInf = value.hasInf();
  const bool hasSameNaNs = relativeTransform().hasSameNaNs(value);

  // No NaNs in the transform, or NaNs are allowed in this case.
  if (isGroupOrVectorOperationWithAllNaN || (!hasNaN && !hasInf) || hasSameNaNs) {
    return;
  }

  reportedNaN = true;

  if (FF::ee_log_nan_transform()) {
    const bool previousHasNaNs = relativeTransform().hasNaN();

    bool ancestorHasNaNs = false;
    for (ConstNodePtr ancestor = parent(); ancestor && ancestor->type() != NodeType::CANVAS;
         ancestor = ancestor->parent()) {
      if (ancestor->relativeTransform().hasNaN()) {
        ancestorHasNaNs = true;
        break;
      }
    }

    bool descendantHasNaNs = false;
    forEachNode(self(), [&descendantHasNaNs](ConstNodePtr node) {
      descendantHasNaNs = node->relativeTransform().hasNaN();
      return descendantHasNaNs ? Loop::BREAK : Loop::CONTINUE;
    });

    ConstNodePtr container = parent();
    const bool isParentStackChild = container && container->isStack();
    const StackLayoutSize parentHorizontalSizing =
        container ? StackLayoutSizeHelper::stackHorizontalSize(container) : StackLayoutSize::FIXED;
    const StackLayoutSize parentVerticalSizing =
        container ? StackLayoutSizeHelper::stackVerticalSize(container) : StackLayoutSize::FIXED;
    const StackMode parentStackMode = container ? container->stackMode() : StackMode::NONE;
    int siblings = container ? container->childCount() : 0;

    sloge(hasNaN ? previousHasNaNs ? "Relative transform contains NaNs"
                                   : "Relative transform contains new NaNs"
                 : "Relative transform contains inf",
          {
              {"nodeType", typeName()},
              {"isGroup", isGroup()},
              {"isStyle", isStyle()},
              {"isInternalOnlyNode", isInternalOnlyNode()},
              {"isTopLevelNode", isTopLevelNode()},
              {"isStateGroup", isStateGroup()},
              {"hasPaintedFill", hasPaintedFill()},
              {"stackMode", Figma::toString(stackMode())},
              {"parentStackMode", Figma::toString(parentStackMode)},
              {"isParentStackChild", isParentStackChild},
              {"isAbsolutePositioned", stackPositioning() == StackPositioning::ABSOLUTE},
              {"horizontalConstraint", Figma::toString(horizontalConstraint())},
              {"verticalConstraint", Figma::toString(verticalConstraint())},
              {"horizontalSizing",
               Figma::toString(StackLayoutSizeHelper::stackHorizontalSize(self()))},
              {"verticalSizing", Figma::toString(StackLayoutSizeHelper::stackVerticalSize(self()))},
              {"parentHorizontalSizing", Figma::toString(parentHorizontalSizing)},
              {"parentVerticalSizing", Figma::toString(parentVerticalSizing)},
              {"ancestorHasNaNs", ancestorHasNaNs},
              {"descendantHasNaNs", descendantHasNaNs},
              {"siblingCount", siblings},
              {"transform", value.toString()},
              {"previousTransform", Figma::toString(relativeTransform())},
              {"size", Figma::toString(size())},
          },
          {.reportAsSentryError = true});
  }
}

void Node::setRelativeTransform(AffineTransform value) {
  slogErrorForNaNTransform(value);

  // Report to Sentry if a connector's vector sublayer's relative transform is being set to a new
  // value that is too close to the old value
  static bool reportedNotDifferentEnough = false;
  if (FF::figjam_log_transform_too_close() && !reportedNotDifferentEnough &&
      // Check that the node is the connector's vector sublayer
      type() == NodeType::VECTOR && parent() && parent()->isConnector() &&
      // Check if the relative transform is not different enough
      !isDifferentEnough(value, relativeTransform())) {
    reportedNotDifferentEnough = true;

    sloge(
        "Setting relative transform on connector vector sublayer when new value is not "
        "different enough from the current value",
        {{"connector ID", parent()->uniqueID()},
         {"old transform", relativeTransform().toString()},
         {"new transform", value.toString()}},
        {.reportAsSentryError = true});
  }

  set(NodeField::RELATIVE_TRANSFORM, AffineTransformF(value));
