// Given an array of indices of moving children in a node, this function prepares it for being set
// as sortedMovingChildIndices/used in a node iterator by:
// - sorting it
// - and correctly appending or prepending absolute positioned indices, for any absolute positioned
// nodes that should render on top.
void Node::setMovingChildIndicesAndManageAbsolutePositionedChildren(
    const Array<int>& indicesUnsorted) {
  // We need to sort movingChildIndices in the setter because the node iterator where we use these
  // for rendering is a much hotter path, so it's preferable that they get sorted ahead of time
  // here.
  Array<int> indices = indicesUnsorted.copy();

  if (stackReverseZIndex()) {
    // For reverse stacking order, iterate over children in order and prepend absolute positioned
    // children, since we render the first nodes on top.
    for (NodePtr child : children()) {
      if (child->stackPositioning() == StackPositioning::ABSOLUTE) {
        indices.emplace_back(indexOfChild(child));
      } else {
        // Stop looking for absolute position children the first time we encounter an
        // auto-positioned children. We can't make interleaved absolute position children
        // render on top.
        break;
      }
    }
  } else {
    // For default stacking order, iterate the opposite way and add nodes to the end, since we
    // render the last nodes on top.
    for (NodePtr child : reversedChildren()) {
      if (child->stackPositioning() == StackPositioning::ABSOLUTE) {
        indices.emplace_back(indexOfChild(child));
      } else {
        break;
      }
    }
  }

  // For reasons that are sort of magic and sort of math, if we sort these numerically, even with
  // absolute positioned nodes added in, it just MAGICALLY WORKS. This is because:
  // - abs pos nodes only get added if they render on top of the stack
  // - when !stackReverseZIndex, that means they have the highest indices, higher than all the
  // moving nodes
  // - when !!stackReverseZIndex, that means they have the lowest indices, lower than all the moving
  // nodes
  // - and when we feed the numerically sorted indices to the ReorderingStackChildIteratorImpl, we
  // append these to the front or back correctly so that when we iterate the right way through the
  // indices, the absolute positioned nodes at the front or back of this array will always appear at
  // the top.
  std::sort(indices.begin(), indices.end());

  set(NodeField::SORTED_MOVING_CHILD_INDICES, ImmutableArray<int>(indices));
}

void Node::resetSortedMovingChildIndices() { set(NodeField::SORTED_MOVING_CHILD_INDICES, {}); }

ImmutableArray<int> Node::sortedMovingChildIndices() const {
  return getCopyOf(NodeField::SORTED_MOVING_CHILD_INDICES).as<ImmutableArray<int>>();
}

// In Auto Layout v3, we're expanding horizontal padding to two fields, which are left and right
// individual padding values (similarly for vertical padding). Under the hood, this works by
// renaming stackHorizontalPadding to stackPaddingLeft. This means that the original setters for
// horizontal and vertical padding no longer exist, and thus get replaced by these shims. We also
// added a new field, stackPaddingRight, through a server side migration.

void Node::setStackHorizontalPadding(float horizontalPadding) {
  // When the flag is disabled, we use the previous code path as if nothing has changed, just
  // setting the left padding (as if it weren't renamed).
  setStackPaddingLeft(horizontalPadding);

  // In order for future code to work correctly with the feature flag enabled, setting the
  // horizontal padding needs to set both the left and right padding.
  setStackPaddingRight(horizontalPadding);
}

void Node::setStackVerticalPadding(float verticalPadding) {
  // When the flag is disabled, we use the previous code path as if nothing has changed, just
  // setting the top padding (as if it weren't renamed).
  setStackPaddingTop(verticalPadding);

  // In order for future code to work correctly with the feature flag enabled, setting the
  // vertical padding needs to set both the top and bottom padding.
  setStackPaddingBottom(verticalPadding);
}

float Node::stackPaddingAlongAxis(Axis axis) const {
  // In v3, now that there is independent padding, stackPaddingAlongAxis becomes ambiguous. In the
  // X direction, it the left or right padding? Or the sum of both? Instead, use
  // stack->stackPaddingTopLeft().component(Axis::X).
  // TODO: https://app.asana.com/0/1186896156571649/1188515727072553
  logWarningToConsole_debugOnly_deprecated(
      "Auto Layout V3",
      "Node::stackPaddingAlongAxis is ambiguous and will be removed. Instead, use "
      "stack->stackPaddingTopLeft().component(Axis::X).");
  return stackPaddingTopLeft().component(axis);
}

