// Finds the primary responsive node (aka breakpoint) of a responsive set
// For now, the primary responsive node is the first child
ConstNodePtr Node::findPrimaryResponsiveNode() const {
  ConstNodePtr responsiveSet = findContainingResponsiveSet();
  if (responsiveSet && responsiveSet->hasChildren()) {
    return responsiveSet->childAtIndex(0);
  }

  return nullptr;
}

// Checks if the containing responsive node is also the the primary responsive node
bool Node::isInPrimaryResponsiveNode() const {
  ConstNodePtr responsiveNode = findContainingResponsiveNode();
  if (!responsiveNode) {
    return false;
  }

  return responsiveNode == findPrimaryResponsiveNode();
}

