void Node::setStyleOverrideTable(const StyleOverrideTable& value) {
  if (type() == NodeType::TEXT) {
    if (auto data = textData()) {
      // We store text data references as Shared<const TextData>,
      // so to modify it we need to explicitly make a copy and set it back.
      auto copy = data->copy();
      copy.dangerouslySetStyleOverrideTable(value.copy());  // copies map of maps
      setTextData(std::move(copy));
      return;
    }
  }

  else if (type() == NodeType::VECTOR || type() == NodeType::HIGHLIGHT) {
    auto data = compiledVectorData();
    if (data->valid()) {
      // VectorData constructor will make a copy of overrides.
      setVectorData(
          VectorData(data->compiledNetwork->toNetwork().copy(), data->normalizedSize, value));
      return;
    }
  }

  logWarningToConsole_debugOnly_deprecated(
      "Node", "ignoring setStyleOverrideTable() on node with missing properties");
}

