String Node::stateAbbreviatedName() const {
  if (!isState()) return "";

  auto stateGroup = findContainingStateGroupOrSelf();
  if (!stateGroup) return "";

  auto model = stateGroup->stateGroupModel(StateGroupModelCachePolicy::REGENERATE_IF_DIRTY);
  if (!model) return "";

  const auto* stateModel = model->getStateModel(_uniqueID);
  if (!stateModel) return "";

  return stateModel->abbreviatedName();
}

