static const String TEMPLATE_DESCRIPTION_MAGIC = "Figma.TemporaryIsTemplate";
static const String GROUP_TEMPLATE_DESCRIPTION_MAGIC = "Figma.TemporaryIsTemplateGroup";

bool Node::isTemplate() const {
  return FF::dse_templates_proto() && isSymbol() &&
         description().startsWith(TEMPLATE_DESCRIPTION_MAGIC);
}

bool Node::isTemplateGroup() const {
  return FF::dse_templates_proto() && isSymbol() &&
         description() == GROUP_TEMPLATE_DESCRIPTION_MAGIC;
}

void Node::setTemplateType(TemplateType templateType) {
  if (!FF::dse_templates_proto()) {
    return;
  }
  if (templateType == TemplateType::DEFAULT && isSymbol()) {
    setDescription(TEMPLATE_DESCRIPTION_MAGIC);
  } else if (templateType == TemplateType::GROUPED && isSymbol()) {
    setDescription(GROUP_TEMPLATE_DESCRIPTION_MAGIC);
  } else {
    setDescription("");
  }
}

NodePtr Node::nodeWithEditMode() const {
  NodePtr node = findLocalNodeOrNull(editMode().nodeUniqueID);

  NodePtr currentSelection = selection().singleNode(scene());
  NodePtr immutableFrame = node ? node->findContainingImmutableFrame() : nullptr;
  NodePtr tableCell = node ? node->findContainingTableCell() : nullptr;

  // See: https://app.asana.com/0/1200138314437429/1200176986425063/f
  if (editMode().type == EditModeType::TEXT && node && node->type() == NodeType::TEXT &&
      currentSelection && node != currentSelection) {
    if (currentSelection == immutableFrame || currentSelection == tableCell) {
      sloge(
          "Containing immutable frame or table cell of node with edit mode is selected instead "
          "of node",
          {{"node", node->toString()},
           {"immutableFrame", immutableFrame ? immutableFrame->toString() : "null"},
           {"tableCell", tableCell ? tableCell->toString() : "null"}});
    }
  }

  // TODO(jessietea): Remove this once we make sure we always have the immutable sublayer selected
  // instead of the parent IF. (Currently) For immutable frames, we could be selected a parent
  // node and entering edit mode into a child node (especially for text), so allow that special
  // case.
  return (node == currentSelection || immutableFrame == currentSelection ||
          tableCell == currentSelection)
             ? node
             : nullptr;
}

bool Node::hasPaintedFill() const {
  // caller checks isVisible and opacity() on object via isRendered()
  //
  //  the more checks thrown in here, the less applicable it is elsewhere
  //  this is mostly for rendering and export to skip fills/strokes
  //  for example, the geometry checks means we can't call this to update the geometry cache

  // TODO: Right now hasPaintedFill is incorrectly false for a transparent object that has a fill
  // with a non-transparent style override
  //  what has style override - just text ?, could make some letters visible, but not others ?

  if (isLineLike(type())) return false;

  if (!fillPaintData()->containsEnabledVisiblePaint()) return false;

  if (type() == NodeType::TEXT) {
    if (Text::hasPlaceholder(self())) return false;
    return !derivedTextData()->laidGlyphs().empty();
  }

  // the presence of fill geometry isn't enough, fills are uses as masks on inside/outside stroke
  if (supportsGeometryCache()) {
    const auto& network = geometryCache().network(GeometryType::FILL);
    if (hasNoRegions(network)) return false;
  }

  return true;
}

bool Node::hasPaintedStroke() const {
  // see notes in hasPaintedFill()

  if (strokeWeight() <= 0) return false;

  if (!strokePaintData()->containsEnabledVisiblePaint()) return false;

  // This could be made faster for NodeType::TEXT nodes

  if (supportsGeometryCache()) {
    const auto& network = geometryCache().network(GeometryType::STROKE);
    if (hasNoRegions(network)) return false;
  }

  return true;
}

bool Node::hasPaintedFillOrStroke() const { return hasPaintedFill() || hasPaintedStroke(); }

// Side effect: may allocate and store GeometryCache.
GeometryCache& Node::geometryCache() {
  static GeometryCache empty(nullptr);

  if (!isValid()) {
    // While we shouldn't be accessing geometryCache on null-nodes, a REPORT_ERROR
    // should already be fired at this point by virtue of access an invalid NodePtr.
    return empty;
  }

  if (!supportsGeometryCache()) {
    // The undo system request the geometry cache before a type has even been set,
    // so NodeType::NONE requests the cache.
    // FIXME: From the perspective of this function, this is pretty dangerous --
    // anybody that writes to this mutable object will change all "empty" values.
    return empty;
  }

  Owned<GeometryCache>& geometryCache = _geometryCache;
  if (!geometryCache) {
    geometryCache.reset(new GeometryCache(self()));
  }
  // Unlike all other values, this value is just mutated in place
  return *geometryCache;
}

// This is actually where the lazy geometry cache initialization happens,
// even on "const" object's first access to geometryCache().
const GeometryCache& Node::geometryCache() const {
  return self().irresponsiblyCastAwayConst()->geometryCache();
}

void Node::invalidateGeometryCacheForSelf() {
  if (supportsGeometryCache()) {
    geometryCache().invalidateNetworks();
  }
}

void Node::invalidateGeometryCacheForSelfAndParents() {
  if (!scene()) return;

  invalidateGeometryCacheForSelf();
  if (NodePtr p = parent()) {
    p->invalidateGeometryCacheOnParentChain();
  }
}

void Node::invalidateGeometryCacheOnParentChain() {
  for (NodePtr node = self(); node; node = node->parent()) {
    REPORT_IF_FALSE_DEBUG_ONLY(isNodeTypeWithChildren(node->type()));

    // We don't need to invalidate framelike ancestor geometries, who are not affected by
    // descendant geometry — the ONLY nodes with children and with geometries that depend on
    // descendants are boolean op nodes. (Invalidating framelike geometries would in theory be
    // cheap, but in some cases (like individual borders + dashed strokes + rounded corners, where
    // we need to use boolean ops) can be quite expensive.)
    bool shouldInvalidate = node->type() == NodeType::VECTOR_OPERATION;
    if (shouldInvalidate) {
      node->invalidateGeometryCacheForSelf();
    }
  }
}

void Node::setCachedBooleanOp(const RefPtr<const CachedBooleanOp>& cachedOp) const {
  cachedBooleanOpForNode().insert_or_assign(_fields.dkey(), cachedOp);
}

void Node::setPathForSpread(const RenderTree::UniquePathPtr& path) const {
  pathForSpreadForNode().insert_or_assign(_fields.dkey(), path);
}

const StyleOverrideTable& Node::styleOverrideTable() const {
  if (type() == NodeType::TEXT) {
    if (const auto& data = textData()) {
      return data->styleOverrideTable();
    }
  }

  else if (type() == NodeType::VECTOR || type() == NodeType::HIGHLIGHT) {
    auto data = compiledVectorData();
    if (data->valid()) {
      return data->styleOverrideTable;
    }
  }

  // this gets called a lot now, and there's no test for hasStyleOverrideTable, it's const anyways
  // logWarningToConsole_debugOnly_deprecated("Node", "ignoring styleOverrideTable() on node with
  // missing properties");
  return emptyValue<StyleOverrideTable>();
}

GUID Node::overrideKey() const {
  if (_cachedIsImmutableFrameSublayer) {
    // NOTE(jessietea): We have switched to using immutableFrameSublayerPath for immutable frames
    // but *just* in case we forgot any spots where we check overrideKey, here's a fix. Hopefully
    // this can be removed soon.
    slogw(
        "Using overrideKey on an immutable frame is deprecated, please switch to "
        "immutableFrameSublayerPath");
    OverridePath path = immutableFrameSublayerPath();
    return path.size() >= 2 ? path.guids()[1] : GUID();
  }
  GUID stored = fields().overrideKey();
  return stored.isValid() ? stored : uniqueID();
}

void Node::setOverrideKey(GUID key) {
  if (_cachedIsImmutableFrameSublayer) {
    REPORT_ERROR("setOverrideKey should no longer be called on IF sublayers");
  }

  set(NodeField::OVERRIDE_KEY, key);
}

bool Node::hasExplicitOverrideKey() const {
  return hasNodeField(NodeField::OVERRIDE_KEY) && fields().overrideKey().isValid();
}

void Node::setSelection(Selection value, bool isUserTriggered) {
#if BUILD_TEST
  for (const NodePtr& selected : value.unorderedNodes(scene())) {
    if (!isEqualToOrAncestorOf(selected)) {
      sloge_debugOnly_previously_loge("selection contains node outside the canvas!");
    }
  }
#endif

  // Use a local scope to set the global variable lastSelectionWasUserTriggered.
  // This value can then be read by any observer via handleSceneGraphEvent. Ideally, we could
  // thread this value through the call stack, but that would require changes to the schema
  // for multiplayer events, which is not worth it for this one-off use case.
  BoolScope isUserTriggeredScope(SceneGraph::lastSelectionWasUserTriggered, isUserTriggered);

  set(NodeField::SELECTION, std::move(value));
}

bool Node::isFieldInUndoRedoBuffer(NodeField field) const {
  if (tracking() == NodeTracking::IGNORE) {
    // NOTE(jessietea): Untracked nodes generally don't store data in the undo/redo buffer since
    // they are either impermanent (ghost nodes) or sublayers that get updated by their parent
    // tracked nodes (immutable frames and instances). There is some local state though that is tied
    // directly to a node and cannot be recomputed from other fields or the parent node. As of now,
    // this is just the text insertion style. In many cases this *could* be recalculated from other
    // fields, but there are certain user text editing actions that only update the insertion style
    // until the user types.
    return field == NodeField::TEXT_INSERTION_STYLE;
  }
  return field != NodeField::IS_EXPANDABLE && field != NodeField::IS_EXPANDED &&
         field != NodeField::IS_TEMPORARILY_EXPANDED && field != NodeField::LOCAL_OPACITY &&
         field != NodeField::TEXT_LAYOUT_DATA && field != NodeField::VECTOR_EDITOR_DATA &&

         // These aren't in the undo/redo buffer because they are lazily updated by
         // multiplayer and so likely won't be filled in before an undo commit is done
         field != NodeField::FILL_GEOMETRY && field != NodeField::STROKE_GEOMETRY &&
         field != NodeField::DERIVED_SYMBOL_DATA &&

         // Since this is derived from an IF's NODE_GENERATION_DATA (and possibly SIZE) and
         // can be recomputed based on multiplayer conflicts/remote changes, it doesn't make
         // sense to store it in the undo/redo buffer. We'll either undo the actual source of truth
         // values and re-layout or we'll leave the DIFD alone
         field != NodeField::DERIVED_IMMUTABLE_FRAME_DATA &&

         // This is a field automatically set after running client-side migrations.
         // The migrations should not be undo-able, and neither should be this indicator
         // of migration status.
         field != NodeField::CLIENT_MIGRATION_STATUS &&

         // This is a cache for whiteboard tables derived from other table-related node fields, so
         // it shouldn't be tracked by undo/redo
         field != NodeField::TABLE_CACHE &&

         // this is calculated from other properties and will be recomputed on undo/redo.
         // (also it gets animated!)
         field != NodeField::TABLE_SPAN_DISPLACEMENTS &&

         // this is similarly transient UI state and undo shouldn't mess with it.
         field != NodeField::TABLE_FLOATING_SPANS &&

         // These widget fields never be tracked by undo redo.
         field != NodeField::RENDERED_SYNCED_STATE && field != NodeField::IS_WIDGET_NODE_HOVERED &&

         // These fields are related to animations, and should only be a part of intermediate states
         // while animations are playing.
         field != NodeField::OVERLAY_TRANSFORM &&
         field != NodeField::SHOULD_RENDER_ON_INDEPENDENT_LAYER &&

         // This is a cache for diagramming layout and should not be tracked by undo/redo
         field != NodeField::LAYOUT_RULE &&

         (
             // We can discard fields on primary instances inherited from Symbols
             // because they can be re-created from the Symbol or the instance overrides.
             // This isn't just an optimization, it is also required for the correct
             // handling of these fields in undo/redo.
             (!isInstance() || !fieldIsInheritedFromSymbols(field, isPrimaryInstance()))

             // If this is a `size` change on a primary instance, we still need to track
             // this field! That's because `size` is different from most other fields that could
             // be stored on an instance. We don't need to hold onto the values for any other
             // inherited field on an instance because they can be inferred entirely correctly
             // during `SymbolUpdater`.
             //
             // The same is actually technically true for `size` -- it is inferred from: the
             // symbol size, the `size` override stored on the instance, and the size of the
             // contents of the instance if auto-layout is enabled. However, `relativeTransform`
             // is inferred partially based on the initial `size` of the primary instance during
             // `SymbolUpdater`!
             //
             // To see the code that uses the original `size` to infer the `relativeTransform` of
             // a primary instance, see the comment around `originalSize` and
             // `updateTransformForStackResize` in `SymbolUpdater::updateInstance()`.
             //
             // https://app.asana.com/0/1130342712535404/1152658385656339
             || (isStack() && field == NodeField::SIZE && isPrimaryInstance()));
}

bool Node::shouldAlwaysSnapToPixelGrid() const { return isStackOrFixedSizeContainer(); }

bool Node::containerSupportsFillStrokeAndCorners() const {
  switch (type()) {
    case NodeType::FRAME:
    case NodeType::SYMBOL:
    case NodeType::INSTANCE:
    case NodeType::STICKY:
    case NodeType::SHAPE_WITH_TEXT:
    case NodeType::CONNECTOR:
    case NodeType::MODULE:
    case NodeType::SLIDE_GRID:
    case NodeType::SLIDE_ROW:
    case NodeType::SLIDE:
    case NodeType::TABLE:
    case NodeType::TABLE_CELL:
    case NodeType::SECTION:
      return true;

    default:
      return false;
  }
}

NodeTracking Node::tracking() const { return _tracking; }

AffineTransform Node::absoluteTransform() const {
  if (!isValid()) return {};

  _updateAbsoluteInformationIfNecessary();
  return _cachedAbsoluteTransform;
}

Rect Node::absoluteTextBounds() const {
  if (!isValid()) return {};

  REPORT_IF_FALSE_DEBUG_ONLY(type() == NodeType::TEXT);
  _updateAbsoluteInformationIfNecessary();

  const auto& map = absoluteTextBoundsForNode();
  if (auto it = map.find(_fields.dkey()); it != map.end()) {
    return it->second;
  }

  return {};
}

Rect Node::absoluteMeasurementBounds() const {
  if (!isValid()) return {};

  _updateAbsoluteInformationIfNecessary();

  if (type() == NodeType::LINE) {
    // Note: Nodes of type LINE only have a width and no height regardless of whether there is a
    // stroke weight. However, measurement lines should take the stroke weight into account and be
    // drawn from the center of the LINE for better symmetry. The following uses trig (as we must
    // take rotations into account) to translate the absoluteBounds such that it is centered
    // within the LINE.
    double angle = absoluteTransform().toAngle();
    float hypothenuse = strokeWeight() / 2;
    const Vector scale(hypothenuse, hypothenuse);
    const Vector translation = -((Vector::fromAngle(angle) * scale).rotate90());
    return absoluteBounds().offsetBy(translation);
  }

  return absoluteBounds();
}

Rect Node::absoluteBounds() const {
  if (!isValid()) return {};

  _updateAbsoluteInformationIfNecessary();
  return absoluteCornerPoints().bounds();
}

Rect Node::absoluteBoundsWithStroke() const {
  if (!isValid()) return {};

  _updateAbsoluteInformationIfNecessary();

  float weight = strokeWeight();
  StrokeAlign align = strokeAlign();
  if (!(weight > 0) || align == StrokeAlign::INSIDE ||
      !strokePaintData()->containsEnabledVisiblePaint()) {
    // If there's no visible stroke outside the shape's normal bounds. Just return those bounds
    return absoluteBounds();
  }

  // We could return geometryCache().transformedBounds() here. But I'm worried that that
  // would generate geometry in cases where we would otherwise not need it, which would
  // be really expensive. Possibly we could check if the data exists and is up-to-date
  // and if so use it, but that seems unnecessarily complicated for now.

  if (align == StrokeAlign::CENTER) {
    // For center-stroke, only half of the stroke goes outside the bounding box
    weight /= 2.0;
  }

  // Technically we could use 1 as miterLimit for nodes that use something other than miter as
  // their strokeJoin. However that gets complicated since strokeJoin can be overridden for
  // individual vertices on a VECTOR node. It's also more fields to look up which could affect
  // performance
  float miterLimit =
      hasNodeField(NodeField::STROKE_MITER_LIMIT) ? strokeMiterLimit() : defaultMiterLimit;

  return absoluteBounds().expand(weight * miterLimit);
}

Corners Node::absoluteCornerPoints() const {
  if (!isValid()) return {};

  _updateAbsoluteInformationIfNecessary();
  return Corners(_cachedAbsoluteTransform, size());
}

bool Node::isLockedInCanvas() const {
  for (ConstNodePtr node = self(); node; node = node->parent()) {
    if (node->locked()) {
      return true;
    }
  }
  return false;
}

bool Node::isVisibleInCanvas(IncludeInternalOnlyCanvas includeInternalOnlyCanvas) const {
  for (ConstNodePtr node = self(); node; node = node->parent()) {
    if (!node->isVisible() && !(includeInternalOnlyCanvas == IncludeInternalOnlyCanvas::YES &&
                                node->isInternalOnlyCanvas())) {
      return false;
    }
  }
  return true;
}

bool Node::isVisibleInInstance() const {
  if (!isInstanceSublayer()) {
    return false;
  }

  for (ConstNodePtr node = self(); !node->isPrimaryInstance(); node = node->parent()) {
    if (!node->isVisible()) {
      return false;
    }
  }
  return true;
}

bool Node::isHighestLockedNodeInHierarchy() const {
  if (!locked()) {
    return false;
  }
  for (ConstNodePtr node = parent(); node; node = node->parent()) {
    if (node->locked()) {
      return false;
    }
  }
  return true;
}

bool Node::isAccessibilityAnnotatable() const {
  if (isStateGroup()) {
    return false;
  }
  if (type() == NodeType::FRAME) {
    return !isGroup();
  }
  if (isSymbol()) {
    return true;
  }
  if (type() == NodeType::SLICE || type() == NodeType::SECTION) {
    return false;
  }
  const auto p = parent();
  // disallow for subpart of a boolean op
  if (p && p->type() == NodeType::VECTOR_OPERATION) {
    return false;
  }
  return true;
}

bool Node::canHaveBehaviors() const {
  if (isResponsiveSet()) {
    return false;
  }
  if (isStateGroup()) {
    return false;
  }
  if (type() == NodeType::FRAME) {
    return !isGroup();
  }
  if (isSymbol()) {
    return true;
  }
  if (type() == NodeType::SLICE || type() == NodeType::SECTION) {
    return false;
  }
  const auto p = parent();
  // disallow for subpart of a boolean op
  if (p && p->type() == NodeType::VECTOR_OPERATION) {
    return false;
  }
  return true;
}

int Node::indexOfChild(ConstNodePtr child) const { return indexOf(_cachedChildren, child); }

ConstNodePtr Node::childAtIndex(int index) const {
  const auto& cachedChildren = _cachedChildren;
  if (index < 0 || index >= static_cast<int>(cachedChildren.size())) {
    REPORT_ERROR("child index out of range");
    return nullptr;
  }

  ConstNodePtr child = cachedChildren[index];
  ReadonlyHooksHack().incrementPerfNodeTypeHistogram(child->type());
  return child;
}

NodePtr Node::childAtIndex(int index) {
  const Node* constThis = this;
  return constThis->childAtIndex(index).irresponsiblyCastAwayConst();
}

int Node::visibleChildCount() const {
  int count = 0;
  for (ConstNodePtr child : _cachedChildren) {
    if (child->isVisible()) {
      count++;
    }
  }
  return count;
}

NodePtr Node::firstVisibleChild() {
  if (_cachedChildren.empty()) {
    return nullptr;
  }
  for (auto child : _cachedChildren) {
    if (child->isVisible()) {
      return child;
    }
  }
  return nullptr;
}

NodePtr Node::lastVisibleChild() {
  if (_cachedChildren.empty()) {
    return nullptr;
  }
  for (auto it = _cachedChildren.rbegin(); it < _cachedChildren.rend(); it++) {
    if ((*it)->isVisible()) {
      return (*it);
    }
  }
  return nullptr;
}

NodePtr Node::childAtPath(const Array<int>& path) {
  NodePtr cursor = self();
  for (int idx : path) {
    if (idx < 0 || idx >= cursor->childCount()) {
      return nullptr;
    }
    cursor = cursor->childAtIndex(idx);
  }
  return cursor;
}

void Node::prependChild(NodePtr child) { insertChild(0, child); }

void Node::appendChild(NodePtr child) { insertChild(childCount(), child); }

void Node::appendChild(NodePtr child, ScrollBehavior behavior) {
  insertChild(childCount(), child, behavior);
}

void Node::appendChildWithPosition(NodePtr child, const Position& position) {
  if (!isValid()) return;
  REPORT_IF_FALSE_DEBUG_ONLY(child->scene() == scene());
  REPORT_IF_FALSE_DEBUG_ONLY(child != self() && !child->isAncestorOf(self()));
  REPORT_IF_FALSE_DEBUG_ONLY(childCount() == 0 ||
                             position > childAtIndex(childCount() - 1)->parentIndex().position);
  child->changeParentIndex(ParentIndex(uniqueID(), position), NodeOrigin::LOCAL,
                           scene()->sessionID());
}

void Node::transferChild(int index, NodePtr child, ScrollBehavior behavior) {
  scene()->transferChild(self(), index, child, behavior);
}

void Node::insertChild(int index, NodePtr child, ScrollBehavior behavior) {
  if (!isValid()) return;

  scene()->insertChild(self(), index, child, behavior);
}

NodePtr Node::removeChildWithoutUnregistering(NodePtr child) {
  return removeChildWithoutUnregistering(indexOfChild(child));
}

NodePtr Node::removeChildWithoutUnregistering(int index) {
  return scene()->removeChildWithoutUnregistering(self(), index);
}

void Node::removeAndUnregisterSelfAndChildTree(const char* reason, bool forceRemove) {
  if (!isValid()) {
    return;
  }

  if (type() == NodeType::SLIDE_GRID) {
    // SlideGrids cannot be removed
    return;
  }

  if (NodePtr container = parent()) {
    if (container->isSlideRow() && container->childCount() == 1) {
      container->removeAndUnregisterSelfAndChildTree(reason, forceRemove);
      return;
    }
  }

  NodePtr containingPage = findContainingCanvas();
  MultiplayerMapHandoffStatus handoffStatusMap;
  if (containingPage && containingPage->handoffStatusMap()) {
    handoffStatusMap = containingPage->handoffStatusMap()->map().copy();
  }
  bool updatedMap = false;

  // This is static to avoid re-allocating memory everytime we need to build
  // the array. The function must not call itself as a result.
  // FIXME: We should use a pool of Array<NodePtr> in a bunch of places like this
  // so this memory doesn't "leak".
  static Array<NodePtr> nodesToUnregister;
  nodesToUnregister.clear();

  // Stores nodes that we do not want to unregister.
  static Array<NodePtr> nodesToSoftDelete;
  nodesToSoftDelete.clear();

  // Unregister all the hard deleted nodes so we can re-use their ids.
  // Also, record all nodes to be soft deleted.
  forEachNode(self(), [forceRemove, &handoffStatusMap, &updatedMap](NodePtr node) {
    if (handoffStatusMap.has(node->uniqueID())) {
      // Important: Set "no status" rather than remove from the map completely.
      // We want to record the information the status was removed. This information is important
      // when merging branches. (See MultiplayerMapDiff::applyDiff where we merge the maps.)
      handoffStatusMap.set(node->uniqueID(), NodeStatus::NONE);
      updatedMap = true;
    }

    if (!forceRemove && node->scene()->shouldSoftDeleteNode(node)) {
      nodesToSoftDelete.push_back(node);
      // If we find a node to soft delete, do not continue
      // traversing its children, as doing so will hard delete them.
      return Loop::SKIP;
    }

    nodesToUnregister.push_back(node);
    return Loop::CONTINUE;
  });

  for (auto node : nodesToSoftDelete) {
    // Soft delete nodes here to hide them.
    _scene->softDeleteNode(node);
  }

  // If we are unregistering the root, (as opposed to soft-deleting it), then remove
  // the root first so we don't generate nested events as we unregister the children.
  if (!nodesToUnregister.empty()) {
    REPORT_IF_FALSE(nodesToUnregister.front() == self());
    if (NodePtr par = parent()) {
      par->removeChildWithoutUnregistering(self());
    }
  }

  if (containingPage && updatedMap) {
    containingPage->setHandoffStatusMap(
        makeShared<MultiplayerMapHandoffStatus>(std::move(handoffStatusMap)));
  }

  for (auto node : nodesToUnregister) {
    node->unregisterFromScene(reason);
  }
}

void Node::unregisterFromScene(const char* reason, int callCount) {
  if (isValid() && _scene) {
    _scene->unregisterNodeInternal(self(), NodeOrigin::LOCAL, _scene->sessionID(), reason,
                                   callCount);
  }
}

void Node::unregisterRemoteFromScene(int remoteSessionID) {
  static int callCount = 0;
  ++callCount;
  if (isValid() && _scene) {
    // Multiplayer does not track instance sublayers or immutable frame sublayers so when a remote
    // instance or immutable frame  is removed from the scene in response to a multiplayer
    // message, we must manually remove its sublayers, otherwise they will persist in memory.
    if (isInstance() || isImmutableFrame()) {
      // We can't just call this->removeAndUnregisterSelfAndChildTree because
      // the current node is remote (not local) and needs NodeOrigin::REMOTE and
      // the appropriate remoteSessionID
      // We create a copy here because when the child is removed from the scene,
      // it will be removed from _cachedChildren. So if we just iterated over
      // _cachedChildren directly, the loop would access memory out of bounds
      Array<NodePtr> children = _cachedChildren.copy();
      for (const NodePtr& child : children) {
        forEachNode(child, [&](NodePtr node) {
          if (node->isValid()) {
            _scene->unregisterNodeInternal(node, NodeOrigin::REMOTE, remoteSessionID,
                                           "Node::unregisterRemoteFromScene/loop", callCount);
          }
          return Loop::CONTINUE;
        });
      }
    }

    _scene->unregisterNodeInternal(self(), NodeOrigin::REMOTE, remoteSessionID,
                                   "Node::unregisterRemoteFromScene/end", callCount);
  }
}

bool Node::backgroundOrFillVisible(IncludeGroups includeGroups) const {
  if (supportsFillPaintData(includeGroups)) {
    return fillPaintData()->containsAtLeastOneVisiblePaint();
  } else {
    return backgroundEnabled() && backgroundOpacity() > 0;
  }
}

ColorF Node::backgroundColorToRender(
    EnforceExportBackgroundSettings enforceExportBackgroundSettings) const {
  if (!isExportBackground() &&
      enforceExportBackgroundSettings == EnforceExportBackgroundSettings::YES) {
    return ColorF::createTransparent();
  }

  if (!backgroundEnabled()) {
    return ColorF::createTransparent();
  }

  return backgroundColor().multiplyAlpha(backgroundOpacity());
}

AffineTransform Node::computeAbsoluteTransform(ConstNodePtr node, ConstNodePtr parent) {
  // Only call this function if you are certain this is a valid node.
  REPORT_IF_FALSE_DEBUG_ONLY(node->isValid());

  if (node->type() == NodeType::GROUP_DEPRECATED && node->childCount() == 0) {
    // For groups and vector operations this is more complicated. The
    // data->cachedAbsoluteTransform is the transform of the node content.
    return AffineTransform(Vector::invalid(), Vector::invalid(), Vector::invalid());
  }

  AffineTransform transform;
  if (parent) {
    if (!parent->absoluteTransform().hasNaN()) {
      transform = parent->absoluteTransform();
    }
  }
  transform.multiply(node->overlayTransform());
  transform.multiply(node->relativeTransform());
  return transform;
}

Rect Node::computeAbsoluteTextBounds(const AffineTransform& absoluteTransform,
                                     const Text::DerivedTextData* derivedTextData,
                                     ConstNodePtr node) {
  Rect bounds(node->size());
  for (const auto& baseline : derivedTextData->baselines()) {
    if (!baseline.lineY || !baseline.lineHeight) {
      continue;
    }

    bounds.size.x = std::fmax(bounds.size.x, baseline.position.x + baseline.width);
    bounds.size.y = std::fmax(bounds.size.y, *baseline.lineY + *baseline.lineHeight);
  }

  return absoluteTransform.transformRect(bounds);
}

void Node::_updateAbsoluteInformationIfNecessary() const {
  // Only call this function if you are certain this is a valid node.
  REPORT_IF_FALSE_DEBUG_ONLY(isValid());

  if (_isAbsoluteTransformDirty) {
    _isAbsoluteTransformDirty = false;
    AffineTransformF newTransform = computeAbsoluteTransform(self(), parent());
    Optional<Vector> delta;
    if (_geometryCache && _cachedAbsoluteTransform.isTranslateOnly(newTransform)) {
      delta = Vector(newTransform.m02 - _cachedAbsoluteTransform.m02,
                     newTransform.m12 - _cachedAbsoluteTransform.m12);
    }
    _cachedAbsoluteTransform = newTransform;
    if (_geometryCache) {
      _geometryCache->nodeAbsoluteTransformChanged(delta);
    }
  }

  if (type() == NodeType::TEXT && _isAbsoluteTextBoundsDirty) {
    _isAbsoluteTextBoundsDirty = false;
    if (auto textData = Node::derivedTextData()) {
      absoluteTextBoundsForNode().insert_or_assign(
          _fields.dkey(),
          computeAbsoluteTextBounds(_cachedAbsoluteTransform, textData.get(), self()));
    }
  }
}

void Node::_invalidateAbsoluteTransformsOfSelf() {
  _isAbsoluteTransformDirty = true;
  _isAbsoluteTextBoundsDirty = true;
}

void Node::_invalidateAbsoluteTransformsOfSelfAndChildren() {
  if (_scene->isLoading()) {
    // We shouldn't need to invalidate absolute bounds during scene load because
    // this cache starts out dirty and should never be accessed until after the scene is
    // initialized.
    return;
  }

  forEachNode(self(), [](NodePtr node) {
    node->_invalidateAbsoluteTransformsOfSelf();
    return Loop::CONTINUE;
  });
}

void Node::_invalidateDiagramCanvasSpaceBounds() {
  if (isDiagramNode()) {
    if (NodePtr root = diagramRoot()) {
      if (Shared<AssistedLayoutRule> rule = root->layoutRule()) {
        rule->invalidateCanvasSpaceBounds();
      }
    }
  }
}

void Node::_linkToParentIndex() {
  REPORT_IF_FALSE_DEBUG_ONLY(isValid());
  // This method shouldn't be called if this isn't true
  REPORT_IF_FALSE_DEBUG_ONLY(_cachedParent == nullptr);
  REPORT_IF_TRUE_DEBUG_ONLY(_isPartOfLoop);

  // Link the node bi-directionally with its parent
  if (NodePtr parentNode = _scene->nodeByIdOrNull(parentIndex().guid)) {
    _cachedParent = parentNode->self();
    auto& parentChildren = parentNode->_cachedChildren;
    auto it = std::lower_bound(parentChildren.begin(), parentChildren.end(), self(),
                               _areNodesOrderedCorrectly);
    int index = std::distance(parentChildren.begin(), it);
    parentChildren.insert(it, self());
    _updateDescendsFromRoot(parentNode->descendsFromRoot());
    _invalidateAbsoluteTransformsOfSelfAndChildren();

#if BUILD_DEBUG
    // Check that children are sorted after insertion.
    for (size_t i = 1; i < parentChildren.size(); i++) {
      REPORT_IF_FALSE_DEBUG_ONLY(
          _areNodesOrderedCorrectly(parentChildren[i - 1], parentChildren[i]));
    }
#endif

    if (hasWeakHandle() && scene()->hasViewerShim()) {
      DeprecatedJsSceneHooks::linkNodeToParent(getOrCreateWeakHandle(),
                                               parentNode->getOrCreateWeakHandle(), index);
    }

    parentNode->updateHasInstanceDescendant();
    parentNode->updateHasBubbledInstanceDescendant();
    parentNode->updateHasVariantBoundDescendant();
  }
}

void Node::_unlinkFromParentIndex() {
  if (NodePtr parentNode = parent()) {
    // Unlink the node bi-directionally with its parent
    REPORT_IF_FALSE_DEBUG_ONLY(parentNode->uniqueID() == parentIndex().guid);
    parentNode->_cachedChildren.removeByValue(self());

    _cachedParent = nullptr;
    _updateDescendsFromRoot(false);

    parentNode->updateHasInstanceDescendant();
    parentNode->updateHasBubbledInstanceDescendant();
    parentNode->updateHasVariantBoundDescendant();
  }

  if (hasWeakHandle() && scene()->hasViewerShim()) {
    DeprecatedJsSceneHooks::unlinkNodeFromParent(getOrCreateWeakHandle());
  }
}

bool Node::_checkIsPartOfLoop() const {
  SceneGraph* scn = scene();
  if (!scn) return false;

  // Invariant: isPartOfLoop false before calling this function.
  REPORT_IF_TRUE_DEBUG_ONLY(_isPartOfLoop);

  for (NodePtr cursor = scn->nodeByIdOrNull(parentIndex().guid); cursor;
       cursor = scn->nodeByIdOrNull(cursor->parentIndex().guid)) {
    if (cursor == self()) {
      return true;
    }
    if (cursor->_isPartOfLoop) {
      // If going up the parent chain, and you find something in a loop already,
      // then you know you can never get back to yourself (you'll be stuck in
      // that other loop forever, and we already checked we aren't in a loop now).
      break;
    }
  }
  return false;
}

void Node::_updateDescendsFromRoot(bool value) {
  REPORT_IF_FALSE_DEBUG_ONLY(self() != scene()->root() &&
                             (value ? _cachedParent && _cachedParent->descendsFromRoot()
                                    : !_cachedParent || !_cachedParent->descendsFromRoot()));

  if (_cachedDescendsFromRoot == value) {
    return;
  }

  _cachedDescendsFromRoot = value;

  for (int i = 0; i < childCount(); i++) {
    childAtIndex(i)->_updateDescendsFromRoot(value);
  }
}

void Node::updateHasInstanceDescendant() {
  // Traverse up the ancestor chain to recompute whether each ancestor
  // has instance descendants, starting at the current node.
  NodePtr currentNode = self();
  bool newValue = false;

  while (currentNode && currentNode->type() != NodeType::CANVAS) {
    if (!newValue) {
      for (const NodePtr& child : currentNode->children()) {
        if (child->hasInstanceDescendant() || child->isInstance()) {
          newValue = true;
          break;
        }
      }
    }

    if (newValue == currentNode->hasInstanceDescendant()) {
      // Since the value hasn't changed, the values in the ancestor chain shouldn't be affected.
      break;
    } else {
      currentNode->_hasInstanceDescendant = newValue;
      currentNode = currentNode->parent();
    }
  }
}

void Node::updateHasBubbledInstanceDescendant() {
  // Traverse up the ancestor chain to recompute whether each ancestor
  // has bubbled instance descendants, starting at the current node.
  NodePtr currentNode = self();
  bool newValue = false;

  while (currentNode && (currentNode->isOrInProductComponent() || currentNode->isOrInInstance() ||
                         currentNode->isOrInModule())) {
    if (!newValue) {
      for (const NodePtr& child : currentNode->children()) {
        if (child->hasBubbledInstanceDescendant() || child->propsAreBubbled()) {
          newValue = true;
          break;
        }
      }
    }

    if (newValue == currentNode->hasBubbledInstanceDescendant()) {
      // Since the value hasn't changed and the current node isn't bubbled itself,
      // the values in the ancestor chain shouldn't be affected.
      break;
    } else {
      currentNode->_hasBubbledInstanceDescendant = newValue;
      currentNode = currentNode->parent();
    }
  }
}

void Node::updateHasVariantBoundDescendant() {
  if (!FF::ds_dirty_all_states_in_group()) {
    return;
  }

  // Traverse up the ancestor chain to recompute whether each ancestor
  // has variant bound descendants, starting at the current node.
  NodePtr currentNode = self();
  bool newValue = false;

  while (currentNode && currentNode->isOrInSymbol() && !currentNode->isOrInInstance()) {
    if (!newValue) {
      for (const NodePtr& child : currentNode->children()) {
        if (child->hasVariantBoundDescendant() || child->primaryInstanceHasVariantBinding()) {
          newValue = true;
          break;
        }
      }
    }

    if (newValue == currentNode->hasVariantBoundDescendant()) {
      // Since the value hasn't changed, the values in the ancestor chain shouldn't be affected
      break;
    } else {
      currentNode->_hasVariantBoundDescendant = newValue;
      currentNode = currentNode->parent();
    }
  }
}

bool Node::primaryInstanceHasVariantBinding() {
  if (!FF::ds_dirty_all_states_in_group()) {
    return false;
  }

  // We only care about primary instances in this context
  if (!isPrimaryInstance()) return false;

  // If the node itself has a variant binding, return true
  if (auto vcm = variableConsumptionMap()) {
    if (vcm->map().getOrDefault(VariableField::VARIANT_PROPERTIES)) {
      return true;
    }
  }

  // If the backing symbol has a variant bound descendant, return true
  if (auto node = _scene->nodeByIdOrNull(symbolID())) {
    if (node->hasVariantBoundDescendant()) return true;
  }

  // If any nInst has an override creating a variant variable binding, return true
  for (const auto& it : symbolOverrides()) {
    const auto& overrides = it.second;
    const auto overridesVcm = overrides.getOrNull(NodeField::VARIABLE_CONSUMPTION_MAP);
    if (overridesVcm) {
      const auto vcm = overridesVcm->as<Shared<const MultiplayerMapVariableFieldVariableData>>();
      if (vcm && vcm->map().getOrDefault(VariableField::VARIANT_PROPERTIES)) {
        return true;
      }
    }
  }

  return false;
}

const Set<GUID>& Node::getMatchingInstances(ConstNodePtr instance) const {
  static Set<GUID> emptySet;

  if (!isStateGroup() || !instance || !instance->isInstance()) {
    return emptySet;
  }

  if (scene() && scene()->stateGroupObserver()) {
    const auto& connectedInstancesMap =
        scene()->stateGroupObserver()->getConnectedInstances(self());
    return connectedInstancesMap.getOrDefault(instance->uniqueID());
  }

  return emptySet;
}

bool Node::_areNodesOrderedCorrectly(ConstNodePtr left, ConstNodePtr right) {
  REPORT_IF_FALSE_DEBUG_ONLY(left && right);
  REPORT_IF_FALSE_DEBUG_ONLY(left != right);
  if (!left->isValid() || !right->isValid()) {
    // Even though this shouldn't happen, we still enforce a strict ordering
    // on failed pointers. Note that this is also really bad in the sense that the
    // ordering of already-placed objects has changed (they used to be valid pointers
    // and now they are not).
    return (!left->isValid()) && right->isValid();
  }
  // Sanity check our invariants.
  REPORT_IF_FALSE_DEBUG_ONLY(left->parent() && right->parent());
  REPORT_IF_FALSE_DEBUG_ONLY(left->parent() == right->parent());

  auto leftParentIndex = left->parentIndex();
  auto rightParentIndex = right->parentIndex();
  return leftParentIndex.position < rightParentIndex.position ||
         (leftParentIndex.position == rightParentIndex.position &&
          left->uniqueID() < right->uniqueID());
}

float Node::opacity() const {
  if (isWidgetNodeHovered()) {
    auto hoverStyle = widgetHoverStyle();
    if (hoverStyle->isOpacitySet) {
      return hoverStyle->opacity;
    }
  }
  reportIfAccessingUnsetNodeField(NodeField::OPACITY, self());
  return _fields.hasNodeField(NodeField::LOCAL_OPACITY) ? _fields.localOpacity()
                                                        : _fields.opacity();
}

float Node::opacityToRoot() const {
  float opacity_ = 1;
  for (ConstNodePtr p = self(); p; p = p->parent()) {
    opacity_ *= p->opacityToRender();
    if (opacity_ == 0) return 0;
  }
  return opacity_;
}

void Node::setFillGeometry(MultiplayerGeometry value) {
  set(NodeField::FILL_GEOMETRY, std::move(value));
}

void Node::setStrokeGeometry(MultiplayerGeometry value) {
  set(NodeField::STROKE_GEOMETRY, std::move(value));
}

void Node::synthesizeGeometryCacheChanges() {
  if (!isValid() || !supportsGeometryCache()) return;
  SilenceUnsetNodeFieldAccessScope scope(scene(), !descendsFromRoot());
  auto editScope = EditScope::system("synthesize-geometry-cache-changes");
  const GeometryCache& cache = geometryCache();
  // Don't write out FILL_GEOMETRY for text nodes. Hopefully nodes
  // don't change type from text to non-text or vice versa.
  if (type() != NodeType::TEXT) {
    // By default, don't sync e.g. fill geometry for nodes that only have center-aligned strokes,
    // as these are unnecessary for rendering.
    UnnecessaryGeometry includeUnnecessaryGeometry = UnnecessaryGeometry::IGNORE;
    if (effectData()->hasKnockoutShadow()) {
      // The exception is knockout shadows, for which we need fill geometry (we need the full
      // geometry to use to knock out the drop shadow).
      includeUnnecessaryGeometry = UnnecessaryGeometry::INCLUDE;
    }

    setFillGeometry(
        MultiplayerGeometry(cache.paths(GeometryType::FILL, includeUnnecessaryGeometry),
                            cache.styleIDs(GeometryType::FILL, includeUnnecessaryGeometry)));
  }
  setStrokeGeometry(
      MultiplayerGeometry(cache.paths(GeometryType::STROKE),
                          cache.styleIDs(GeometryType::STROKE, UnnecessaryGeometry::IGNORE)));
}

void Node::clearEffectSpreadForVectorTypes(ClearSpreadReason reason) {
  auto _effectData = effectData()->copy();
  if (!_effectData.clearedEffectsSpread()) {
    return;
  }

  // Show a visual bell depending on why we've cleared effect spread.
  if (reason == ClearSpreadReason::CONVERT_TYPE) {
    if (auto isEffectStyle =
            safelyGetDirectlyInheritedStyle(InheritStyleField::INHERIT_EFFECT_STYLE_ID)) {
      ReadonlyHooksHack().showVisualBellLocalized(
          "style_spread_no_longer_applies",
          "visual_bell.style_s_spread_field_no_longer_applies_when_converting_to_vector_type",
          Json::nullValue, false);
    } else {
      ReadonlyHooksHack().showVisualBellLocalized(
          "clear_spread_for_type", "visual_bell.shadow_spread_is_not_supported_for_vector_type",
          Json::nullValue, false);
    }
  } else if (reason == ClearSpreadReason::DETACH_STYLE) {
    // This should only be called via detaching a style if the node can never render with a spread
    // value (based on its node type):
    REPORT_IF_TRUE_DEBUG_ONLY(isRectOrEllipseLike(type()));

    // Do nothing: we invoke this function when detaching a style from a non-spread-eligible node.
    // We don't show a visual bell in that case, because the style (with spread) already didn't
    // apply to the non-spread-eligible node. (We still need to invoke this function in that case
    // so that we clear thes spread field that may have been copied over from the effect style.)
  }

  set(NodeField::EFFECT_DATA, _effectData.release());
}

void Node::setCurrentPage(NodePtr canvas) {
  // CURRENT_PAGE is stored on the root node of the scene graph for undo.
  if (type() == NodeType::DOCUMENT) {
    GUID canvasGUID;
    if (canvas)
      canvasGUID = canvas->uniqueID();
    else {
      sloge_debugOnly_previously_loge("requested page is null");
    }
    set(NodeField::CURRENT_PAGE, std::move(canvasGUID));
  } else {
    sloge_debugOnly_previously_loge("should only be called with a DOCUMENT node");
  }
}

NodePtr Node::currentPage() const {
  if (scene() && type() == NodeType::DOCUMENT) {
    GUID canvasGuid = getCopyOf(NodeField::CURRENT_PAGE).as<GUID>();
    NodePtr node = scene()->nodeByIdOrNull(canvasGuid);
    if (node != nullptr && node->type() == NodeType::CANVAS &&
        (!node->isInternalOnlyCanvas() || FF::show_internal_only_canvas())) {
      return node;
    }

    // If not stored yet, assume first valid canvas.
    // If this logic changes, make sure to also update the multiplayer side.
    // For read only files, we load one page at a time (incremental loading).
    // If no nodeId is specified in the url, multiplayer defaults to sending
    // down the first user canvas (the behavior here).
    for (auto child : scene()->root()->children()) {
      if (child->type() == NodeType::CANVAS &&
          (!child->isInternalOnlyCanvas() || FF::show_internal_only_canvas())) {
        return child;
      }
    }
  }
  return nullptr;
}

inline bool isSensitiveField(NodeField field) {
  switch (field) {
    case NodeField::NAME:
    case NodeField::SYMBOL_LINKS:
    // TODO(@eddyim): Remove SYMBOL_DESCRIPTION and STYLE_DESCRIPTION once migration is complete
    case NodeField::SYMBOL_DESCRIPTION:
    case NodeField::STYLE_DESCRIPTION:
    case NodeField::DESCRIPTION:
      return true;
    default:
      return false;
  }
}

String Node::propertiesString() const {
  String result("{\n");

  for (auto it : fields().iter(DenseNodeFields::IterMode::NORMAL)) {
    // (it.copyOfValue() -> DenseNodeFields::getCopyOf() -> Any).toString()
    // means a deep copy of every field will be temporarily created, including
    // dereferenced Owned<> values.
    String strValue;
    Any value = it.copyOfValue();
    if (isSensitiveField(it.field()) && value.is<ImmutableString>()) {
      // This replicates the behavior in toString(Any), but with an inserted call to
      // maybeObfuscate. We could avoid duplicating this logic, but there's very little
      // logic here, and this code is intended for debugging and logging so not a big
      // deal if they fall out of sync
      strValue = "<ImmutableString:" +
                 Json::Value(maybeObfuscate(value.as<ImmutableString>().str())).toString() + '>';
    } else {
      strValue = value.toDebugString();
    }
    strValue = strValue.replaceAll("\n", "\n  ");
    append_format(result, "  %s: %s\n", it.field(), strValue);
  }
  result += "}\n";
  return result;
}

static String makeIndent(int level, bool highlight) {
  static const int indentSize = 2;
  if (level <= 0) {
    return {};
  }
  if (highlight) {
    return String((level - 1) * indentSize, '-') + "> ";
  } else {
    return String(level * indentSize, ' ');
  }
}

String Node::hierarchyStringLocal(int level, bool highlight) const {
  String result = makeIndent(level, highlight);
  append_format(result, "%s: '%s' %s", nodeTypeToLowerCaseString(type()), nameForWeb(), uniqueID());

  if (isMask()) {
    result += " isMask";
  }

  if (_instanceData) {
    // Don't call instanceOverridePath() here because we want to print internal state
    // and bypass validity checks.
    if (_instanceData->path.isValid()) {
      append_format(result, " %s", _instanceData->path);
    } else {
      result += " [nopath]";
    }
  }

  if (isOrInSymbol()) {
    if (overrideKey() != uniqueID()) {
      append_format(result, " (okey %s)", overrideKey());
    }
  }

  if (isStateGroup()) {
    append_format(result, " (state group: %s)", stateGroupKey());
  }

  if (version().isValid()) {
    append_format(result, " (version: %s)", version().str());
  }

  switch (type()) {
    case NodeType::INSTANCE:
      append_format(result, " (symbol: %s)", symbolID());
      break;
    case NodeType::SYMBOL: {
      if (FF::ds_publish_file_deprecation()) {
        if (publishID().isValid()) {
          append_format(result, " (publishID: %s, sourceLibraryKey: %s)", publishID(),
                        sourceLibraryKey().str());
        }
      } else {
        if (publishFile_DEPRECATED().isValid()) {
          append_format(result, " (publishID: %s, publish file: %s, sourceLibraryKey: %s)",
                        publishID(), publishFile_DEPRECATED().c_str(), sourceLibraryKey().str());
        }
      }
      if (!sharedSymbolVersion().empty()) {
        append_format(result, " (shared version: %s)", sharedSymbolVersion());
      }
      break;
    }
    default:
      if (isStyle()) {
        result += " (style)";
      }
      if (hasS1SharedStyleReference()) {
        append_format(result, " (shared ref: %s)", *sharedS1StyleReference());
      }
      if (hasS1SharedStyleMasterData()) {
        append_format(result, " (shared master: %s)", *sharedS1StyleMasterData());
      }
      break;
  }
  return result;
}

void Node::hierarchyStringWithChildren(int level, bool highlight, Array<String>& lines) const {
  if (level > MAX_HIERARCHY_LEVELS) {
    return;
  }
  lines.push_back(hierarchyStringLocal(level, highlight));
  for (const ConstNodePtr& child : children()) {
    child->hierarchyStringWithChildren(level + 1, false, lines);
  }
}

int Node::hierarchyStringForAncestors(Array<String>& lines) const {
  int level = 0;
  Array<ConstNodePtr> ancestors;
  for (ConstNodePtr p = parent(); p; p = p->parent()) {
    if (level > MAX_HIERARCHY_LEVELS) {
      break;
    }
    ancestors.push_back(p);
    ++level;
  }
  level = 0;
  while (!ancestors.empty() && level <= MAX_HIERARCHY_LEVELS) {
    lines.push_back(ancestors.back()->hierarchyStringLocal(level, false));
    ancestors.pop_back();
    ++level;
  }
  return level;
}

// New API. Show hierarchy of ancestors to root and this subtree.
String Node::hierarchyString() const {
  Array<String> lines;
  int level = hierarchyStringForAncestors(lines);
  if (!level) {
    lines.push_back("(null parent)");
    ++level;
  }
  hierarchyStringWithChildren(level, true, lines);
  return join(lines, "\n");
}

String Node::overriddenSymbolIDsString() const {
  String result = format("overridden symbolIDs for %s", uniqueID());

  for (const auto& it : symbolOverrides()) {
    const OverridePath& path = it.first;
    const auto& overrides = it.second;
    const Any* overriddenSymbolID = overrides.getOrNull(NodeField::OVERRIDDEN_SYMBOL_ID);
    if (overriddenSymbolID) {
      append_format(result, "\n  %s: %s", path, overriddenSymbolID->as<GUID>());
    }
  }

  return result.trim();
}

String Node::symbolOverridesString(bool includeAllTextData) const {
  String result = format("symbol overrides for %s (symbol id: %s)", uniqueID(), symbolID());
  append_format(result, "\n    %s",
                Figma::symbolOverridesString(symbolOverrides(), includeAllTextData));
  return result;
}

String Node::derivedSymbolOverridesString() const {
  String result = format("derived symbol overrides for %s (symbol id: %s)", uniqueID(), symbolID());

  for (const auto& it : derivedSymbolData().overrides) {
    const OverridePath& path = it.first;
    const auto& overrides = it.second;
    append_format(result, "\n  %s:", path);

    for (const auto& oit : overrides) {
      auto value = oit.second.toDebugString();
      value = value.replaceAll("\n", "\n  ");
      append_format(result, "\n   %s: %s", Figma::toString(oit.first), value);
    }
  }
  return result;
}

String Node::overrideStashString() const {
  String result = format("overrideStash for %s (symbol id: %s)", uniqueID(), symbolID());

  if (auto stash = overrideStashInMem()) {
    append_format(result, "\nStashInMem %s", Figma::toDebugString(*stash));
  }
  return result;
}

String Node::componentPropDefsString() const {
  String result = format("componentPropDefs for %s", uniqueID());
  for (const auto& it : componentPropDefs()) {
    append_format(result, "\n  %s:", it.first);
    append_format(result, "\n %s", it.second.toDebugString());
  }
  return result;
}

String Node::componentPropRefsString() const {
  String result = format("componentPropRefs for %s", uniqueID());
  for (const auto& it : allComponentPropRefs()) {
    append_format(result, "\n  %s:", Figma::toString(it.first));
    append_format(result, "\n %s", it.second.toDebugString());
  }
  return result;
}

String Node::componentPropAssignmentsString() const {
  String result = format("componentPropAssignments for %s (symbol id: %s)", uniqueID(), symbolID());
  for (const auto& it : getCombinedComponentPropAssignments()) {
    append_format(result, "\n  %s:", it.first);
    append_format(result, "\n %s", Figma::toDebugString(it.second));
  }
  return result;
}

template <typename M>
static String formatMapping(const String& title, const M& mappings) {
  String out = format("  %s:\n", title);
  for (const auto& mapping : mappings) {
    append_format(out, "    %s\n", mapping);
  }
  return out;
}

static void logLines(String str) {
#if USE_LOG
  auto lines = str.split("\n");
  for (auto& line : lines) {
    logToConsole_debugOnly_deprecated("Node", "%s", line.c_str());
  }
#endif
}

