// FigJam performance experiment:
// Uncomment next line to skip invalidating bounds on transform changes.
// # define FIGJAM_PERF_RELATIVE_TRANSFORM_RT_INVALIDATION_OFF 1

void Node::_invalidateBoundsFromPropertyChange(NodeField field, NodeOrigin origin,
                                               Any oldValueIfLocal) {
  switch (field) {
    // These should never exist on a node
    case NodeField::PHASE:
    case NodeField::MISSING:
    case NodeField::ANY: {
      REPORT_IF_FALSE_DEBUG_ONLY(false);
      break;
    }

    // Reparenting shouldn't ever cause us to get here since that goes through
    // Node::changeParentIndex() instead of SceneGraph::changeProperty()
    case NodeField::PARENT_INDEX: {
      REPORT_IF_FALSE_DEBUG_ONLY(false);
      break;
    }

    // Legacy unused fields don't affect rendering.
    case NodeField::DEPRECATED_STACK_CHILD_COUNTER_ALIGN:
    case NodeField::BACKGROUND_PAINT_DATA__NO_LONGER_USED:
    case NodeField::RECTANGLE_CORNER_TOOL_INDEPENDENT__DEPRECATED:
    case NodeField::INHERIT_FILL_STYLE_ID_FOR_BACKGROUND__NO_LONGER_USED:
    case NodeField::CONTAINER_SUPPORTS_FILL_STROKE_AND_CORNERS__DEPRECATED: {
      break;
    }

    // These fields don't affect rendering
    case NodeField::ANNOTATIONS:
    case NodeField::MEASUREMENTS:
    case NodeField::AUTO_RENAME:
    case NodeField::KEY:
    case NodeField::COMPONENT_KEY:
    case NodeField::CURRENT_PAGE:
    case NodeField::DERIVED_SYMBOL_DATA:
    case NodeField::EXPORT_SETTINGS_DATA:
    case NodeField::FILL_GEOMETRY:
    case NodeField::GUIDES_DATA:
    case NodeField::HANDLE_MIRRORING:
    case NodeField::HORIZONTAL_CONSTRAINT:
    case NodeField::HYPERLINK:
    case NodeField::MENTION:
    case NodeField::INTERNAL_ONLY:
    case NodeField::IS_C2:
    case NodeField::IS_EXPANDABLE:
    case NodeField::IS_EXPANDED:
    case NodeField::IS_EXPORT_ENABLED:
    case NodeField::IS_EXPORT_TRIMMED:
    case NodeField::IS_INFINITE_RES_ENABLED:
    case NodeField::IS_RETINA_MODE_ENABLED:
    case NodeField::IS_SYMBOL_PUBLISHABLE:
    case NodeField::IS_PUBLISHABLE:
    case NodeField::IS_PAGE_DIVIDER:
    case NodeField::LIBRARY_MOVE_INFO:
    case NodeField::LIBRARY_MOVE_HISTORY:
    case NodeField::DEVELOPER_RELATED_LINKS:
    case NodeField::EMBED_DATA:
    case NodeField::STAMP_DATA:
